create table if not exists `taggerd1__blocks`(
id int(11) NOT NULL PRIMARY KEY  auto_increment,
name varchar(50) NOT NULL   ,
constant varchar(50) NOT NULL UNIQUE  ,
text text NOT NULL  );

insert into `taggerd__taggerd1__blocks` set `id` = '1',`name` = 'Copyright',`constant` = 'COPYRIGHT',`text` = '<a href=\'\'http://palmengine.ru/\'\'>Сайт работает на Palm CMS &copy; 2010</a>';
insert into `taggerd__taggerd1__blocks` set `id` = '2',`name` = 'Текст снизу',`constant` = 'BOTTOM_TEXT',`text` = '<div class=\"pages_global_bottom_text\">\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>Наш адрес:&nbsp; Москва, Петровско-Разумовская аллея, д.10, кор. 1 (м. Динамо)</strong></span></div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>Тел. <span style=\"color: rgb(153, 0, 0);\">(495)</span> 612-46-71, <span style=\"color: rgb(153, 0, 0);\">(495) </span>782-6-287, тел/факс <span style=\"color: rgb(153, 0, 0);\">(495) </span>612-46-72</strong></span></div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>электронная почта: </strong><span style=\"font-family: Verdana;\"><a href=\"mailto:taggerd@mail.ru\"><strong><span style=\"color: rgb(153, 0, 0);\">taggerd@mail.ru</span></strong></a><strong><span style=\"color: rgb(153, 0, 0);\">&nbsp;&nbsp;</span></strong></span></span></div>\r\n</div>';
create table if not exists `taggerd1__comments`(
id int(11) NOT NULL PRIMARY KEY  auto_increment,
pic varchar(150) NOT NULL   ,
icon varchar(150) NOT NULL   ,
xsmall char(150) NOT NULL   ,
parent int(11) NOT NULL   ,
name tinytext NOT NULL   ,
text text NOT NULL   ,
date timestamp NOT NULL   DEFAULT CURRENT_TIMESTAMP);

create table if not exists `taggerd1__files`(
id int(11) NOT NULL PRIMARY KEY  auto_increment,
position int(11) NOT NULL   ,
name varchar(255) NOT NULL   ,
path varchar(255) NOT NULL UNIQUE  ,
date timestamp NOT NULL   DEFAULT CURRENT_TIMESTAMP ,
type varchar(50) NOT NULL   ,
icon varchar(255) NOT NULL   ,
xsmall varchar(255) NOT NULL   ,
size int(11) NOT NULL  );

create table if not exists `taggerd1__items`(
id int(11) NOT NULL PRIMARY KEY  auto_increment,
parent int(11) NOT NULL   ,
position int(11) NOT NULL   ,
name varchar(255) NOT NULL   ,
short_text varchar(255) NULL   ,
text text NOT NULL   ,
pic varchar(255) NOT NULL   ,
icon varchar(255) NOT NULL   ,
xsmall varchar(255) NOT NULL   ,
date timestamp NOT NULL   DEFAULT CURRENT_TIMESTAMP ,
display enum('Да','Нет') NOT NULL   DEFAULT 'Нет');

create table if not exists `taggerd1__pages`(
id int(11) NOT NULL PRIMARY KEY  auto_increment,
parent int(11) NOT NULL   ,
position int(5) NOT NULL   DEFAULT '1' ,
page varchar(50) NOT NULL   ,
name varchar(50) NOT NULL   ,
title varchar(255) NOT NULL   ,
description varchar(255) NOT NULL   ,
keywords varchar(255) NULL   ,
text text NOT NULL   ,
date timestamp NOT NULL   DEFAULT CURRENT_TIMESTAMP ,
display enum('Да','Нет') NOT NULL   DEFAULT 'Да');

insert into `taggerd__taggerd1__pages` set `id` = '1',`parent` = '0',`position` = '-1',`page` = 'Главная',`name` = 'Услуги ЧОП, охрана объектов Москва',`title` = 'ЧОП ТАГГЕРД, услуги охраны в москве, охрана чоп москва, охрана объектов, техническе системы безопасности, ',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает охранные услуги по приемлемым ценам: физическая охрана объектов, личная охрана,  защита информации, установка технических систем безопасности. Охрана ЧОП - недорого, надежно, качественно!',`keywords` = 'охрана объектов, чоп москва, охранные услуги, услуги охраны, обеспечение безопасности, охранная фирма, охрана дач, охрана котеджей, охрана дачи',`text` = '<div>\r\n<div>\r\n<div style=\"text-align: left;\"><span style=\"font-size: large;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 255);\"><span><strong>ГЛАВНАЯ&nbsp;СТРАНИЦА</strong></span></span></span></span></div>\r\n<div style=\"text-align: left;\"><span style=\"font-family: Arial;\"><strong><span style=\"font-size: smaller;\"><span style=\"color: rgb(0, 0, 128);\">общая информация, услуги ЧОП, охрана объектов Москва и область</span></span></strong></span></div>\r\n<div style=\"text-align: left;\">&nbsp;</div>\r\n<div style=\"text-align: center;\">&nbsp;\r\n<div style=\"text-align: center;\">\r\n<table width=\"700\" cellspacing=\"0\" cellpadding=\"12\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#cc0000\">\r\n            <div style=\"text-align: center;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(255, 255, 255);\"><span style=\"font-size: small;\"><strong><font size=\"2\">ВНИМАНИЕ !!!&nbsp; АНТИКРИЗИСНАЯ АКЦИЯ !!!</font></strong></span></span></span></div>\r\n            <div style=\"text-align: center;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(255, 255, 255);\"><span style=\"font-size: small;\"><strong><font size=\"2\">СТОИМОСТЬ УСЛУГ ПО ОХРАНЕ ОБЪЕКТОВ - ОТ 70 РУБЛЕЙ ЗА 1 ЧАС !!!</font></strong></span></span></span></div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center;\">&nbsp;&nbsp;</div>\r\n</div>\r\n</div>\r\n<div style=\"text-align: center;\">\r\n<div style=\"text-align: center;\">\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 255);\"><span><span style=\"font-size: x-large;\"><strong><u><span style=\"font-family: Comic Sans MS;\"><em><span class=\"style23\"><font size=\"5\">Здравствуйте!</font></span></em></span></u></strong></span></span></span></div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><em><font size=\"3\"><strong><span style=\"font-family: Comic Sans MS;\"><span class=\"style23\">Мы рады приветствовать Вас на сайте</span></span></strong> <strong><span style=\"font-family: Comic Sans MS;\"><span class=\"style23\">группы компаний &quot;ТАГГЕРД&quot; ! !&nbsp;!</span></span></strong></font></em></span></span></div>\r\n<div style=\"text-align: center;\">\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div style=\"text-align: left;\">\r\n<table width=\"200\" cellspacing=\"9\" cellpadding=\"0\" border=\"0\" align=\"left\">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <div style=\"text-align: center;\"><span style=\"font-size: xx-large;\"><span style=\"font-family: Tahoma;\"><span><span><span style=\"color: rgb(204, 0, 0);\"><strong>&quot;ТАГГЕРД&quot;</strong></span></span></span></span></span></div>\r\n            <div style=\"text-align: center;\">&nbsp;</div>\r\n            <div style=\"text-align: center;\">&nbsp;<img width=\"180\" height=\"180\" src=\"/files/storage/images/hitimech.png\" alt=\"\" />&nbsp;&nbsp;</div>\r\n            <div style=\"text-align: center;\"><span style=\"font-family: Tahoma;\"><span><span><span style=\"font-size: xx-large;\"><strong><span><span><font size=\"6\"><span style=\"display: none;\" id=\"1273644842024S\">&nbsp;</span><span style=\"font-family: Verdana;\"><span><span><span><span style=\"color: rgb(255, 0, 0);\">17 ЛЕТ</span></span></span></span></span></font></span></span></strong></span></span></span></span></div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\"><b>Охрана бизнеса и личная охрана</b> &ndash; необходимые условия успешного существования в современном мире. Без уверенности в собственной безопасности и сохранности имущества, не возможно спокойно жить, плодотворно работать, строить планы и перспективы на будущее! </span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Группа предприятий <strong>&laquo;ТАГГЕРД&raquo;, </strong>в которую входит&nbsp;ряд частных охранных предприятий (ЧОП), охраняющих объекты в Москве и области&nbsp;&nbsp;и предприятий, специализирующихся на установке комплексных инженерно-технических систем безопасности, существует уже более <strong>17-и</strong> лет и имеет&nbsp;заслуженную репутацию добросовестного и порядочного партнера в сфере услуг охраны и безопасности.&nbsp;</span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Коллектив&nbsp;<strong>&quot;ТАГГЕРД&quot; </strong>- сплоченная команда профессионалов объединенных общим делом, традициями, интересами, целями. У нас работают ветераны вооруженных сил РФ, бывшие сотрудники силовых структур: МВД,&nbsp;ФСБ, ФСО&nbsp;и спецподразделений.</span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Мы выполняем полный спектр услуг по <strong>охране объектов </strong>недвижимости, материальнх ценностей&nbsp;и грузов при перевозке, по обеспечению безопасности предприятий и частных лиц. В своей работе мы используем комплексный подход к вопросам безопасности, включающий: организационные и административные меры, физическую охрану, технические системы безопасности, мероприятия по защите информации, юридическое сопровождение.</span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">За время работы&nbsp;мы <strong>приняли&nbsp;под охрану </strong>более 300 объектов промышленного и гражданского назначения,</span></span></div>\r\n<table width=\"370\" cellspacing=\"9\" cellpadding=\"12\" border=\"0\" align=\"right\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#ccccff\" bordercolor=\"#0000ff\">\r\n            <div style=\"text-align: left;\">&nbsp;</div>\r\n            <div style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 255);\"><span><span style=\"font-size: medium;\"><strong><span style=\"font-family: Comic Sans MS;\"><font size=\"3\">Только у нас:</font></span></strong></span></span></span></div>\r\n            <div style=\"text-align: left;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: small;\"><font size=\"2\">- приемлемые цены, а также скидки, бонусы</font></span></strong></span></span></div>\r\n            <div style=\"text-align: left;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: small;\"><font size=\"2\">- выставление поста охраны в день обращения</font></span></strong></span></span></div>\r\n            <div style=\"text-align: left;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: small;\"><font size=\"2\">- полный спектр охранных услуг</font></span></strong></span></span></div>\r\n            <div style=\"text-align: left;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: small;\"><font size=\"2\">- комплексный подход к вопросам безопасности</font></span></strong></span></span></div>\r\n            <div style=\"text-align: left;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: small;\"><font size=\"2\">- 17-ти летний опыт охраны объектов</font></span></strong></span></span></div>\r\n            <div style=\"text-align: left;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: small;\"><font size=\"2\">- заслуженная положительная репутация</font></span></strong></span></span></div>\r\n            <div style=\"text-align: left;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: small;\"><font size=\"2\">- надежность и конфиденциальность</font></span></strong></span></span></div>\r\n            <div style=\"text-align: left;\"><span style=\"font-family: Comic Sans MS;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: small;\"><font size=\"2\">- охрана объектов в Москве и области</font></span></strong></span></span></div>\r\n            <div style=\"text-align: left;\">&nbsp;</div>\r\n            <div style=\"text-align: left;\">&nbsp;</div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">смонтированы более 100&nbsp;сложных современных охранных систем, обеспечена личная безопасность&nbsp;более 30 vip-персон. </span></span><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">В нашем арсенале имеется все необходимое для обеспечения надежной безопасности граждан и имущества: опыт, знания, современное оборудование и аппаратура, вооружение, транспорт, связь.</span></span></div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Оказание охранных услуг &nbsp;является для нас привычной, повседневной работой, которую мы выполняем&nbsp;много лет, ежедневно, круглосуточно, без выходных и праздников. Наш богатый опыт гарантирует высокую степень защиты, надежность, профессионализм и оперативность в решении вопросов безопасности любой сложности!&nbsp;&nbsp;</span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Для каждого клиента у нас предусмотрен индивидуальный подход,&nbsp;оптимальный набор мер и услуг,&nbsp;взаимоприемлемые </span></span><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">расценки на охранные услуги. У нас принято не навязывать клиенту не нужные и&nbsp;лишние услуги,&nbsp; а рационально и комплексно сочетать организационные меры, технические средства безопасности и физическую охрану.</span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<table width=\"200\" cellspacing=\"9\" cellpadding=\"0\" border=\"0\" align=\"left\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img width=\"280\" height=\"250\" src=\"/files/storage/images/ohranniki-stroy.jpg\" alt=\"\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify;\">&nbsp;<span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Все сотрудники ЧОП имеют за плечами опыт работы в <strong>охране объектов&nbsp;</strong>от 2-х лет, высшее или среднее образование,&nbsp;диплом об окончании специализированной школы охраны и удостоверение частного охранника. Многие сотрудники имеют опыт боевых действий. При оформлении на фирму охранники&nbsp;проходят специальный отбор, обязательную проверку и тестирование на профпригодность. Мы постоянно трудимся над повышением уровня физической и профессиональной подготовки охранников, проводим занятия и учения, знакомим с новыми технологиями и техническими средствами охраны.</span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Мы работаем на благо заказчика,&nbsp;честно и порядочно, стараясь не раздувать, а оптимизировать цену к реальным условиям охраны и требуемому уровню безопасности.</span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n</div>\r\n<div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span style=\"font-family: Comic Sans MS;\"><span><strong><font size=\"3\">Охранные услуги, оказываемые ЧОП:</font></strong></span></span></span></span></div>\r\n<table width=\"200\" cellspacing=\"9\" cellpadding=\"0\" border=\"0\" align=\"right\">\r\n    <tbody>\r\n        <tr>\r\n            <td><font size=\"3\"><img width=\"280\" height=\"300\" src=\"/files/storage/images/uslugiohrany.jpg\" alt=\"\" /></font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;<span style=\"font-size: larger;\"><font size=\"2\"><span style=\"color: rgb(255, 0, 0);\">&diams;</span><span><span style=\"color: rgb(0, 0, 128);\">&nbsp; <strong>охрана объектов </strong>в Москве и Московской области, с использованием собак, вооружения, техники и спецсредств,&nbsp;в том числе:</span></span></font></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\"><font size=\"2\">-&nbsp; <strong>офисов </strong>и офисных центров</font></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\"><font size=\"2\">-&nbsp; <strong>котеджей</strong>, загородных домов,&nbsp;дач,&nbsp;садовых&nbsp;товариществ</font></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\"><font size=\"2\">-&nbsp; <strong>магазинов</strong>, торговых центров, гипермаркетов, рынков</font></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\"><font size=\"2\">-&nbsp; <strong>развлекательны центров</strong>, ночных клубов, ресторанов</font></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\"><font size=\"2\">-&nbsp; <strong>производственных и складских помещений</strong>, терминалов,&nbsp;</font></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\"><font size=\"2\">-&nbsp; <strong>образовательных&nbsp;учреждений </strong>(школ, ВУЗ-ов, детских садов и др.)</font></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\"><font size=\"2\">-&nbsp; <strong>банковских и финансовых организаций&nbsp;</strong>и т.д.</font></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><font size=\"2\"><span style=\"color: rgb(255, 0, 0);\">&diams;&nbsp;</span><strong><span><span style=\"color: rgb(0, 0, 128);\">сопровождение материальных ценностей </span></span></strong><span><span style=\"color: rgb(0, 0, 128);\">и грузов при перевозке</span></span></font></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(255, 0, 0);\">&diams;</span><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span><strong><span style=\"color: rgb(0, 0, 128);\">личная охрана</span></strong></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(255, 0, 0);\">&diams;</span><span><span style=\"color: rgb(0, 0, 128);\">&nbsp;<strong>обеспечение безопасности </strong>при проведении массовых мероприятий (спортивных и праздничных мероприятий, свадеб, концертов и др.)</span></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(255, 0, 0);\">&diams;</span><strong><span><span style=\"color: rgb(0, 0, 128);\">&nbsp;монтаж современных интеллектуальных систем безопасности </span></span></strong><span><span style=\"color: rgb(0, 0, 128);\">(охранная и пожарная сигнализация, интелектуальное видеонаблюдение, контроль доступа)</span></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(255, 0, 0);\">&diams;</span><span><span style=\"color: rgb(0, 0, 128);\">&nbsp;<strong>защита информации</strong>, охрана интеллектуальной собственности, коммерческой тайны</span></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(255, 0, 0);\">&diams;</span><span><span style=\"color: rgb(0, 0, 128);\"> <strong>юридическое сопровождение </strong>и консультирование по вопросам безопасности&nbsp;&nbsp;&hellip; и другие</span></span></span><font size=\"2\"><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\">.</span></span></font></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n</div>\r\n<div>\r\n<div>\r\n<table width=\"200\" cellspacing=\"9\" cellpadding=\"0\" border=\"0\" align=\"left\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img width=\"300\" height=\"225\" src=\"/files/storage/images/sistemybezopasnosti-2_.jpg\" alt=\"\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><strong><span style=\"font-family: Comic Sans MS;\"><font size=\"3\">В своей работе мы преследуем следующие </font></span></strong></span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><strong><span style=\"font-family: Comic Sans MS;\"><font size=\"3\">стратегические принципы:</font></span></strong></span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">&nbsp;</span></span></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span>&bull;&nbsp;</span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp; Профессионализм,</span></span></font></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span>&bull;</span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;&nbsp; Индивидуальный подход,</span></span></font></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span>&bull;</span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;&nbsp; Конфиденциальность,</span></span></font></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span>&bull;</span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;&nbsp; Надежность,</span></span></font></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span>&bull;</span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp; &nbsp;Законность,</span></span></font></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span>&bull;</span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;&nbsp; Оперативность,</span></span></font></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span>&bull;</span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;&nbsp; Системность,</span></span></font></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span>&bull;</span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp; &nbsp;Гибкость,</span></span></font></div>\r\n<div><font size=\"3\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><span><span>&bull;</span></span></span></span><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 128);\"><span>&nbsp;&nbsp; Порядочность.<span style=\"display: none;\" id=\"1277912270070E\">&nbsp;</span></span></span></span></font></div>\r\n</div>\r\n</div>\r\n<div><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">&nbsp;</span></span></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center;\">&nbsp;<span style=\"color: rgb(204, 0, 0);\"><span><strong><span style=\"font-size: small;\"><em><font size=\"2\">Для организации&nbsp;незамедлительного реагирования на любые угрозы&nbsp;в отношении наших&nbsp;клиентов, у нас действует круглосуточная служба оперативного дежурного, а также мобильная группа быстрого реагирования (ГБР), способная прибыть к месту происшествия в считанные минуты и эффективно решить любую проблему безопасности на месте!</font></em></span></strong></span></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n<div style=\"text-align: center;\"><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><span><span style=\"color: rgb(0, 0, 128);\"><strong>Вы можете обратиться к нам в любой день, в любое время суток!&nbsp; Мы всегда готовы оперативно оказать&nbsp;квалифицированную помощь и защиту всем, кто в ней нуждается!&nbsp;</s