insert into `taggerd__pages` set `id` = '9',`parent` = '0',`position` = '10',`autotext` = '',`page` = 'Охранники ЧОП',`name` = 'Физическая охрана',`title` = 'ЧОП ТАГГЕРД - охранная фирма (Москва) предлагает охранные услуги, технические средства безопасности, охранное агентство',`description` = 'Группа предприятий \"ТАГГЕРД\" предлагает широкий спектр охранных услуг: охрану объектов, личную охрану, сопровождение грузов,  защиту информации, установку технических систем безопасности. Охранная фирма \"ТАГГЕРД\" - недорого, надежно, качественно!',`keywords` = 'охранная фирма, лучшая охранная фирма москвы, охранные услуги, услуги охраны, охрана чоп, охрана объектов москва',`text` = '<h1><span style=\"font-size: xx-small\"><span><span style=\"color: #000080\"><span style=\"font-family: Arial\"><strong><span style=\"font-family: Arial\">ОХРАННАЯ ФИРМА &quot;ТАГГЕРД&quot; ВЫБИРАЕТ ПРОФЕССИОНАЛОВ&nbsp;! / ФИЗИЧЕСКАЯ ОХРАНА / ОХРАННИКИ ЧОП</span></strong></span></span></span></span></h1>\r\n<h2><span style=\"color: #0000ff\"><span style=\"font-size: large\"><span style=\"font-family: Arial\"><span><strong>СПЕЦИАЛИЗАЦИЯ ОХРАННИКОВ ЧОП</strong></span></span></span></span></h2>\r\n<div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"font-size: large\"><span style=\"color: rgb(0,0,255)\"><span><span style=\"font-family: Verdana\"><font size=\"4\"><img height=\"140\" alt=\"\" width=\"56\" src=\"/files/storage/images/000053.jpg\" /><img height=\"140\" alt=\"\" width=\"57\" src=\"/files/storage/images/blazers_win001299z.jpg\" /><img height=\"140\" alt=\"\" width=\"41\" src=\"/files/storage/images/costume_ofis015738z.jpg\" /><img height=\"140\" alt=\"\" width=\"48\" src=\"/files/storage/images/costume_sum000017z.jpg\" /><img height=\"140\" alt=\"\" width=\"50\" src=\"/files/storage/images/costume_sum014581z.jpg\" /><img height=\"140\" alt=\"\" width=\"59\" src=\"/files/storage/images/shirts001630.jpg\" /><img height=\"140\" alt=\"\" width=\"58\" src=\"/files/storage/images/blazers_win001290z.jpg\" /><img height=\"140\" alt=\"\" width=\"55\" src=\"/files/storage/images/120659555615338(1).jpg\" /><img height=\"140\" alt=\"\" width=\"41\" src=\"/files/storage/images/costume_sum003439z.jpg\" /><img height=\"140\" alt=\"\" width=\"53\" src=\"/files/storage/images/shirts000259z.jpg\" /><img height=\"140\" alt=\"\" width=\"71\" src=\"/files/storage/images/blazers_win002169z.jpg\" /><img height=\"140\" alt=\"\" width=\"46\" src=\"/files/storage/images/catalog_65.jpg\" /><img height=\"140\" alt=\"\" width=\"55\" src=\"/files/storage/images/costume_ofis011919z.jpg\" /></font></span></span></span></span></div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div style=\"text-align: left\">\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">Каждый охранник, работающий в штате охранного агентства&nbsp;(ЧОП), имеет действующее&nbsp;Удостоверение личности частного охранника, с указанием квалификации, диплом об окончании Школы подготовки и обучения охранников, опыт работы от 2-х лет, российское гражданство и прописку. Все сотрудники при оформлении в охранную фирму&nbsp;проходят психологическое тестирование, специальную проверку персональных данных и уровня профессиональной подготовки. </span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">В рамках охранной фирмы&nbsp;сотрудники проходят дополнительное обучение, специальную и физическую подготовку, повышают профессиональный уровень знаний, совершенствуют свои &nbsp;навыки владения оружием и спецсредствами. Периодически все сотрудники сдают квалификационные экзамены и&nbsp;профессиональные&nbsp;тесты. </span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">В зависимости от особенностей и специфики работы объекта охраны, мы подбираем и соответствующий контингент охранников и специалистов. Поэтому в охранном бизнесе существует своя внутренняя специализация и свои направления деятельности. Очень сложно найти сотрудника, который умел бы все. Тонкостей и нюансов&nbsp;в каждой сфере деятельности и на каждом объекте предостаточно. Поэтому каждый охранник в зависимости от опыта работы, наклонностей, уровня подготовки и имеющихся навыков, имеет свою узкую специализацию. Взять хотя бы для примера телохранителя, охранника на входе в ночной клуб, инкассатора или охранника склада &ndash; всех их объединяет только звание ОХРАННИК, а функциональные обязанности, специфика работы, профессиональные требования и подготовка абсолютно разные.</span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #0000ff\"><span><span style=\"font-size: larger\"><span style=\"font-family: Arial\">&nbsp;</span><span style=\"font-family: Arial\"><span><span style=\"font-size: medium\"><strong>Для выполнения охранных услуг&nbsp;использутся</strong></span></span></span></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #0000ff\"><span><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><span><span style=\"font-size: medium\"><strong>охранники следующей специализации:</strong> </span></span></span></span></span></span><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">&nbsp;</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник на внутренний пост (в помещении: офис, производство, склад, магазин и т.д.)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник на наружный пост (пост на улице, патруль)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник-парковщик (автотранспорта)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник-кинолог (<a href=\"http://taggerd.su/page/5/21/\"><span style=\"color: #000080\">охрана с собакой</span></a>)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник-швейцар (в рестораны, гостиницы, ночные клубы)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник-консьерж (элитные жилые дома офисы, отели)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник-телохранитель (личный охранник)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- VIP-охранник (для охраны элитных заведений и частных апартаментов)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник-консьерж (в жилые дома)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник-менеджер (офисы)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник для сопровождения МЦ и грузов</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- инкассатор (сопровождение денежных средств)</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- техник-оператор систем видеонаблюдения и средств охраны</font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">- охранник группы &laquo;Быстрого реагирования&raquo; </font></span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">&nbsp;</font></span></span></span></div>\r\n<div style=\"text-align: center\"><strong><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\">Охранные услуги оказываемые&nbsp;ЧОП могут осуществляться с использованием оружия или спецсредств, разрешенных к использованию частным охранным предприятиям.</font></span></span></span></strong></div>\r\n</span></span></span></div>\r\n</div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<table cellspacing=\"0\" cellpadding=\"7\" width=\"700\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\" bgcolor=\"#666699\"><img height=\"110\" alt=\"\" width=\"670\" src=\"/files/storage/images/hop-9.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div style=\"text-align: center\">\r\n<div style=\"text-align: center\"><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: medium\"><strong><span><font size=\"3\">ОКАЗАНИЕ&nbsp;УСЛУГ&nbsp;БЕЗОПАСНОСТИ&nbsp;И&nbsp;ОХРАНЫ - НАША&nbsp;ПРОФЕССИЯ!</font></span></strong></span></span></div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: medium\"><strong><span><font size=\"3\">\r\n<div style=\"text-align: center\"><span style=\"color: #ff0000\"><span style=\"font-family: Comic Sans MS\"><span><span style=\"font-size: medium\"><strong><font size=\"3\">\r\n<table cellspacing=\"1\" cellpadding=\"9\" width=\"400\" align=\"center\" border=\"1\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\" style=\"text-align: center\"><span style=\"color: #ffffff\"><span style=\"font-size: medium\"><strong><a href=\"http://taggerd.su/page/0/19/\"><span style=\"color: #ffffff\">ЗАКАЗАТЬ&nbsp; УСЛУГИ&nbsp; ОН-ЛАЙН</span></a></strong></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n</font></strong></span></span></span></span></div>\r\n</font></span></strong></span></span></div>\r\n</div>\r\n</div>',`date` = '2010-11-08 16:13:04',`display` = 'Да';
insert into `taggerd__pages` set `id` = '11',`parent` = '0',`position` = '14',`autotext` = '',`page` = 'Обратная связь',`name` = 'Обратная связь',`title` = 'ЧОП ТАГГЕРД, услуги охраны в москве, охрана чоп москва, технические системы безопасности, охранные услуги',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает охранные услуги по приемлемым ценам: физическая охрана, личная охрана,  защита информации, оборудование и технические системы безопасности. Недорого, надежно, качественно!',`keywords` = 'заказать услуги охраны, написать в чоп, отправить резюме в чоп, ',`text` = '',`date` = '2010-11-07 11:26:28',`display` = 'Да';
insert into `taggerd__pages` set `id` = '12',`parent` = '0',`position` = '6',`autotext` = '',`page` = 'Виды и типы охраны',`name` = 'ЧОП - плюсы и минусы',`title` = 'ЧОП ТАГГЕРД (Москва) предлагает - охрана объектов недвижимости, любые охранные услуги, технические системы безопасности, охранные услуги',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает охранные услуги по приемлемым ценам: физическая охрана, личная охрана,  защита информации, установка технических систем безопасности. Охрана ЧОП - недорого, надежно, качественно!',`keywords` = 'охрана чоп москва, охранные услуги, услуги охраны,личная охрана, охрана объектов, обеспечение безопасности',`text` = '<h1><span style=\"font-size: xx-small\"><span><span style=\"color: #000080\">ВИДЫ И ТИПЫ ОХРАНЫ / ОХРАННЫЕ УСЛУГИ / ОХРАНА ЧОП - ПЛЮСЫ И МИНУСЫ / </span></span></span></h1>\r\n<h2><span style=\"color: #0000ff\"><span style=\"font-size: large\"><span><span><span style=\"font-family: Arial\"><strong>ЦЕЛИ&nbsp;И СРЕДСТВА ОХРАНЫ</strong></span></span></span></span></span></h2>\r\n<table cellspacing=\"0\" cellpadding=\"8\" width=\"700\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#ffffff\"><img height=\"260\" alt=\"\" width=\"280\" align=\"left\" src=\"/files/storage/images/2.jpg\" /></td>\r\n            <td bgcolor=\"#ccccff\">\r\n            <div style=\"text-align: justify\">&nbsp;</div>\r\n            <div style=\"text-align: justify\"><span style=\"color: #000080\"><em><span style=\"font-size: xx-small\">&quot;<strong>Безопасность</strong> &ndash; это свойство и способность противостоять внешним и внутренним дестабилизирующим факторам или действиям, стойкость по отношению к враждебным посягательствам, достигнутая профилактикой и предупреждением возможных угроз, которые могут нанести ущерб собственности или личности. Безопасность обеспечивается комплексом мер, включающих в себя: создание систем защиты, повышения устойчивости к угрозам для собственности и личности, минимизация убытков в результате локализации источников угроз и чрезвычайных происшествий.</span></em></span></div>\r\n            <div style=\"text-align: justify\">&nbsp;</div>\r\n            <div style=\"text-align: justify\"><strong><span style=\"color: #000080\"><em><span style=\"font-size: xx-small\">Охрана</span></em></span></strong><span style=\"color: #000080\"><em><span style=\"font-size: xx-small\"> &ndash; это способ и средство обеспечения безопасности и поддержания требуемого режима функционирования. Охрана осуществляется путем организации физической защиты, технической защиты, наблюдения, мониторинга электронных средств охраны и чрезвычайного реагирования на сигналы тревоги и опасности.</span></em></span></div>\r\n            <div style=\"text-align: justify\">&nbsp;</div>\r\n            <div style=\"text-align: justify\"><strong><span style=\"color: #000080\"><em><span style=\"font-size: xx-small\">Охранники</span></em></span></strong><span style=\"color: #000080\"><em><span style=\"font-size: xx-small\"> &ndash; сотрудники, осуществляющие физическую охрану, прошедшие соответствующую подготовку и получившие специальные навыки и знания для обеспечения безопасности субъекта или объекта.&quot;</span></em></span></div>\r\n            <div><span style=\"color: #000080\">&nbsp;</span></div>\r\n            <div align=\"right\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\"><em>Из учебника по безопасности</em></span></span></div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: medium\"><span style=\"color: #0000ff\"><span style=\"font-size: larger\"><span style=\"font-size: smaller\"><span style=\"font-size: larger\"><span style=\"color: #000080\"><span style=\"color: #000080\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"2\"><span style=\"color: #0000ff\"><span style=\"color: #000080\"><span style=\"color: #0000ff\"><strong>Если Вы решили защитить</strong></span> свой бизнес и нанять охрану, но сомневаетесь, какой из множества вариантов выбрать - государственную (ОВО МВД, ВГУП), частную охрану (ЧОП) или же обойтись собственными силами и нанять сторожей, контролеров и т.п.; если Вы не знаете какие технические средства приобрести, где и как их установить - давайте попробуем разобраться в этой проблеме вместе&hellip;</span></span></font></span></span></span></span></span></span></span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div>\r\n<div style=\"text-align: left\">\r\n<div style=\"text-align: justify\">\r\n<div>\r\n<div><span style=\"color: #0000ff\"><strong>Для начала следует</strong></span> <span style=\"color: #000080\">выявить возможные угрозы для Вас и Вашего бизнеса и, исходя из этого, определить набор необходимых мер безопасгости. Для этого следует исследовать объект охраны на предмет технической укрепленности, определения его категории сложности, на основе чего разработать комплекс мер (Концепция безопасности), включающих физическую охрану, административные и организационные меры, техническую и электронную защиту. Чем, более совершенна технология применяемой системы охраны, тем, более надежно будет защищен охраняемый объект.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span style=\"font-size: medium\">ВИДЫ И ТИПЫ ОХРАНЫ:</span></strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<table cellspacing=\"1\" cellpadding=\"7\" width=\"100%\" align=\"center\" border=\"1\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div align=\"center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">№</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div align=\"center\"><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">Различия и типов и видов</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div align=\"center\"><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">Содержание</span></span></strong></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div style=\"text-align: center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">1.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По принадлежности</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Государственная организация (ведомственная, вневедомственная): милиция, ОВО, ВОХР, ВГУП и др.</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Частная организация: ЧОП, ЧОО, подразделение СБ при предприятии и др.</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Частные лица: вахтеры, контролеры, сторожа, консьержи и др.</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Комбинированная (смешанная) охрана</span></span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div style=\"text-align: center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">2.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По способам защиты</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Физическая охрана (охранники)</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Техническая защита (механическая, электронная)</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Корпоративная защита </span></span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div style=\"text-align: center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">3.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По объектам защиты</span></span></strong></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">&nbsp;</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Личная (персональная) охрана</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Физическая охрана стационарных объектов</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Физическая охрана мобильных объектов</span></span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">4.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По способам физической охраны</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Стационарные посты (внутренние, наружные)</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Обходные посты (внутренние, наружные)</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Сопровождение</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Патрульные авто- и мото- группы</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Группа быстрого реагирования</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- Кинологи с собаками</span></span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div style=\"text-align: center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">5.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По вариантам технической защиты</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- охранная и пожарная сигнализация, <span style=\"font-size: xx-small\"><span style=\"color: #000080\">периметровые системы охраны,</span></span></span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- первичные средства пожаротушения,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- системы видеонаблюдения, IP-телевидение</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- системы пожаротушения,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- средства оповещение и управления эвакуацией,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- антитеррористическое оборудование,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- досмотровое оборудование,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- средства защиты информации (телекоммуникационные сети, связь),</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- специальный транспорт (бронеавтомобили),</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- GSM-сигнализация,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- пультовая охрана,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- тревожная кнопка,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- системы ограничения и контроля доступа, домофоны,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- инженерно-технические средства (ограждения, турникеты, шлагбаумы, замки),</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- антикражные системы,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- дежурное освещение,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- интегрированные системы безопасности.</span></span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div style=\"text-align: center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">6.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По административным мерам</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- кадровая безопасность,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- режимные мероприятия,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- правила внутреннего распорядка,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- инструкции,</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- обязательства по защите информации</span></span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div style=\"text-align: center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">7.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По категории сложности охраны</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- простой тип: стационарные или обходные посты, без вооружения и технических средств;</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- усложненный тип: с использованием спецсредств, служебных собак, оборудования;</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- сложный комбинированный тип: стационарные посты, авто- мото- или пеший патруль, вооруженные и невооруженные охранники, использование собак, комплексных систем безопасности и др.</span></span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div style=\"text-align: center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">8.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По режиму охраны</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ffffcc\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- круглосуточный (непрерывный)</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- дневной</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- ночной</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- почасовой</span></span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"4%\" bgcolor=\"#ccccff\">\r\n            <div style=\"text-align: center\"><span style=\"font-size: xx-small\"><span style=\"color: #000080\">9.</span></span></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"31%\" bgcolor=\"#ccccff\">\r\n            <div><strong><span style=\"font-size: xx-small\"><span style=\"color: #000080\">По степени важности объекта</span></span></strong></div>\r\n            </td>\r\n            <td valign=\"top\" width=\"63%\" bgcolor=\"#ccccff\">\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- с упрощенным режимом охраны</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- с особым режимом охраны</span></span></div>\r\n            <div><span style=\"font-size: xx-small\"><span style=\"color: #000080\">- со смешанным режимом охраны</span></span></div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;</div>\r\n</div>\r\n</div>\r\n<div>\r\n<div><span style=\"color: #0000ff\"><strong><span>Набор технических средств </span></strong></span><span style=\"color: #000080\">необходимых на конкретном объекте может определить только специалист в этой области, исходя из степени важности, категории сложности объекта,&nbsp;схемы расположения, конструктивных особенностей, требований к защищенности и режиму охраны. Комплексная защита может включать в себя: системы охранной и пожарной сигнализации, контроля доступа, видеонаблюдения и другое оборудование. Следует помнить, что количество средств технической защиты прямо обратно пропорционально количеству сил и средств физической охраны используемых на объекте.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><strong><span style=\"color: #0000ff\"><span>Тип физической охраны</span></span></strong><span style=\"color: #000080\"> следует определять исходя из прогноза возможных угроз и респекта Вашего бизнеса, ибо порой для поддержания имиджа компании требуется индивидуальный подход к внешнему виду и содержанию охраны. Следует также обратить особое внимание на стоимость различных вариантов охраны и взвесить свои финансовые возможности. От решения этих вопросов во многом будет зависеть то, кто Вас будет охранять - сотрудник милиции, профессиональный охранник или сторож - дядя Вася. Возможен, конечно, и смешанный вариант охраны (в ряде случаев он может оказаться предпочтительным и оптимальным). Если после анализа рассмотренных выше аспектов решение так и не сформировалось, можно переходить к следующим аргументам.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><strong><span style=\"color: #0000ff\"><span>Плюсы Вневедомственной охраны </span></span></strong><span style=\"color: #000080\">(ОВО) налицо &ndash; это, прежде всего, &laquo;милицейская форма&raquo; (немаловажный аргумент в некоторых ситуациях), спецподготовка, &laquo;особые&raquo; полномочия и оружие. К существенным плюсам можно также отнести минимально-нормативное время реагирования в чрезвычайных ситуациях.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span>В случае со сторожами</span></strong></span><span style=\"color: #000080\"> и вахтерами, о каких-либо дополнительных правах, оружии или спецсредствах, а также о профессиональной подготовке вообще говорить не приходится.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span>Охрана ЧОП</span></strong></span><span style=\"color: #000080\"> одета в форменную одежду или строгий костюм, могжет иметь огнестрельное оружие и спецсредства, собственные службы экстренного реагирования (ГБР), а подготовка охранников (физическая и правовая) ничем не хуже милицейской. &laquo;Прав&raquo; у частной охраны, конечно, поменьше чем у милиции, но возможностей для гибкого решения конфликтных ситуаций значительно больше. Также хорошо известно, что &laquo;особые&raquo; полномочия милиции в различных ситуациях могут сыграть как в ту, так и в другую сторону. Следует учесть и то, что милицейская охрана не несет обязательств по защите вашей конфиденциальной информации и коммерческой тайны.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span>Стоимость охраны</span></strong></span><span style=\"color: #000080\"> может оказаться одним из важнейших аргументов при выборе. У Вневедомственной охраны расценки достаточно высокие (в полтора-два раза выше, чем у &quot;частников&quot;) и к тому же постоянно монопольно растут. ЧОП-ы в этом плане действует более гибко, согласуя свои тарифы и их индексацию с возможностями Заказчика.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span>Самый &laquo;дешевый&raquo; вид</span></strong></span><span style=\"color: #000080\"> охраны на первый взгляд может показаться вариант со сторожами, но эта дешевизна может дорого обойтись потом, из-за отсутствия полной материальной ответственности (в случае причинения вам ущерба, Вы сможете претендовать лишь на часть месячного заработка своего горе-сторожа).</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span>Не все так просто </span></strong></span><span style=\"color: #000080\">и с материальной ответственностью Вневедомственной охраны (ОВО). В случае причинения ущерба по вине милиции, для взыскания причитающихся вам убытков потребуется не только бухгалтерская документация на пропавшее имущество, но и веские доказательства вины, подтвержденные решением суда, которое в данном случае будет полностью зависеть от результатов официального следствия. Как Вы думаете, на чьей они будут стороне? Да и сами договоры с ОВО, содержат ряд юридически хитростей, позволяющих избежать материальную ответственность. При этом милицейский договор содержит высокие требования к заказчикам и техническому оснащению объектов, что может привести к дополнительным существенным расходам на его дооборудование. Помимо этого, перед подписанием договора необходимо предоставить внушительный комплект документов, включая уставные, регистрационные документы, свидетельства на собственность, справки об отсутствии долговых обязательств, залога или ареста на имущество, отсутствия судебных споров. Если, в результате проверки, в ваших документах окажется что-то не так, Вам могут отказать в охране или использовать данный факт при наступлении материальной ответственности.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span>Совсем&nbsp;иная ситуация</span></strong></span><span style=\"color: #000080\"> с охранными агентствами. Частная охранная организация&nbsp;несет полную материальную ответственность за принятое под охрану имущество и в случае его порчи или кражи заинтересован не афишировать инцидент, а решить проблему мирным путем, не доводя дело до следствия и суда, ибо ЧОП дорожит своей репутацией и каждым клиентом. Многие&nbsp;охранные агентства&nbsp;даже страхуют собственную ответственность&nbsp;и охраняемое имущество&nbsp;в страховых компаниях.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span>Еще один существенный аргумент </span></strong></span><span style=\"color: #000080\">&ndash; это кадры. Кадры, как в МВД, так и в частную охрану проходят специальный отбор - проверяется репутация по прежним местам работы, профессиональная и физическая пригодность, психологическую уравновешенность и т.д. Но, индивидуальный подбор, с учетом пожеланий и требований конкретного Заказчика, производят только частные охранные организации. Кадрами сторожей (подбором, контролем и увольнением) Вам придется заниматься самостоятельно, и поверьте, это не такой простой вопрос.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #0000ff\"><strong><span>Подводя итог </span></strong></span><span style=\"color: #000080\">вышесказанному, хочется отметить, что каждый вариант охраны - частная охрана, милиция, вахтеры -&nbsp;имеет свои плюсы и минусы, и только Вам решать на чем остановиться. Иногда имеет смысл выставить &quot;смешанную&quot; охрану: например на входных дверях - милицейский пост, на внутренних постах - охранники ЧОП... </span></div>\r\n<span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\"><strong><font size=\"2\">Надеемся, что данная информация поможет Вам сделать правильный выбор.&nbsp;</font></strong><strong><font size=\"2\">Если же потребуется дополнительный совет - обращайтесь к нам, в&nbsp;охранное агентство&nbsp;&quot;ТАГГЕРД&quot;, мы тщательно исследуем вашу проблему, дадим рекомендации и даже разработаем комплексную концепцию безопасности, учитывающую особенности охраны объекта и специфику Вашего бизнеса.</font></strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><strong><span><span><font size=\"2\">См. также статью &quot;<a href=\"http://taggerd.su/page/5/13/\"><span style=\"color: #0000ff\">Выбор ЧОП</span></a>&quot;</font></span></span></strong></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"font-size: medium\"><span style=\"color: #0000ff\"><span><strong>ОКАЗАНИЕ&nbsp;УСЛУГ&nbsp;БЕЗОПАСНОСТИ - НАША&nbsp;ПРОФЕССИЯ!</strong></span></span></span></div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>',`date` = '2010-11-08 12:04:09',`display` = 'Да';
insert into `taggerd__pages` set `id` = '14',`parent` = '4',`position` = '1',`autotext` = '',`page` = 'Охранная и пожарная сигнализация',`name` = 'Охранная и пожарная сигнализация',`title` = 'ЧОП ТАГГЕРД (Москва) технические системы безопасности, охранная и пожарная сигнализация',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает охранные услуги: физическая охрана, личная охрана,  защита информации, оборудование и технические системы безопасности, охранная и пожарная сигнализация. Недорого, надежно, качественно!',`keywords` = 'комплексные системы безопасности, охранная сигнализация, контроль доступа, системы оповещения, пожарная сигнализация, ip-видеонаблюдение',`text` = '<div>\r\n<h1><span style=\"font-size: xx-small\"><span style=\"color: #000080\"><span style=\"\"><span style=\"font-family: Tahoma\">СИСТЕМЫ ОХРАННОЙ&nbsp;И&nbsp;ПОЖАРНОЙ СИГНАЛИЗАЦИИ /ОХРАНА ПЕРИМЕТРА / ВИДЫ И ТИПЫ СИГНАЛИЗАЦИИ /</span></span></span></span></h1>\r\n<h2><span style=\"color: #0000ff\"><span><span style=\"font-size: large\"><span style=\"font-family: Tahoma\"><b>ОХРАННО-ПОЖАРНАЯ СИГНАЛИЗАЦИЯ</b></span></span></span></span></h2>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"200\" align=\"left\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img height=\"291\" alt=\"\" width=\"280\" src=\"/files/storage/images/o-p-signalizacia.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div><span style=\"font-size: larger\"><span style=\"color: #000080\">\r\n<div style=\"text-align: justify\"><span><span><font size=\"2\">Системы охранной и пожарной сигнализации устанавливаются на объектах как правило совместно, но могут быть установлены и по отдельности. Основной задачей охранной сигнализации является выявление факта вторжения на подконтрольную территорию и активизация сигнальных устройств на пульте сигнализации на посту охраны или предупредительных устройств на местах (светового сигнала, звуковой сирены). Основной задачей системы пожарной сигнализации является обнаружение места возникновения очага возгорания на ранней стадии, оповещение о пожаре находящихся на объекте людей, формирование управляющих сигналов для систем автоматического пожаротушения, для&nbsp;инженерных систем здания (отключения вентиляции, включения систем дымоудаления, опускания лифтов и&nbsp;т.д.).</font></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span><span><font size=\"2\">Приёмные устройства сигнализации расположенные на пульте управления служат для приёма тревожного сигнала от охранных и пожарных извещателей и определение&nbsp;конкретного места происшествия,&nbsp;(номера охраняемой зоны, с которой был принят сигнал).</font></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span><span><font size=\"2\">Современная охранно-пожарная сигнализации имеет собственную развитую функцию оповещения. Благодаря возможности гибкой интеграции с различными системами безопасности и жизнеобеспечения здания, охранно-пожарная сигнализация может быть настроена на выполнение самых разнообразных задач в случае обнаружения пожара, несанкционированного прониконвения или неисправности в одной из зон. Например, при возникновении пожарной тревоги, панель пожарной сигнализации может автоматически отключать электропитания в зоне пожара, включение системы пожаротушения, &laquo;дымоудаления&raquo;, разблокировку выходов на путях эвакуации и одновременное включение системы оповещения с информацией для мест возгорания и смежных с ней зон.</font></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span><span><font size=\"2\">Все охранные и пожарные сигнализации&nbsp;построены по одной схеме: имеется набор датчиков, распределенных по помещениям в соответствии с определенным планом разбивки его на охраняемые зоны, за каждую из которых отвечает один или несколько датчиков. Все датчики связаны сигнальными шлейфами с центральным устройством - контрольной панелью (пультом управления) сигнализации. Системы охранной и пожарной стгнализации снабжаются резервными источниками питания, обеспечивающими бесперебойное функционирование систем при отключении напряжения основного источника питания.</font></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span><span><font size=\"2\">Система охранной и пожарной сигнализации может программироваться непосредственно с пульта (панели управления) с помощью встроенной клавиатуры или с помощью управляющего программного обеспечения, установленного на компьютере. Назначение контрольной панели сигнализации - распознать тревожный сигнал, поступивший от сработавших датчиков, идентифицировать опасную зону.</font></span></span></div>\r\n<div style=\"text-align: justify\"><span><span><span><span><span><font size=\"2\">Все элементы системы сигнализации могут быть подключены по беспроводной технологии. Это позволит в любой момент изменить конфигурацию расположения датчиков, не прибегая к прокладке кабеля.</font></span></span></span></span></span></div>\r\n</span></span></div>\r\n<div>&nbsp;</div>\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"500\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img height=\"324\" alt=\"\" width=\"500\" src=\"/files/storage/images/signalizaciaohrannaya.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: #0000ff\"><span style=\"font-size: small\"><span><strong>К техническим средствам охранно-пожарной сигнализации относятся:</strong></span></span></span>\r\n<div>&nbsp;</div>\r\n<div><span style=\"font-family: Arial\"><span style=\"font-size: larger\">\r\n<div><strong><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">- охранно-пожарные извещатели (датчики),</font></span></span></strong></div>\r\n<div><strong><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">- охранно-пожарные приемно-контрольные приборы и пульты (панели),</font></span></span></strong></div>\r\n<div><strong><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">- исполняющие устройства: пожарные и охранные сигнально-пусковые устройства, GSM и коммуникационные модули оповещения,</font></span></span></strong></div>\r\n<div><strong><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">- приборы управления,</font></span></span></strong></div>\r\n<div><strong><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">- охранно-пожарные оповещатели (световые и звуковые сирены, указатели эвакуации),</font></span></span></strong></div>\r\n<div><strong><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">- тревожная кнопка,</font></span></span></strong></div>\r\n<div><strong><span style=\"color: #000080\">- специальные источники бесперебойного питания.</span></strong></div>\r\n<div><span><span>&nbsp;</span></span></div>\r\n<div><span style=\"color: #0000ff\"><span style=\"font-size: small\"><strong><font size=\"+0\">Типы датчиков для систем пожарной сигнализации</font></strong></span></span><span style=\"color: #0000ff\"><strong><font size=\"2\">:</font></strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: #000080\">дымовые, тепловые, комбинированные, ручные, а&nbsp;также специальные извещатели пламени, линейные инфракрасные&nbsp;&nbsp;&nbsp;и аспирационные системы;</span><font size=\"2\"><span><span><br />\r\n</span></span><span>&nbsp;</span></font></div>\r\n<div><span style=\"color: #0000ff\"><span style=\"font-size: small\"><strong>Типы датчиков для системы охранной сигнализации:</strong></span></span></div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">- Магнитно-контактные датчики (герконы) открытия двери, окна, форточки и т.д.<br />\r\n- Пассивные инфракрасные датчики движения<br />\r\n- Активные инфракрасные датчики движения<br />\r\n- Акустические датчики разбития стекла<br />\r\n- Вибрационные извещатели для определения разрушения или пролома поверхности<br />\r\n- Емкостные извещатели для определения прикосновения к охраняемому объекту<br />\r\n- Радиоволновые датчики для контроля внутреннего объема помещения<br />\r\n- Датчики утечки воды<br />\r\n- Датчики утечки газа<br />\r\n- Датчики температуры<br />\r\n- Датчик отключения электричества</font></span></span></div>\r\n<div><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">&nbsp;</font></span></span></div>\r\n<div><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">Системы сигнализации могут быть автономными или заведены на пульт сигнализации на посту охраны. Автономные системы сигнализации могут иметь различные каналы передачи тревожного сигнала &ndash; по телефонной линии, по радиоканалу, по GSM каналу.</font></span></span></div>\r\n<div><span style=\"color: #000080\"><span style=\"font-size: larger\"><font size=\"2\">&nbsp;</font></span></span></div>\r\n<div><span style=\"color: #000080\">Наша компания рекомендует качественное сертифицированное оборудование от&nbsp;ведущих мировых производителей:&nbsp;GE Security (Aritech), DSC, System Sensor, CROW, Ademco, TaneAlarm, Optex, Jablotron, Tantos, Pironix, Болид, Аргус, Texecom, Parsec, Pironix, Legos, Alarm, Visonic, Bosh и&nbsp;др. Выбор того&nbsp;или иного типа оборудования определяется решаемыми задачами и&nbsp;возможностями заказчика.</span></div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n</span></span></div>\r\n<table cellspacing=\"1\" cellpadding=\"0\" width=\"700\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\">&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><span><span><font size=\"2\">\r\n<div><span style=\"font-size: large\"><span style=\"color: #0000ff\"><b>ОХРАНА&nbsp;ПЕРИМЕТРА ОБЪЕКТА</b></span></span></div>\r\n<div><span><span><b><font size=\"2\">&nbsp;</font></b></span></span></div>\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"250\" align=\"left\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img height=\"211\" alt=\"\" width=\"280\" src=\"/files/storage/images/ohranaperimetra.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify\"><span style=\"font-family: Arial\"><span style=\"color: #000080\"><font size=\"2\">В наши дни наиболее часто применяются следующие виды систем охраны периметра: емкостные, оптические, радиолучевые, сейсмические.</font></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-family: Arial\"><span style=\"color: #000080\">&nbsp;</span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-family: Arial\"><span style=\"color: #000080\"><font size=\"2\">Существует большое разнообразие систем охраны периметра. Обычно эти системы из-за ряда ограничений рекомендуется применять в комплексе с другими системами охраны (колючая проволока, видеонаблюдение, контроль доступа и др.), тогда средства безопасности становятся, наиболее эффективны, так как покрывают недостатки друг друга.</font></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-family: Arial\"><span style=\"color: #000080\">&nbsp;</span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-family: Arial\"><span style=\"color: #000080\"><font size=\"2\">Принцип работы систем охраны периметра достаточно прост, изменение параметров электрического поля при приближении или прикосновении нарушителя. Технически система представляет собой электрический контур, подключенный к контрольному устройству. При изменении параметров безопасности, например, касание проводника или приближение к нему человека, система охраны периметра посылает сигнал о нарушении границы периметра местной службе охраны, либо службе управления вневедомственной охраны. В настоящий момент техника охраны периметра настолько разнообразна, что позволяет организовать эффективную защиту любого периметра без полосы отчуждения и предварительной очистки рубежей.</font></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-family: Arial\"><span style=\"color: #000080\">&nbsp;</span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-family: Arial\"><span style=\"color: #000080\"><font size=\"2\">Системы охраны периметров занимают особое место в ряду систем безопасности, они являются средствами раннего обнаружения, которые позволяют обнаружить нарушителя еще до его проникновения на охраняемую территорию.</font></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"font-family: Arial\"><span style=\"color: #000080\"><font size=\"2\">См. также <span style=\"color: #0000ff\"><strong><a href=\"http://taggerd.su/page/4/15/\"><span style=\"color: #0000ff\">Системы видеонаблюдения</span></a></strong></span>.</font></span></span></div>\r\n<div>&nbsp;</div>\r\n<div><span><span>\r\n<div><span><span><strong><span><span><span>\r\n<div style=\"text-align: center\"><span style=\"color: #ff0000\"><span style=\"font-size: medium\"><strong>ОКАЗАНИЕ&nbsp;УСЛУГ&nbsp;БЕЗОПАСНОСТИ - НАША&nbsp;ПРОФЕССИЯ!</strong></span></span></div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"color: #ff0000\"><span style=\"font-size: medium\"><strong>\r\n<div style=\"text-align: center\"><span style=\"color: #ff0000\"><span style=\"font-family: Comic Sans MS\"><span><span style=\"font-size: medium\"><strong><font size=\"3\">\r\n<table cellspacing=\"1\" cellpadding=\"9\" width=\"400\" align=\"center\" border=\"1\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\" style=\"text-align: center\"><span style=\"color: #ffffff\"><span style=\"font-size: medium\"><strong><a href=\"http://taggerd.su/page/0/19/\"><span style=\"color: #ffffff\">ЗАКАЗАТЬ&nbsp; УСЛУГИ&nbsp; ОН-ЛАЙН</span></a></strong></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n</font></strong></span></span></span></span></div>\r\n</strong></span></span></div>\r\n</span></span></span></strong></span></span></div>\r\n</span></span></div>\r\n</font></span></span></div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>',`date` = '2010-10-25 15:15:17',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '15',`parent` = '4',`position` = '2',`autotext` = '',`page` = 'Системы видеонаблюдения',`name` = 'Системы видеонаблюдения',`title` = 'ЧОП ТАГГЕРД, услуги охраны в москве, технические системы безопасности, системы видеонаблюдения',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает охранные услуги: физическая охрана, личная охрана,  защита информации, оборудование и технические системы безопасности и видеонаблюдения. Недорого, надежно, качественно!',`keywords` = 'системы безопасности, видеонаблюдение, мониторинг, интеллектуальное видеонаблюдение, ip-видеонаблюдение',`text` = '<h1><span style=\"font-size: xx-small\"><span style=\"\"><span style=\"color: #000080\"><span style=\"font-family: Tahoma\"><b>ИНТЕЛЕКТУАЛЬНОЕ ВИДЕОНАБЛЮДЕНЕ / IP-ВИДЕОНАБЛЮДЕНИЕ / БЕСПРОВОДНОЕ ВИДЕОНАБЛЮДЕНИЕ</b></span></span></span></span></h1>\r\n<h2><span style=\"font-size: large\"><span style=\"color: #0000ff\"><span><span style=\"font-family: Tahoma\"><b>СИСТЕМЫ&nbsp;ВИДЕОНАБЛЮДЕНИЯ</b></span></span></span></span></h2>\r\n<div>\r\n<div><span style=\"color: #000080\"><span style=\"font-size: larger\">\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span><span><font color=\"#000080\">\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"250\" align=\"left\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img height=\"250\" alt=\"\" width=\"250\" src=\"/files/storage/images/Video.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</font></span></span></div>\r\n<div style=\"text-align: justify\"><span><span><font color=\"#000080\">Именно изображение несет максимум информации об окружающем пространстве и поступающей из него угрозе, а потому видеокамеры самых различных конструкций стали наиболее востребованным элементом любых систем безопасности. Некоторые камеры &laquo;видят&raquo; мир не только в том спектре, который воспринимает человеческий глаз, но и в инфракрасных лучах, что делает возможным использование видеокамер при слабом освещении и ночью. </font></span></span></div>\r\n<div style=\"text-align: justify\"><span><span>\r\n<div><font color=\"#000080\"><br />\r\nИзображение с видеокамер транслируется по проводам или радиоканалам на централизованный видеосервер, где обрабатывается специальными программами. Профессиональная система видеонаблюдения автоматически реагирует на малейшее движение, подавая сигнал тревоги и записывая изображение в архив. Некоторые системы даже настраиваются на заданные характеристики движущихся объектов. Например, система может не реагировать на проходящих через ворота предприятия рабочих, но подает сигнал тревоги при приближении автомобиля.</font></div>\r\n<div style=\"text-align: left\"><font color=\"#000080\"><br />\r\nОбъем сохраняемой информации ограничивается лишь требованиями, которые ставятся перед разработчиками системы безопасности. Само изображение всегда может быть извлечено из архива для просмотра, обработано программами видео наблюдения, увеличивающими четкость и контрастность, распечатано на принтере и пр.</font></div>\r\n<div style=\"text-align: left\"><span><font color=\"#000080\"><br />\r\nСовременная система видеонаблюдения имеет ряд вспомогательных функций, например, дистанционный контроль объекта. В этом случае лицо, имеющее на то полномочия, может наблюдать за всем, что происходит на объекте с помощью обычного мобильного телефона или ноутбука.&nbsp;&nbsp;</font></span></div>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<div><span><font color=\"#000080\">\r\n<div>&nbsp;</div>\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"250\" align=\"left\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"text-align: left\"><img height=\"380\" alt=\"\" width=\"320\" src=\"/files/storage/images/sistemyvideo.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: left\"><span style=\"font-size: medium\"><span style=\"color: #0000ff\"><span><strong>В систему видеонаблюдения </strong></span></span></span><span style=\"font-size: medium\"><span style=\"color: #0000ff\"><span><strong>входит:</strong></span></span></span></div>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<div style=\"text-align: left\"><em><strong>- видеокамеры (портативные, уличные (в термокожухах), беспроводные, поворотные, купольные, цветные или монохромные и т.д.</strong></em></div>\r\n<div style=\"text-align: left\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: left\"><em><strong>- видеорегестраторы или специализированные платы видеонаблюдения,</strong></em></div>\r\n<div style=\"text-align: left\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: left\"><em><strong>- устройства обработки и записи видеосигнала (видеомагнитофоны, видеорегистраторы,&nbsp; квадратры, мультплексоры, видео рекордеры),</strong></em></div>\r\n<div style=\"text-align: left\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: left\"><em><strong>- видеомониторы,&nbsp;</strong></em></div>\r\n<div style=\"text-align: left\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: left\"><em><strong>- блоки бесперебойного питания,</strong></em></div>\r\n<div style=\"text-align: left\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: left\"><em><strong>- видео-серверы,</strong></em></div>\r\n<div style=\"text-align: left\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: left\"><em><strong>- ИК-подсветки,</strong></em></div>\r\n<div style=\"text-align: left\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: left\"><em><strong>- монтажный кабель (либо радиоизлучатели),</strong></em></div>\r\n<div style=\"text-align: left\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: left\"><em><strong>- программное компьютерное обеспечение.</strong></em></div>\r\n</font></span></div>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span><span><font size=\"2\">Обычно монтаж систем безопасности не ограничивается только видеонаблюдением, нередко осуществляется интеграция с системами контроля доступа (СКД).</font></span></span></div>\r\n<div style=\"text-align: left\"><span><span><font size=\"2\">&nbsp;</font></span></span></div>\r\n<div style=\"text-align: left\"><span><span><font size=\"2\">Наша компания рекомендует качественное сертифицированное оборудование от&nbsp;ведущих мировых производителей: PELCO, JVC, KT&amp;C, Computar, BOSCH, Geutebrueck, Sony, GESecurity, Panasonic, Samsung, EverFocus, Watec, Hant, Germikom, Sanyo и&nbsp;др. Выбор того&nbsp;или иного типа оборудования определяется решаемыми задачами и&nbsp;возможностями заказчика.</font></span></span></div>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<table cellspacing=\"1\" cellpadding=\"0\" width=\"700\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\" bgcolor=\"#6600ff\">\r\n            <div style=\"text-align: left\">&nbsp;</div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n</div>\r\n</span></span></div>\r\n<div>&nbsp;</div>\r\n</span></span></div>\r\n<div>&nbsp;<span style=\"font-size: large\"><span><span style=\"color: #0000ff\"><span><span style=\"font-family: Arial\"><b>IP-видеонаблюдение</b></span></span></span></span></span></div>\r\n<div>&nbsp;</div>\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"600\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\">\r\n            <div><img height=\"311\" alt=\"\" width=\"600\" src=\"/files/storage/images/IP-videonabludenie.jpg\" /></div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">Благодаря развитию интернет-технологий, теперь стало возможно круглосуточно наблюдать за домом, офисом, предприятием, находясь на любом удалении от места, в любой точке мира. Достаточно при этом иметь под рукой компьютер или сотовый телефон. Удаленный видеоконтроль особенно необходим тем, кто по долгу службы не имеет возможности находиться дома. Например, с помощью удаленного видеонаблюдения стало возможным контролировать работу наемного персонала: нянь, домработниц, прислуги и т.п.</span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">&nbsp;</span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">IP-видеонаблюдение &ndash; новое перспективное направление развития рынка безопасности, основанное на&nbsp;высокоэффективных алгоритмах сжатия и&nbsp;передачи информации по&nbsp;сетям TCP/IP, позволяют вести эффективное круглосуточное наблюдение удаленного объекта&nbsp;или группы территориально распределенных объектов. Одновременно могут&nbsp;контролироваться в&nbsp;реальном масштабе времени десятки, сотни и&nbsp;даже тысячи камер видеонаблюдения, а&nbsp;также обеспечивается&nbsp;непрерывная запись происходящих событий на&nbsp;цифровые носители. IP-камеры могут быть установлены в любом месте, где есть сеть LAN, или могут быть подключены напрямую, через модем или сотовый телефон. При устройстве IP-видеонаблюдения работа осуществляется только с цифровыми данными, которые передаются и обрабатываются в общей сети. Аналоговый видеосигнал оцифровывается ip-камерой или ip-видеосервером и в дальнейшем данные могут быть записаны на любой (даже удаленный) видеорегистратор. Просмотр получаемого изображения в он-лайн режиме осуществляется, через web-браузер. через подключенный к сети персональный компьютер или на экране охранных мониторов через IP-сервер.</span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">&nbsp;</span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">IP-видеонаблюдение &ndash; альтернативное решение для сложных крупномасштабных объектов, для построения распределенных решений, для реализации проектов, где требуется обеспечить передачу данных на большие расстояния и их централизованное хранение.</span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">&nbsp;</span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">Наша компания рекомендует качественное сертифицированное оборудование IP-видеонабдюдения от&nbsp;ведущих мировых производителей: Mavix, Axis, Boch, Arecont, Vision, Smartec, JVC и др. Выбор того&nbsp;или иного типа оборудования определяется решаемыми задачами и&nbsp;возможностями заказчика.</span></span></span></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;<span style=\"color: #000080\">См. также</span><strong><span style=\"color: #000080\"> <a href=\"http://taggerd.su/page/4/16/\"><span style=\"color: #0000ff\">Системы контроля доступа</span></a>&nbsp; (СКД).</span></strong></div>\r\n<div><strong><span style=\"color: #000080\">&nbsp;</span></strong></div>\r\n<div><span><span>\r\n<div style=\"text-align: center\"><span style=\"font-size: medium\"><span style=\"color: #ff0000\"><strong>ОКАЗАНИЕ&nbsp;УСЛУГ&nbsp;БЕЗОПАСНОСТИ - НАША&nbsp;ПРОФЕССИЯ!</strong></span></span></div>\r\n</span></span></div>\r\n</div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div style=\"text-align: center\">\r\n<div style=\"text-align: center\"><span style=\"color: #ff0000\"><span style=\"font-family: Comic Sans MS\"><span><span style=\"font-size: medium\"><strong><font size=\"3\">\r\n<table cellspacing=\"1\" cellpadding=\"9\" width=\"400\" align=\"center\" border=\"1\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\" style=\"text-align: center\"><span style=\"color: #ffffff\"><span style=\"font-size: medium\"><strong><a href=\"http://taggerd.su/page/0/19/\"><span style=\"color: #ffffff\">ЗАКАЗАТЬ&nbsp; УСЛУГИ&nbsp; ОН-ЛАЙН</span></a></strong></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n</font></strong></span></span></span></span></div>\r\n</div>',`date` = '2010-10-25 15:15:51',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '16',`parent` = '4',`position` = '3',`autotext` = '',`page` = 'Контроль доступа',`name` = 'Контроль доступа',`title` = 'ЧОП ТАГГЕРД (Москва), услуги охраны, технические системы безопасности, охранное оборудование, контроль доступа',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает охранные услуги: физическая охрана, личная охрана,  защита информации, оборудование и технические системы безопасности, котроль доступа. Недорого, надежно, качественно!',`keywords` = 'системы безопасности, охранная сигнализация, контроль доступа, скд, системы контроля доступа, оборудование конроля доступа',`text` = '<h1><span style=\"font-size: xx-small\"><span style=\"\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-family: Tahoma\"><b>СИСТЕМЫ КОНТРОЛЯ ДОСТУПА / ДОМОФОНЫ / УСТАНОВКА СКД /</b></span></span></span></span></h1>\r\n<h2><span style=\"font-size: large\"><span style=\"color: rgb(0,0,255)\"><span><span style=\"font-family: Tahoma\"><b>КОНТРОЛЬ&nbsp;ДОСТУПА&nbsp;В ПОМЕЩЕНИЯ</b></span></span></span></span>&nbsp;</h2>\r\n<div>\r\n<h2><span style=\"font-size: large\"><span style=\"color: rgb(255,0,0)\"><span><span><strong>1. СИСТЕМЫ КОНТРОЛЯ ДОСТУПА (СКД)&nbsp;</strong></span></span></span></span></h2>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"250\" align=\"left\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img height=\"233\" alt=\"\" width=\"280\" src=\"/files/storage/images/kontroldostupa-1.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">Системы контроля доступа, как часть охранной и учетной системы, эффективны и многофункциональны. Такие системы используются как для ограничения пропускного режима в &laquo;закрытые для посторонних&raquo;, контролируемые зоны, контроля за передвижением персонала, поиск требуемого сотрудника в любой момент времени, так и в качестве учета времени прихода/ухода сотрудника и количество отработанного времени в целом. &nbsp;При попытке несанкционированного прохода происходит мгновенное извещение службы охраны объекта.</span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">Для массового прохода на общую территорию предприятия, завода, офисного центра используют автоматические турникеты и шлагбаумы оснащенные различными системами контроля доступа.</span></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: larger\"><span style=\"color: rgb(0,0,128)\"><span><span style=\"font-family: Arial\">Системы доступны и просты в использовании, что позволяет легко и быстро обучить персонал работе с охранным комплексом.</span></span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: medium\"><span style=\"color: rgb(0,0,255)\"><span><strong><span style=\"font-family: Arial\">В систему контроля доступа входит:</span></strong></span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: left\"><strong><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">- средства управления доступом: автономные или сетевые контроллеры, кодовые панели, считыватели, картоприемники,</span></span></span></strong></div>\r\n<div style=\"text-align: left\"><strong><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">- исполняющие устройства: электронные замки и доводчики на двери, турникеты, шлагбаумы, ворота, контрольно-пропускные шлюзы и т.д.</span></span></span></strong></div>\r\n<div style=\"text-align: left\"><strong><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">- персональные индификаторы: магнитная карточка, ключи Touch Memory, &nbsp;бесконтактные карты Proximiti,</span></span></span></strong></div>\r\n<div style=\"text-align: left\"><strong><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">- программное компьютерное обеспечение, с базой данных и статистикой проходов.</span></span></span></strong></div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<table cellspacing=\"7\" cellpadding=\"0\" width=\"450\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\"><img height=\"400\" alt=\"\" width=\"400\" src=\"/files/storage/images/kontroldostupa-1.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify\">\r\n<div><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><font size=\"2\">На платформе систем контроля и&nbsp;управления доступом часто создаются интегрированные системы, объединяющие в&nbsp;единый комплекс системы охранно-пожарной сигнализации, видеонаблюдения, ограничения доступа, жизнеобеспечения зданий. Простейшие системы контроля доступа можно построить с&nbsp;помощью домофонов, электрозамков и&nbsp;кодовых панелей на&nbsp;входных дверях.</font></span></span></div>\r\n<div><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><font size=\"2\">Новые технологии для&nbsp;систем контроля доступа в&nbsp;настоящее время развиваются по&nbsp;направлениям: биометрические системы идентификации и&nbsp;бесконтактные смарт-карты.</font></span></span></div>\r\n<div><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><font size=\"2\">&nbsp;</font></span></span></div>\r\n<div><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><font size=\"2\">Наша компания рекомендует качественное сертифицированное оборудование СКД от&nbsp;ведущих мировых производителей: Parsec, Legos, Appolo-Appacs, Securitron, GUNNEBO, Perco, Dorma, CAME, ОМА, FERMAX, Biosmart, Gate, Parsec, BIOLink, Legos, Panasonic, Sphinx и&nbsp;др. </font></span></span></div>\r\n<div><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\">\r\n<div><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span><font size=\"2\">Турникеты: РостЕвроСтрой, Бастион, Акэс, Porco, Kaba, Automatic System, Itadis.</font></span></span></span></div>\r\n<div><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><font size=\"2\">Автоматические шлагбаумы: Came, Nice, Alutech, Faac, Kaba.</font></span></span></div>\r\n<div>&nbsp;</div>\r\n</span></span></div>\r\n<div><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><font size=\"2\">Выбор того&nbsp;или иного типа оборудования определяется решаемыми задачами и&nbsp;возможностями заказчика.</font></span></span></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"700\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#3300ff\">&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;</div>\r\n<h2><span style=\"color: rgb(255,0,0)\"><span style=\"font-size: large\"><span><strong>2. ДОМОФОНЫ</strong></span></span></span></h2>\r\n</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: larger\"><span style=\"font-family: Arial\">\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"200\" align=\"left\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p><img height=\"200\" alt=\"\" width=\"250\" src=\"/files/storage/images/domofon.jpg\" /></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div><span><span><font size=\"2\">Домофоны являются наиболее простыми устройствами контроля доступа и обеспечения безопасности квартиры, подъезда жилого дома, небольшого офиса или загородного дома и состоят из видеомонитора и наружной видеопанели.</font></span></span></div>\r\n<div><span><span><font size=\"2\">&nbsp;</font></span></span></div>\r\n<div><span><span><font size=\"2\">Основным назначением домофона является обеспечение возможности переговорить и увидеть по монитору&nbsp;посетителей, перед тем, как открыть дверь. Домофон оборудуется дополнительными исполняющими устройствами: электронными дистанционно управляемыми замками&nbsp;и доводчиками на двери.</font></span></span>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><span><span><font size=\"2\">Наша компания рекомендует качественное сертифицированное оборудование от&nbsp;ведущих мировых производителей:&nbsp;Commax, Kocom, WooJu и&nbsp;др. Выбор того&nbsp;или иного типа оборудования определяется решаемыми задачами и&nbsp;возможностями заказчика.</font></span></span></div>\r\n</span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">См. также <a href=\"http://taggerd.su/page/4/17/\"><span style=\"color: rgb(0,0,255)\">Системы оповещения</span></a>.</span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"color: rgb(255,0,0)\"><span style=\"font-family: Comic Sans MS\"><strong><span style=\"font-size: medium\"><span><span><font size=\"3\">ОКАЗАНИЕ УСЛУГ БЕЗОПАСНОСТИ - НАША ПРОФЕССИЯ!&nbsp;</font></span></span></span></strong></span></span></div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"color: rgb(255,0,0)\"><span style=\"font-family: Comic Sans MS\"><strong><span style=\"font-size: medium\"><span><span><font size=\"3\">\r\n<div style=\"text-align: center\"><span style=\"color: rgb(255,0,0)\"><span style=\"font-family: Comic Sans MS\"><span><span style=\"font-size: medium\"><strong><font size=\"3\">\r\n<table cellspacing=\"1\" cellpadding=\"9\" width=\"400\" align=\"center\" border=\"1\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\" style=\"text-align: center\"><span style=\"color: rgb(255,255,255)\"><span style=\"font-size: medium\"><strong><a href=\"http://taggerd.su/page/0/19/\"><span style=\"color: rgb(255,255,255)\">ЗАКАЗАТЬ&nbsp; УСЛУГИ&nbsp; ОН-ЛАЙН</span></a></strong></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n</font></strong></span></span></span></span></div>\r\n</font></span></span></span></strong></span></span></div>\r\n</div>\r\n<p>&nbsp;</p>',`date` = '2010-10-25 15:16:30',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '17',`parent` = '4',`position` = '4',`autotext` = '',`page` = 'Системы оповещения',`name` = 'Системы оповещения',`title` = 'ЧОП ТАГГЕРД (Москва), услуги охраны, технические системы безопасности, охранное оборудование, системы оповещения',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает охранные услуги: физическая охрана, личная охрана,  защита информации, оборудование и технические системы безопасности и оповещения. Недорого, надежно, качественно!',`keywords` = 'системы безопасности, системы оповещения, оборудование безопасности',`text` = '<h1><span style=\"font-size: large;\"><span style=\"color: rgb(0, 0, 255);\"><span><span style=\"font-family: Tahoma;\"><b>СИСТЕМЫ&nbsp;ОПОВЕЩЕНИЯ</b></span></span></span></span></h1>\r\n<div>&nbsp;</div>\r\n<div>\r\n<table width=\"300\" cellspacing=\"9\" cellpadding=\"0\" border=\"0\" align=\"left\">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p><img width=\"320\" height=\"237\" src=\"/files/storage/images/systemaopovecheniya.jpg\" alt=\"\" /></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Системы речевого оповещения и&nbsp;управления эвакуацией (СОУЭ)&nbsp;предназначены для&nbsp;голосового предупреждения людей при пожаре, стихийных бедствиях и других чрезвычайных и аварийных ситуациях, что&nbsp;приводит к&nbsp;снижению времени их&nbsp;эвакуации.</span></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Большинство систем оповещения строится по модульному принципу, в зависимости от архитектурных особенностей здания и его назначения система оповещения может включать в себя устройства, предназначенные для экстренной трансляции, или дополняться модулями, служащими для повышения качества звука.</span></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Существуют системы музыкального, информационного озвучивания помещений и системы оповещения сотрудников и посетителей о чрезвычайной ситуации. Иногда создаются системы, объединяющие в себе все эти функции. В торговых центрах и офисных зданиях система оповещения может создавать уютную обстановку, транслируя приятную фоновую музыку, или передавать объявления служебного или рекламного характера. В случае поступления с датчиков сигнала тревоги, трансляция общего назначения прерывается, и система оповещения об экстренном случае, информация о котором записана в блок памяти или зачитывается диспетчером.</span></span></span></div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"font-size: medium;\"><span style=\"color: rgb(0, 0, 255);\"><span><strong><span style=\"font-family: Arial;\">В состав систем СОУЭ входят:</span></strong></span></span></span></div>\r\n<div>&nbsp;</div>\r\n<div><strong><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">- звуковые оповещатели (громкоговорители, сирены)</span></span></span></strong></div>\r\n<div><strong><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">- усилители мощности</span></span></span></strong></div>\r\n<div><strong><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">- регуляторы и&nbsp;селекторы</span></span></span></strong></div>\r\n<div><strong><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">- микрофоны и&nbsp;диспетчерские консоли</span></span></span></strong></div>\r\n<div><strong><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">- блоки бесперебойного питания.</span></span></span></strong></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Новейшие системы речевого пожарного оповещения являются цифровыми. Они&nbsp;позволяют обеспечивать надежное многозонное оповещение в&nbsp;экстренных случаях, а&nbsp;также, в&nbsp;остальное время, высококачественную трансляцию фоновой музыки, трансляцию информационных и&nbsp;рекламных сообщений, например, в&nbsp;торговых комплексах, вокзалах, ресторанах и&nbsp;т.п.)</span></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Наша компания рекомендует качественное сертифицированное оборудование СОУЭ от&nbsp;ведущих мировых производителей:&nbsp;BOSCH, Inter M, Autronica, Речор, Орфей, Тромбон, Соната, Inter M, LPA и&nbsp;др. </span></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Выбор того&nbsp;или иного типа оборудования определяется решаемыми задачами и&nbsp;возможностями заказчика.</span></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">См. также <a href=\"http://taggerd.su/page/4/14/\"><span style=\"color: rgb(0, 0, 255);\"><strong>Системы охранной и пожарной сигнализации</strong></span></a></span></span></span></div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: medium;\"><strong><span><span><span style=\"font-family: Arial;\"><font size=\"3\">ОКАЗАНИЕ&nbsp;УСЛУГ&nbsp;БЕЗОПАСНОСТИ - НАША&nbsp;ПРОФЕССИЯ!</font></span></span></span></strong></span></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: medium;\"><strong><span><span><span style=\"font-family: Arial;\"><font size=\"3\">\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-family: Comic Sans MS;\"><span><span style=\"font-size: medium;\"><strong><font size=\"3\">\r\n<table width=\"400\" cellspacing=\"1\" cellpadding=\"9\" border=\"1\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\" style=\"text-align: center;\"><span style=\"color: rgb(255, 255, 255);\"><span style=\"font-size: medium;\"><strong><a href=\"http://taggerd.su/page/0/19/\"><span style=\"color: rgb(255, 255, 255);\">ЗАКАЗАТЬ&nbsp; УСЛУГИ&nbsp; ОН-ЛАЙН</span></a></strong></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n</font></strong></span></span></span></span></div>\r\n</font></span></span></span></strong></span></span></div>\r\n</span></span></span></div>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p>&nbsp;</p>',`date` = '2010-10-16 12:59:16',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '18',`parent` = '0',`position` = '4',`autotext` = '',`page` = 'Договоры',`name` = 'Договоры',`title` = 'ЧОП ТАГГЕРД (Москва), договор на охрану, акт приема под охрану, договор на охранные услуги',`description` = 'Группа охранных предприяий \"ТАГГЕРД\" предлагает ознакомиться со стандартным образцом договора на охрану объекта недвижимости (оказание охранных услуг), а также с Актом приема/передачи объекта под охрану. ',`keywords` = 'договор на охрану, акт приема под охрану, договор на охранные услуги, договор на услуги охраны, договор об оказании охранных услуг, акт передачи под охрану, прием-передача под охрану',`text` = '<h1><span style=\"color: #000080\"><strong><span style=\"font-size: medium\"><span style=\"font-family: Arial\">ОБРАЗЕЦ&nbsp; ДОГОВОРА ОБ ОКАЗАНИИ ОХРАННЫХ УСЛУГ</span></span></strong></span></h1>\r\n<h1><span style=\"color: #0000ff\"><span><strong><span style=\"font-size: medium\"><span style=\"font-family: Arial\">АКТ&nbsp; ПРИЕМА-ПЕРЕДАЧИ ОБЪЕКТА ПОД ОХРАНУ</span></span></strong></span></span></h1>\r\n<p>&nbsp;</p>\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"700\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#ccccff\">&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center\">&nbsp;<span style=\"color: #000080\"><span style=\"font-size: medium\"><strong>ДОГОВОР № __</strong></span></span></div>\r\n<div align=\"center\"><span style=\"font-size: small\"><span><span><span style=\"color: #000080\"><strong><sup>об&nbsp;оказании&nbsp;охранных&nbsp;услуг</sup></strong></span></span></span></span></div>\r\n<div align=\"center\">&nbsp;</div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div style=\"text-align: center\"><span style=\"color: #000080\">г. Москва&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&laquo;___&raquo; __________ 20-- г.</span></div>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: #000080\"><b>__________________________________________________________,</b> именуемое в дальнейшем &laquo;<b>Заказчик&raquo;</b>,&nbsp; в лице Генерального директора _____________________, действующего на основании Устава, с одной стороны, и<b> ООО Частное охранное предприятие </b>именуемое в дальнейшем &laquo;Исполнитель&raquo;, в лице Генерального директора __________________________, действующего на основании Устава и Лицензии на охранную деятельность <b>№ ____ </b>от _____________________ года<b>, </b>действующей до __________________ года, с другой стороны, вместе именуемые в дальнейшем &laquo;Стороны&raquo;, заключили настоящий Договор о нижеследующем:</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>ПРЕДМЕТ ДОГОВОРА</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">1.1.&nbsp;&nbsp;&nbsp;Заказчик поручает, а Исполнитель обязуется оказывать охранные услуги по защите помещений Заказчика от преступных и иных противоправных посягательств и поддержанию общественного порядка на прилегающей к помещениям территории, по адресу: ________________________________________, далее по тексту &ndash; &laquo;Объект&raquo;, в соответствии с согласованным Сторонами планом территории. </span></div>\r\n<div><span style=\"color: #000080\">1.2.&nbsp;&nbsp;&nbsp;Заказчик обязуется принять и оплатить оказанные Исполнителем услуги в порядке и на условиях настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>ПРАВА И ОБЯЗАННОСТИ СТОРОН</b></span></div>\r\n<div><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\"><b>2.1. Исполнитель обязуется:</b></span></div>\r\n<div><span style=\"color: #000080\">2.1.1.Принять Объект под охрану в течение 10 (десяти) дней с момента подписания с момента подписания настоящего Договора по Акту приема-передачи под охрану.</span></div>\r\n<div><span style=\"color: #000080\">2.1.2.Обеспечить сохранность имущества и помещений Заказчика на Объекте, переданных под охрану в порядке установленном настоящим Договором и в соответствии с Инструкцией по организации охраны объекта Заказчика, разработанной Исполнителем и утвержденной Заказчиком, далее по тексту &ndash; &laquo;Инструкция по охране&raquo;, являющейся неотъемлемой частью настоящего Договора<i>.</i></span></div>\r\n<div><span style=\"color: #000080\">2.1.3.Обеспечить охрану от преступных и незаконных посягательств на жизнь и здоровье находящихся на Объектах сотрудников и посетителей Заказчика.</span></div>\r\n<div><span style=\"color: #000080\">2.1.4.Организовать пропускной режим на охраняемом Объекте на основании документов, утвержденных Заказчиком и согласованных с Исполнителем.</span></div>\r\n<div><span style=\"color: #000080\">2.1.5.Осуществлять мониторинг оперативной обстановки и внутреннего порядка на охраняемой территории с использованием технических средств безопасности и охраны, имеющихся на Объекте.</span></div>\r\n<div><span style=\"color: #000080\">2.1.6.Своевременно пресекать нарушения и противоправные действия, направленные на нанесение ущерба имуществу Заказчика и его посетителей, принимать экстренные меры, направленные на защиту и минимизацию ущерба имуществу Заказчика в чрезвычайных ситуациях.</span></div>\r\n<div><span style=\"color: #000080\">2.1.7.Немедленно уведомлять Заказчика обо всех ставших известными Исполнителю случаях угрозы сохранности имущества, находящегося на Объекте.</span></div>\r\n<div><span style=\"color: #000080\">2.1.8.Своевременно информировать Заказчика обо всех имеющихся неисправностях и недостатках работы технических средств безопасности и охраны, имеющихся на Объекте.</span></div>\r\n<div><span style=\"color: #000080\">2.1.9.Не позднее 3-го числа месяца, следующего за отчетным, предоставлять Заказчику составленный в 2 (Двух) экземплярах и подписанный со своей стороны письменный отчет в виде Акта об оказанных услугах.</span></div>\r\n<div><span style=\"color: #000080\">2.1.10.&nbsp;Своевременно выставлять Заказчику счета и счета-фактуры.</span></div>\r\n<div><span style=\"color: #000080\">2.1.11.&nbsp;Неукоснительно соблюдать указания уполномоченных представителей Заказчика на местах, назначенных согласно п.2.2.4 настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">2.1.12.&nbsp;Передавать под охрану и снимать с охраны помещения и имущество в порядке установленном настоящим Договором и Инструкцией по охране Объекта.</span></div>\r\n<div><span style=\"color: #000080\">2.1.13.&nbsp;Незамедлительно реагировать на срабатывание средств охранно-пожарной сигнализации, на проявление на Объекте признаков возгорания, аварий техногенного характера или стихийного бедствия и принимать необходимые меры адекватного реагирования (вызов специальных служб, своевременное сообщение Заказчику и принятие активных мер, своими силами для локализации аварии или происшествия и т.д.).</span></div>\r\n<div><span style=\"color: #000080\">2.1.14.&nbsp;Выполнять иные обязанности, связанные с обеспечением охраны Объекта, вытекающие из существа настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\"><b>2.2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>Исполнитель вправе:</b></span></div>\r\n<div><span style=\"color: #000080\">2.2.1. Вносить предложение об изменении стоимости охранных услуг с учетом индекса инфляции и роста тарифов на охранные услуги, но не чаще, чем 1 (Один) раз в год.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\"><b>2.3. Заказчик обязуется:</b></span></div>\r\n<div><span style=\"color: #000080\">2.3.1 Своевременно оплачивать оказанные Исполнителем услуги в сроки и в порядке, предусмотренном ст.3 настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">2.3.2. Обеспечить приведение Объекта в соответствие с требованиями технической защищенности и оснащенности средствами безопасности и охраны, указанных в<br />\r\nст.4 &nbsp;настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">2.3.3. Утвердить на охраняемом Объекте правила внутреннего трудового распорядка для сотрудников и посетителей Заказчика, а также правила пропускного режима, определяющие порядок прохода и выноса (вывоза) товарно-материальных ценностей.</span></div>\r\n<div><span style=\"color: #000080\">2.3.4. Назначить &laquo;уполномоченных представителей&raquo; на местах, указания которых должны неукоснительно выполняться сотрудниками Службы охраны Исполнителя.</span></div>\r\n<div><span style=\"color: #000080\">2.3.5. Передавать под охрану и снимать с охраны помещения и имущество в порядке установленном настоящим Договором и разработанной Исполнителем Инструкцией по охране.</span></div>\r\n<div><span style=\"color: #000080\">2.3.6. Утвердить на Объекте список лиц, ответственных за передачу помещений и имущества под охрану.</span></div>\r\n<div><span style=\"color: #000080\">2.3.7. Создать Исполнителю надлежащие для выполнения принятых на себя по настоящему Договору обязательств условия, а также требовать от своих сотрудников соблюдения требований безопасности и охраны, действующих на Объекте.</span></div>\r\n<div><span style=\"color: #000080\">2.3.8. Осуществлять своевременную профилактику и ремонт технических средств безопасности и охраны, имеющихся на Объекте.</span></div>\r\n<div><span style=\"color: #000080\">2.3.9. Принимать оказанные Исполнителем в рамках настоящего Договора услуги путем подписания в течение 3 (Трех) рабочих дней со дня получения Акта об оказанных услугах за прошедший месяц. Безосновательный отказ Заказчика подписать Акт об оказанных услугах является основанием для досрочного расторжения настоящего Договора по инициативе Исполнителя в течение 10 (десяти) дней со дня получения безосновательного отказа Заказчика от подписания Акта об оказанных услугах.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\"><b>2.4.&nbsp;Заказчик&nbsp;вправе:</b></span></div>\r\n<div><span style=\"color: #000080\">2.4.1.&nbsp;Проверять качество работы персонала Исполнителя на Объекте.</span></div>\r\n<div><span style=\"color: #000080\">2.4.2. Получать от Исполнителя всю информацию о происшествиях на Объекте, а также о возможных угрозах персоналу и имуществу Заказчика;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>ЦЕНА ДОГОВОРА И ПОРЯДОК РАСЧЕТОВ</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">3.1.&nbsp;&nbsp;Стоимость охранных услуг Исполнителя по охране Объекта указанного в пп. <b>1.1.</b> настоящего Договора за один месяц работы составляет&nbsp;__________________ рублей<b>, </b>без учета <b>НДС </b>(в связи с применением упрощенной системы налогообложения).</span></div>\r\n<div><span style=\"color: #000080\">3.2.&nbsp;Стоимость услуг включает в себя компенсацию затрат Исполнителя по выполнению своих обязательств по данному Договору.</span></div>\r\n<div><span style=\"color: #000080\">3.3.&nbsp;&nbsp;&nbsp;Оплата осуществляется Заказчиком ежемесячно в течение <b>3</b>(Трех) рабочих дней с даты получения выставленного Исполнителем счета на оплату услуг за истекший календарный месяц, но не позднее 5-го числа месяца, следующего за расчетным.</span></div>\r\n<div><span style=\"color: #000080\">3.4.&nbsp;<b>&nbsp;</b>Оплата выполненных охранных услуг осуществляется Заказчиком в безналичном порядке, путем перечисления денежных средств на расчетный счет Исполнителя, на основании выставленного последним счета. Днем оплаты считается день списания денежных средств с корреспондентского счета Заказчика.</span></div>\r\n<div><span style=\"color: #000080\">3.5.&nbsp;Стоимость услуг по договору может быть изменена только по письменному соглашению Сторон Договора. </span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>ТРЕБОВАНИЯ К ЗАЩИЩЕННОСТИ ОБЪЕКТА</b></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\">4.1.&nbsp;&nbsp;На Объекте должны быть определены основные проходы/проезды на охраняемую территорию, через которые будет осуществляться вход/выход граждан и въезд/выезд транспортных средств. В местах основных проходов/проездов должны быть оборудованы Контрольно-пропускные пункты (КПП) или оборудованы посты охраны, имеющие все необходимое для обеспечения пропускного режима и блокирования прохода/проезда. Остальные (запасные) проходы/проезды должны быть надежно закрыты (заблокированы), эти двери/ворота должны быть опечатаны и вскрываться только в случае необходимости под контролем сотрудников Исполнителя.</span></div>\r\n<div><span style=\"color: #000080\">4.2.&nbsp;&nbsp;&nbsp;Все двери, калитки и ворота на Объекте должны быть надежно укреплены и иметь исправные замки и запоры.</span></div>\r\n<div><span style=\"color: #000080\">4.3.&nbsp;&nbsp;&nbsp;&nbsp;Все конструктивные элементы помещений передаваемых под охрану (окна, двери, стены и т.д.), должны находиться в целостном, исправном состоянии, не иметь проломов и пролазов. Двери в эти помещения должны быть надежно укреплены и иметь исправные замки (запоры), а также при необходимости иметь приспособления для опечатывания.</span></div>\r\n<div><span style=\"color: #000080\">4.4.&nbsp;&nbsp;Помещения должны иметь исправные комплекты средств пожаротушения, расположенные в наиболее удобных и доступных местах.</span></div>\r\n<div><span style=\"color: #000080\">4.5.&nbsp;&nbsp;Помещения должны быть оборудованы системами охранной и пожарной сигнализации с выводом тревожного сигнала на Центральный пост охраны.</span></div>\r\n<div><span style=\"color: #000080\">4.6.&nbsp;&nbsp;&nbsp;Для мониторинга оперативной обстановки на Объекте должна быть установлена система видео-наблюдения, охватывающая наиболее важные участки Объекта с выводом информации на Центральный пост охраны и возможностью круглосуточной записи и хранения видеоматериалов.</span></div>\r\n<div><span style=\"color: #000080\">4.7.&nbsp;&nbsp;Территория Объекта с наступлением темноты должна иметь дежурное освещение и быть доступна досмотру сотрудниками Службы охраны.</span></div>\r\n<div><span style=\"color: #000080\">4.8.&nbsp;&nbsp;&nbsp;&nbsp;Места для размещения постов охраны должны быть оборудованы местной и городской телефонной связью, и иметь все необходимое для исполнения принятых на себя Исполнителем обязанностей.</span></div>\r\n<div><span style=\"color: #000080\">4.9.&nbsp;&nbsp;&nbsp;&nbsp;Для нужд Исполнителя должны быть выделены места бытового назначения, удовлетворяющие санитарно-гигиеническим нормам согласно действующему законодательству Российской Федерации.</span></div>\r\n<div><span style=\"color: #000080\">4.10.&nbsp;&nbsp;&nbsp;Все технические средства безопасности и охраны, установленные на Объекте, должны находиться в исправном состоянии и быть дополнительно оборудованы устройствами бесперебойного (аварийного) питания.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>ПРОЦЕДУРА ПЕРЕДАЧИ ОБЪЕКТА ПОД ОХРАНУ</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">5.1.&nbsp;&nbsp;Передача Заказчиком Объекта Исполнителю под охрану производится в срок, указанный в пп. 2.1. настоящего Договора, путем подписания уполномоченными представителями обеих Сторон Акта приема-передачи объекта под охрану. В Акте приема-передачи объекта под охрану помимо даты начала оказания услуг указывается оценка состояния, защищенности и готовности Объекта к передаче под охрану, а также недостатки и неисправности, выявленные при первичном обследовании Объекта на предмет его защищенности и соответствия требованиям безопасности и охраны, предусмотренным ст.4 настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">5.2.&nbsp;&nbsp;Все недостатки и неисправности, влияющие на защищенность Объекта, указанные в Акте приема-передачи объекта под охрану, должны быть исправлены Заказчиком в кратчайшее возможные сроки.</span></div>\r\n<div><span style=\"color: #000080\">5.3.&nbsp;&nbsp;&nbsp;Ответственность Исполнителя за конкретное помещение и/или имущество возникает после приведения Объекта в соответствие с техническими требованиями безопасности и охраны, и устранения имеющихся недостатков согласно п.5.2. настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>6.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>ПОРЯДОК ОРГАНИЗАЦИИ ОХРАНЫ ОБЪЕКТА</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">6.1.&nbsp;&nbsp;Исполнитель оказывает Заказчику услуги по охране Объекта на основании настоящего Договора, Инструкции по организации охраны объекта, разработанной Исполнителем для Объекта Заказчика, и выданного Заказчиком Задания на охрану Объекта.</span></div>\r\n<div><span style=\"color: #000080\">6.2.&nbsp;&nbsp; Услуги по охране Объекта оказываются в дни и часы, предварительно согласованные обеими Сторонами настоящего Договора и утвержденные в ежемесячном Графике несения службы.</span></div>\r\n<div><span style=\"color: #000080\">6.3.&nbsp; Количество сотрудников Исполнителями, привлекаемых ежедневно для охраны Объектов, определяется количеством постов охраны, утвержденных Заказчиком: _________________________________________________.</span></div>\r\n<div><span style=\"color: #000080\">6.4.&nbsp;&nbsp;Сотрудники Исполнителя выполняют служебные функции в соответствии с Инструкцией по организации охраны объекта и в строгом соответствии с требованиями действующего законодательства Российской Федерации.</span></div>\r\n<div><span style=\"color: #000080\">6.5.&nbsp;Сотрудники Исполнителя обязаны выполнять указания уполномоченных представителей Заказчика, касающиеся порядка исполнения обязанностей по охране Объекта, входящие в их компетенцию и не противоречащие настоящему Договору и&nbsp;Инструкции по организации охраны объекта, а также письменные распоряжения (материальные пропуска) на вынос/вывоз имущества Заказчика подписанные его материально-ответственными лицами.</span></div>\r\n<div><span style=\"color: #000080\">6.6.&nbsp;&nbsp;Исполнитель должен своевременно извещать Заказчика обо всех недостатках и неисправностях в работе технических средств безопасности и охраны, выявленных в ходе исполнения поручения или в результате проведения профилактических обследований Объекта на защищенность. В случае обнаружения недостатков и неисправностей Исполнителем составляется соответствующий Акт, который служит основанием (заявкой) для принятия Заказчиком незамедлительных мер для их устранения.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>7.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>Ответственность сторон</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">7.1.&nbsp;&nbsp;&nbsp;&nbsp;В случае неисполнения или ненадлежащего исполнения принятых на себя по настоящему Договору обязательств виновная Сторона настоящего Договора возмещает другой Стороне понесенные последней убытки в порядке и в размере, предусмотренных действующим законодательством РФ.</span></div>\r\n<div><span style=\"color: #000080\">7.2.&nbsp;&nbsp; Исполнитель несет ответственность в размере полной стоимости понесенных убытков за ущерб, причиненный утратой, повреждением или порчей имущества Заказчика, если этот ущерб нанесен:</span></div>\r\n<div><span style=\"color: #000080\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; кражами имущества, совершенными из охраняемых помещений посредством взлома запоров, замков, окон, стен, дверей или иными способами, а также вследствие нарушения сотрудниками Службы охраны Исполнителя порядка выноса/вывоза с территории охраняемого Объекта<br />\r\nматериальных ценностей;</span></div>\r\n<div><span style=\"color: #000080\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; посторонними лицами, проникшими на Объект по вине сотрудников Службы охраны Исполнителя в результате нарушения установленного на Объекте пропускного режима;</span></div>\r\n<div><span style=\"color: #000080\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; утратой, повреждением или уничтожением имущества Заказчика, совершенного в результате ненадлежащего выполнения сотрудниками Исполнителя принятых на себя по настоящему Договору обязательств и нарушения ими Инструкции по охране.</span></div>\r\n<div><span style=\"color: #000080\">Под убытками в настоящем Договоре понимается стоимость утраченных или поврежденных принадлежащих Заказчику и/или посетителям Заказчика материальных ценностей по их остаточной стоимости.</span></div>\r\n<div><span style=\"color: #000080\">Факты и причины утраты, порчи или повреждения имущества Заказчика устанавливаются двусторонним Актом, подписываемым уполномоченными представителями обеих Сторон либо органами дознания, следствия или судом.</span></div>\r\n<div><span style=\"color: #000080\">7.3.&nbsp;&nbsp;&nbsp;&nbsp;Исполнитель не несет ответственность:</span></div>\r\n<div><span style=\"color: #000080\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;за находящееся на Объекте имущество и помещения Заказчика, которые не передавались под охрану или передавались с нарушением установленного порядка передачи под охрану;</span></div>\r\n<div><span style=\"color: #000080\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;за личное имущество сотрудников и посетителей Заказчика, оставленное ими без присмотра; </span></div>\r\n<div><span style=\"color: #000080\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;за ущерб, причиненный имуществу Заказчика, находящемуся в закрытом помещении с подключенной охранной сигнализацией, если не нарушена целостность переданного под охрану помещения, а приборы не зафиксировали несанкционированное вскрытие;</span></div>\r\n<div><span style=\"color: #000080\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;за ущерб, причиненный Заказчику, в случае организованного группового или вооруженного нападения на Объект, при правильном выполнении Исполнителем действий в соответствии с действующим законодательством РФ и Инструкцией по охране.</span></div>\r\n<div><span style=\"color: #000080\">7.4.&nbsp;&nbsp;&nbsp;Стороны несут ответственность за действия своих сотрудников и посетителей Заказчика на охраняемом Объекте, а также за соблюдение ими установленных правил и инструкций, действующих на Объекте.</span></div>\r\n<div><span style=\"color: #000080\">7.5.&nbsp;&nbsp;Возмещение убытков и уплата штрафов не освобождает виновную Сторону от выполнения своих обязательств по настоящему Договору.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>8. ФОРС-МАЖОР</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">8.1.&nbsp;&nbsp;Стороны освобождаются от ответственности за неисполнение или ненадлежащее исполнение обязательств, если докажут, что надлежащее исполнение оказалось невозможным вследствие непреодолимой силы, то есть чрезвычайных и непредотвратимых при данных условиях обстоятельств, если эти обстоятельства непосредственно стали причиной невыполнения обязательств (форс-мажор).</span></div>\r\n<div><span style=\"color: #000080\">8.2.&nbsp;&nbsp;К обстоятельствам непреодолимой силы по настоящему Договору относятся: наводнение, землетрясение, шторм, оседание почвы, и иные явления природы, а также пожар, взрыв, война или военные действия, забастовка в отрасли или регионе, принятие органом государственной власти или местного самоуправления решения, повлекшего невозможность исполнения настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">8.3.&nbsp;&nbsp;При наступлении указанных в п.6.2. настоящего Договора обстоятельств Сторона по настоящему Договору, для которой создалась невозможность исполнения ее обязательств, должна немедленно известить другую Сторону, предоставив соответствующие доказательства.</span></div>\r\n<div><span style=\"color: #000080\">8.4.&nbsp;&nbsp;При отсутствии своевременного извещения, предусмотренного п.6.3. настоящего Договора, Сторона обязана возместить другой Стороне убытки, причиненные не извещением или несвоевременным извещением.</span></div>\r\n<div><span style=\"color: #000080\">8.5.&nbsp;&nbsp;Если период действия обстоятельств непреодолимой силы превысит 3 (Три) месяца, любая из Сторон будет иметь право досрочно расторгнуть настоящий Договор.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>9.&nbsp; СРОК ДЕЙСТВИЯ ДОГОВОРА</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">9.1.&nbsp;&nbsp;Настоящий Договор заключается сроком на 1 (Один) год &nbsp;&ndash; &nbsp;<b>с</b> <b>_______________ &nbsp;20___ года до ________________ 20___ года</b>, и вступает в силу с момента подписания его уполномоченными представителями обеих Сторон.</span></div>\r\n<div><span style=\"color: #000080\">9.2.&nbsp;Настоящий Договор пролонгируется на новый срок, равный 1 (Одному) году, в случае, если ни от одного из Сторон не поступило предложение о намерении расторгнуть настоящий Договор не менее чем за 30 (Тридцать) дней до окончания срока его действия.</span></div>\r\n<div><span style=\"color: #000080\">9.3.&nbsp;&nbsp;Настоящий Договор может быть расторгнут досрочно по соглашению обеих Сторон, а также по инициативе одной из Сторон с обязательным письменным уведомлением о досрочном расторжении настоящего Договора не менее чем за 30 (Тридцать) дней до даты предполагаемого расторжения по основаниям, предусмотренным настоящим Договором либо действующим законодательством Российской Федерации. При этом к моменту предполагаемого расторжения Стороны обязуются произвести взаиморасчеты.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>10.&nbsp;&nbsp;&nbsp;&nbsp;</b><b>АРБИТРАЖ</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">10.1.&nbsp;Все споры и /или разногласия, возникающие между Сторонами в связи с заключением, исполнением или прекращением настоящего Договора, разрешаются путем переговоров между ними.</span></div>\r\n<div><span style=\"color: #000080\">10.2.&nbsp;В случае если возникшие между Сторонами споры и/или разногласия не могут быть урегулированы путем переговоров, такие споры и/или разногласия подлежат рассмотрению в Арбитражном суде г, Москвы в порядке, предусмотренном действующим законодательством Российской Федерации.</span></div>\r\n<div>&nbsp;</div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>11.&nbsp;&nbsp;&nbsp;</b><b>ПРОЧИЕ УСЛОВИЯ</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">11.1.&nbsp;Ни одна из Сторон не вправе передавать свои права и обязанности по настоящему Договору третьим лицам без письменного согласия другой Стороны.</span></div>\r\n<div><span style=\"color: #000080\">11.2.&nbsp;Стороны настоящего Договора обязуются не разглашать и не использовать в личных целях сведения, составляющие коммерческую тайну, которые стали им известны в связи с заключением, исполнением или прекращением настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">11.3.&nbsp;Все изменения, дополнения, приложения и акты к настоящему Договору действительны только в том случае, если они составлены в письменной форме, подписаны уполномоченными представителями обеих Сторон, скреплены печатями, и являются неотъемлемыми частями настоящего Договора.</span></div>\r\n<div><span style=\"color: #000080\">11.4.&nbsp;Во всем остальном, что не предусмотрено настоящим Договором, Стороны руководствуются положениями действующего законодательства Российской Федерации.</span></div>\r\n<div><span style=\"color: #000080\">11.5.&nbsp;Настоящий Договор составлен в 2 (Двух) экземплярах, имеющих одинаковую юридическую силу, по одному &ndash; для каждой Стороны.</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>12. АДРЕСА, БАНКОВСКИЕ РЕКВИЗИТЫ И ПОДПИСИ СТОРОН</b></span></div>\r\n<div><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\"><b><u>&laquo;Заказчик&raquo;:</u></b></span></div>\r\n<div><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\"><b><i>Генеральный директор&nbsp;______________________ / ____________________ /</i></b></span></div>\r\n<div><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <font size=\"2\">мп&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </font></span></div>\r\n<div><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\"><b><u>&laquo;Исполнитель&raquo;</u></b></span></div>\r\n<div><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\"><b><i>Генеральный директор&nbsp;&nbsp;&nbsp; </i></b>_________________________/ _________________/</span></div>\r\n<div><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div><span style=\"color: #000080\"><b><i>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </i></b><font size=\"2\">мп</font></span></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: left\" align=\"center\"><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"700\" align=\"center\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#ccccff\">&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: left\">&nbsp;</div>\r\n</b></span>&nbsp;<span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><span style=\"font-size: small\"><span><b>А&nbsp;К&nbsp;Т</b></span></span></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><span style=\"font-size: small\"><b>приема-передачи объекта под охрану</b>.</span></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b><i>Заказчик: __________________________</i></b></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b><i>Исполнитель: ______________________________</i></b></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><b>&nbsp;</b></span></div>\r\n<div align=\"center\"><span style=\"color: #000080\"><i>Адрес объекта: ___________________________________</i></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: center\"><span style=\"color: #000080\"><i>г. Москва&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &laquo;__&raquo; ______&nbsp;20__ г.</i></span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мы, нижеподписавшиеся, представитель <b>Заказчика</b> в лице Генерального директора _______________________с одной стороны и представитель <b>Исполнителя </b>в лице Генерального директора ________________<b><i>_,</i></b> с другой стороны,&nbsp;составили настоящий <b>АКТ </b>о том,&nbsp;что в соответствии с Договором&nbsp;№ ___________ от&nbsp;_____________ 20__ года <b>Заказчик</b> передал, а <b>Исполнитель</b> принял под охрану объект по адресу: ____________________________________, согласно прилагающемуся <b>Плану</b> объекта.</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;&nbsp; Стороны подтверждают, что объект в соответствии с условиями Договора был совместно обследован на предмет защищенности и оснащенности средствами безопасности и охраны. При этом выявлено следующее:</span></div>\r\n<div><span style=\"color: #000080\">1.&nbsp;&nbsp; Целостность и защищенность объекта от внешних угроз по периметру (примыкающих зданий и сооружений, забора, ворот, калиток, наличие сигнализации, колючей проволоки и иных средств технической защиты): ____________________</span></div>\r\n<div><span style=\"color: #000080\">_______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">_______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">2.&nbsp;Оценка состояния зданий и сооружений, расположенных на внутренней территории объекта:&nbsp;целостность и исправность конструктивных элементов (стен, крыши, подвалов, окон, дверей), отсутствие проломов и пролазов, наличие замков, запоров, приспособлений для опечатывания помещений, сигнализации и т.д.): _______________________________________</span></div>\r\n<div><span style=\"color: #000080\">_______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">3.&nbsp;Наличие и исправность технических средств безопасности и охраны: систем охранной&nbsp;и пожарной сигнализации, видео-наблюдения, контроля и ограничения доступа, связи: ______________________________________________</span></div>\r\n<div><span style=\"color: #000080\">_______________________________________________________________________________ </span></div>\r\n<div><span style=\"color: #000080\">_______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">4. Состояние пожарной безопасности на объекте: наличие поэтажного плана эвакуации, наличие и исправность средств оповещения и пожаротушения, состояние аварийных выходов, и т.д.: ______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">5.&nbsp;Наличие и исправность дежурного освещения на внутренней и внешней территории объекта: ______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">6.&nbsp;Перечень оборудования и имущества Заказчика переданного ЧОП для исполнения служебных функций и оснащения постов охраны: ______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">______________________________________________________________________________</span></div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: #000080\">7.&nbsp;Наличие и соответствие санитарно-гигиеническим нормам мест (помещений) бытового назначения переданных Заказчиком для нужд сотрудников охраны: _____________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">8.&nbsp;Не защищенные участки объекта уязвимые для несанкционированного проникновения и выноса материальных ценностей: _______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">_______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">9. Основные замечания, недостатки и неисправности выявленные на объекте: ______________</span></div>\r\n<div><span style=\"color: #000080\">_______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">10.&nbsp;Выводы и рекомендации: _______________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">_______________________________________________________________________________</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: center\"><span style=\"color: #000080\">&nbsp;&nbsp;&nbsp; <b>От &laquo;Заказчика&raquo;:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;От &laquo;Исполнителя&raquo;:&nbsp;</b></span></div>\r\n<div style=\"text-align: center\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: center\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: center\"><span style=\"color: #000080\">__________________________&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; _________________________</span></div>\r\n<div style=\"text-align: center\"><span style=\"color: #000080\">&nbsp;мп&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; мп</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<p><span style=\"color: #000080\">&nbsp;</span><span style=\"color: #000080\"><span><a href=\"http://taggerd.su/\"><span style=\"color: #000080\">Охрана объектов (Москва и область)</span></a></span></span><span style=\"color: #000080\"><span>&nbsp;</span></span><span style=\"color: #000080\">осуществляется с использованием вооружения, спецсредств и связи.</span></p>',`date` = '2010-11-08 12:02:48',`display` = 'Да';
insert into `taggerd__pages` set `id` = '19',`parent` = '0',`position` = '15',`autotext` = '',`page` = 'Заявка на услуги',`name` = 'Заявка на услуги',`title` = 'ЧОП ТАГГЕРД - Заявка на услуги безопасности и охраны',`description` = 'Заполните он-лайн заявку на услуги безопасности и охраны. Группа охранных предприятий \"ТАГГЕРД\" (Москва) выполнит Вашу заявку оперативно, качественно и не дорого!',`keywords` = 'он-лайн заявка на охрану, заявка на услуги охраны, заявка на услуги безопасности',`text` = '<h1><span style=\"font-size: x-large\"><span style=\"color: #0000ff\"><strong>ЗАЯВКА</strong></span></span></h1>\r\n<h4><span style=\"font-size: small\"><span style=\"color: #000080\"><span><strong>на услуги безопасности и охраны</strong></span></span></span></h4>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>',`date` = '2010-11-08 18:52:46',`display` = 'Да';
insert into `taggerd__pages` set `id` = '20',`parent` = '27',`position` = '5',`autotext` = '',`page` = 'Охрана жилого дома',`name` = 'Охрана жилого дома',`title` = 'ЧОП ТАГГЕРД - профессиональные услуги по охране жилого дома, ТСЖ, гостиницы, общежития, отеля',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает профессиональные услуги по охране жилого дома, квартала, ТСЖ, гостиницы, общежития, отеля. Охрана ЧОП - профессионально, надежно, качественно!',`keywords` = 'охрана дома, охрана ТСЖ, охрана гостиницы, охрана общежития, охрана квартала, охрана отеля, охрана жилья',`text` = '<div>\r\n<div>&nbsp;</div>\r\n<h1><span style=\"font-size: xx-small;\"><span><strong><span style=\"color: rgb(0, 0, 128);\">ОХРАНА ЭЛИТНОГО ЖИЛОГО ДОМА / КВАРТАЛА / ТСЖ / ОБЩЕЖИТИЯ / ГОСТИНИЦЫ / ОТЕЛЯ<br />\r\n</span></strong></span></span></h1>\r\n<h2><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: large;\"><strong>ОХРАНА ЖИЛЫХ ПОМЕЩЕНИЙ<br />\r\n</strong></span></span></h2>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><img width=\"300\" hspace=\"9\" height=\"201\" border=\"2\" align=\"left\" alt=\"\" src=\"/files/storage/images/ohranatsg.jpg\" />При нынешнем уровне криминала в стране, проблема охраны собственности, жилых помещений, личного имущества вышла на новый уровень и стала особо актуальной. Громкая музыка, употребление алкоголя и наркотических средств, драки, антиобщественное поведение подгулявшей молодежи, нарушение правил содержания собак и норм общежития стали обычным явлением для многих московских дворов, а уж тем более общежитий и гостиниц. Кроме того, мы все чаще сталкиваемся с проблемами безопасности, разбойными нападениями, сексуальными домогательствами, актами вандализма, кражами имущества, происходящих на придомовой территории.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Проекты &laquo;Безопасный город&raquo; и &laquo;Безопасный двор&raquo;,</span></strong><span style=\"color: rgb(0, 0, 128);\"> масштабно пропагандируемые в Москве и Московской области, предполагают внедрение современных технических систем и мер обеспечения безопасности в жилых домах и во дворах, с привлечением к этой проблеме всех заинтересованных лиц &ndash; владельцев жилых помещений и коммерческой недвижимости. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">В результате этого уже многие жилые дома, и не только элитные, общежития и гостиницы обзавелись консьержами (вахтерами), в обязанности которых входят отдельные охранные функции. Но, реальную, надежную, качественную службу по обеспечению безопасного проживания, поддержанию общественного порядка, охране имущества собственников может организовать только профессиональная, специально обученная охрана, которой является Частное охранное предприятие (ЧОП) &quot;ТАГГЕРД&quot;.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Стратегия и тактика охраны жилых комплексов, гостиниц, ТСЖ, </span></strong><span style=\"color: rgb(0, 0, 128);\">в большей степени должна быть настроена на профилактику и предупреждение правонарушений на охраняемой территории, нежели пресечение уже возникших инцидентов. В связи с этим, большое значение придается внедрению технических систем безопасности, в число которых входит: видеонаблюдение, средства ограничения и контроля доступа, охранная и пожарная сигнализация.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Система видеонаблюдения</span></strong><span style=\"color: rgb(0, 0, 128);\">, кроме видеокамер, оборудованных датчиками движения, может включать в себя оборудование для видеозаписи и хранения материалов, позволяющее сохранить в памяти контрольного центра управления все события, произошедшие на охраняемой территории в течение определенного периода времени (дня, недели, месяца). Это позволяет проводить аналитику событий и выявлять вероятность подготовки преступлений на ранней стадии. Некоторые системы видеонаблюдения даже позволяют транслировать реальную картинку с видеокамер, через Интернет или систему кабельного телевидения, непосредственно каждому владельцу&nbsp; помещений в данном доме, каждому владельцу телевизора или компьютера, каждому абоненту внутридомовой сети.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Другим традиционным средством защиты</span></strong><span style=\"color: rgb(0, 0, 128);\"> жилого комплекса обычно является установка или укрепление существующего ограждения вокруг дворовой территории, установка ворот, калиток и шлагбаумов. Но, несомненно, самым важным звеном в системе безопасности объекта является физическая охрана - охранники (или, для жилых домов, правильнее говорить консьержи), несущие службу на стационарных постах (на КПП или в подъездах жилых домов), а также осуществляющие патрулирование территории и регулярный осмотр &nbsp;подвальных и чердачных помещений, технических и щитовых блоков, коммуникаций.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Опыт нашей работы доказывает</span></strong><span style=\"color: rgb(0, 0, 128);\"> необходимость применения комплексных систем охраны жилых помещений, домов, общежитий, гостиниц, эффективно сочетающих современные технические средства безопасности, физической охраны и организационных мер (правил, инструкций, распоряжений, ограничивающих и запрещающих отдельные действия на охраняемой территории).</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: small;\"><strong>Для обеспечения безопасности на территории жилых домов могут использоваться три рубежа охраны, приведенных ниже:</strong></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">1-ый рубеж &nbsp;&ndash;&nbsp; обеспечение безопасности придомовой территории</span></strong><span style=\"color: rgb(0, 0, 128);\">, может включать:</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- организация стационарных, либо подвижных постов охраны, на придомовой территории, в местах прохода и проезда на внутреннюю территорию двора и к местам стоянки автомобилей; </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- обеспечение пропускного режима, устанавливающего порядок прохода (входа/выхода) граждан, проезда (въезда/выезда) автотранспорта, ввоза/вывоза имущества и материальных ценностей, через КПП;&nbsp;оборудование придомовой территории (как внутренней так и внешней) системами видеонаблюдения;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- охрана помещений и имущества, переданных под охрану в установленном порядке;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- патрулирование придомовой территории;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- использование систем видеонблюдения, ограничения и контроля доступа, периметровой сигнализации.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">2-ой рубеж &nbsp;&ndash;&nbsp; обеспечение безопасности дома</span></strong><span style=\"color: rgb(0, 0, 128);\"> (подъезда), может включать:</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- проведение антитеррористических мероприятий (в том числе регулярное обследование чердаков, подвалов, чердаков и др.);</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- обеспечение пропускного режима прохода в подъезды дома (комплекса домов) и территорию подземного паркинга;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- контроль соблюдения внутренних правил проживания и пожарной безопасности;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- использование систем охранной и пожарной сигнализации, видеонблюдения, ограничения и контроля доступа;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- поддержание общественного порядка в местах общего пользования;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- рганизация взаимодействия с аварийными службами и коммунальными службами;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- организация взаимодействия с органами внутренних дел.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">3-ий рубеж &nbsp;&ndash;&nbsp; адресная защита</span></strong><span style=\"color: rgb(0, 0, 128);\">, включает:</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- установка систем охранной и пожарной сигнализации, видеонаблюдения, контроля доступа, &laquo;тревожной кнопки&raquo; непосредственно в помещении, с передачей сигнала на централизованный пульт охраны; </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- принятие экстренных мер защиты имущества конкретного собственника в случае возникновения чрезвычайной или аварийной ситуации.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">При принятии жилого дома (ТСЖ), гостиницы, общежития под охрану</span></strong><span style=\"color: rgb(0, 0, 128);\"> наши специалисты проведут комплексное обследование объекта на предмет защищенности, укрепленности и оснащенности, чтобы в дальнейшем определить наиболее эффективные и экономически выгодные способы охраны. также анализируется уровень потенциальных угроз имуществу владельцев, жизни и здоровью граждан. Даются рекомендации по адекватным средствам обеспечения безопасности и по выбору тактики обеспечения охраны объекта.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Богатая практика работы в области обеспечения безопасности жилых комплексов, дисциплина и ответственность сотрудников ЧОП &laquo;ТАГГЕРД&raquo; способны обеспечить надежную охрану любого объекта на высоком профессиональном уровне.</span></strong></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\">Ознакомьтесь дополнительно с новой услугой&nbsp; </span><a href=\"http://taggerd.su/page/5/38/\"><span style=\"color: rgb(0, 0, 255);\"><strong>КОНСЬЕРЖ -СЛУЖБА</strong></span></a></span></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: medium;\"><strong><span><span>ОКАЗАНИЕ УСЛУГ БЕЗОПАСНОСТИ - НАША ПРОФЕССИЯ!</span></span></strong></span></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n</div>\r\n<div style=\"text-align: center;\">\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-family: Comic Sans MS;\"><span><span style=\"font-size: medium;\"><strong><font size=\"3\">\r\n<table width=\"400\" cellspacing=\"1\" cellpadding=\"9\" border=\"1\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\" style=\"text-align: center;\"><span style=\"color: rgb(255, 255, 255);\"><span style=\"font-size: medium;\"><strong><a href=\"http://taggerd.su/page/0/19/\"><span style=\"color: rgb(255, 255, 255);\">ЗАКАЗАТЬ&nbsp; УСЛУГУ&nbsp; ОН-ЛАЙН</span></a></strong></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n</font></strong></span></span></span></span></div>\r\n</div>',`date` = '2010-12-22 16:26:11',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '21',`parent` = '5',`position` = '1',`autotext` = '',`page` = 'Охрана с собаками',`name` = 'Охрана с собаками',`title` = 'ЧОП \"ТАГГЕРД\" (Москва) - охрана объектов с использованием служебных собак, охрана со сторожевыми собаками',`description` = 'ЧОП \"ТАГГЕРД\" предлагает недорого охрану объектов с использованием служебных сторожевых собак, в Москве и области. Собака - идеальный бдительный охранник, который не пьет, не курит, не ворует! Опытные кинологи, инструкторы-собаковеды.',`keywords` = 'охрана с собаками, охрана сторожевыми служебным собаками, использование собак при охране, патрулирование с собаками, чоп с собаками, кинолог с собакой',`text` = '<div>&nbsp;</div>\r\n<h1><span style=\"font-size: xx-small;\"><span><span><span style=\"color: rgb(0, 0, 128);\"><span><span><strong><span>ОХРАНА ОБЪЕКТОВ С СОБАКАМИ / СТОРОЖЕВЫЕ СОБАКИ /&nbsp; СЛУЖЕБНЫЕ СОБАКИ / КИНОЛОГ С СОБАКОЙ</span></strong></span></span></span></span></span></span></h1>\r\n<h2><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: large;\"><strong>ОХРАНА С ИСПОЛЬЗОВАНИЕМ СОБАК</strong></span></span></h2>\r\n<div style=\"text-align: justify;\">\r\n<p><img width=\"250\" hspace=\"10\" height=\"250\" align=\"left\" alt=\"\" src=\"/files/storage/images/ohranasobakami.jpg\" /></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Охрана объектов с использованием служебных собак издавна относится к разряду наиболее надежных и эффективных методов охраны. Собаку не может заменить ни один профессиональный охранник, ни одно техническое средство, будто сигнализация или видеонаблюдение. Не зря говорят, что собака - это идеальный охранник, который всегда на чеку, не спит, не пьет, не курит и не ворует! Собаку сложно подкупить и обмануть, она вынослива и бескомпромиссна, не раздумывает и не сомневается в чрезвычайных ситуациях, не щадя собственной жизни делает все от нее возможное для защиты охраняемой территории. А когда служебных собак используется сразу несколько, то справиться с ними сложно даже подготовленным и вооруженным преступникам.</span></span></span></p>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Не зря поэтому собак используют при охране особо важных и сложных гражданских и военных объектов: складов, терминалов, вокзалов, заводов, дачных и коттеджных поселков, полигонов, гаражей и автостоянок и т.д. В оживленных городах и жилых массивах используются специально обученные бесшумные собаки, подающие голос только по команде инструктора-кинолога.</span></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Кстати, один опытный кинолог может использовать на охране объекта одновременно до десяти сторожевых собак!</span></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Выставление на охрану объекта сторожевых собак моментально меняет внутреннюю атмосферу безопасности на территории, ибо многие люди побаиваются собак. Прекращаются кражи, нарушения дисциплины и внутриобъектового режима, работники начинают более аккуратно и уважительно относиться к охране и руководству предприятия.</span></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;<img width=\"240\" hspace=\"10\" height=\"293\" align=\"right\" alt=\"\" src=\"/files/storage/images/kavkazskayaov.jpg\" /></span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Охрана объектов собаками может осуществляться несколькими способами, в зависимости от особенностей объекта, режима и методов охраны:</span></span></span></strong></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- Цепной стационарный пост,</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- Блок-пост,</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- Свободный караул (без привязи),</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- Патрулирование с кинологом,</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- Сигнальное оповещение.</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Для охранной службы с учетом различных целей и задач мы используем следующие породы собак:</span></span></span></strong></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- кавказская овчарка,</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- немецкая и восточноевропейская овчарка,</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- ротвейлер,</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- московская сторожевая,</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- среднеазиатская и южнорусская овчарка,</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">- ризеншнауцер и др.</span></span></span></div>\r\n<div><span style=\"font-family: Arial;\">&nbsp;</span></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Мы имеем тесные контакты со многими кинологическими питомниками и можем предоставить достаточное количество сторожевых собак.</span></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Все собаки здоровы, обучены специальным навыкам и привиты!</span></span></span></strong></div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<table width=\"700\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\"><img width=\"120\" height=\"90\" alt=\"\" src=\"/files/storage/images/sobaka-ohrannik.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\"><img width=\"110\" height=\"90\" alt=\"\" src=\"/files/storage/images/sob-mosstorojevaya.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\"><img width=\"100\" height=\"90\" alt=\"\" src=\"/files/storage/images/vevropeyskayaovharka.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\"><img width=\"118\" height=\"90\" alt=\"\" src=\"/files/storage/images/Rottweiler.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"105\" height=\"90\" alt=\"\" src=\"/files/storage/images/sob-kavkaec.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\"><img width=\"120\" height=\"90\" alt=\"\" src=\"/files/storage/images/sob-ohranyaet.jpg\" />&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: larger;\">Рекомендуем также ознакомиться со статьей <a href=\"http://taggerd.su/page/0/20/\"><span style=\"color: rgb(0, 0, 255);\"><strong>Консьерж -служба</strong></span></a> рассказывающей об охране жилых домов, общежитий, гостиниц и др.</span></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: medium;\"><strong>ОКАЗАНИЕ ОХРАННЫХ УСЛУГ - НАША ПРОФЕССИЯ!</strong></span></span></div>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p>&nbsp;</p>',`date` = '2010-12-22 14:32:27',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '22',`parent` = '5',`position` = '5',`autotext` = '',`page` = 'Рейдерский захват',`name` = 'Рейдерский захват',`title` = 'Рейдерский захват и поглощение бизнеса - меры защиты',`description` = 'ЧОП ТАГГЕРД рекомендует! Методы противодействия и защиты с рейдерскими захватами и недружественным поглощением бизнеса. ',`keywords` = 'рейдерский захват, защита бизнеса',`text` = '<div>&nbsp;</div>\r\n<h1><span style=\"color: rgb(204, 0, 0);\"><span><span style=\"font-size: large;\"><b>РЕЙДЕРСКИЙ ЗАХВАТ</b></span></span></span></h1>\r\n<div>\r\n<div>&nbsp;<img width=\"300\" hspace=\"10\" height=\"206\" align=\"left\" src=\"/files/storage/images/reyderskiyzahvat.jpg\" alt=\"\" /></div>\r\n<div><span style=\"color: rgb(0, 0, 128);\">\r\n<div style=\"text-align: justify;\"><em>В России, и в особенности в Москве постоянно происходит криминальный передел собственности малого и среднего бизнеса. Рейдерские захваты стали реальной опасностью для лиц ведущих успешный бизнес, имеющих недвижимость или ценное имущество. Выбор процесса и методов нападения криминала и недобросовестных конкурентов на собственников бизнеса различен, и всегда зависит от возможностей противостояния бизнесмена, его связей, наличия защиты и охраны. </em></div>\r\n<div style=\"text-align: justify;\"><em>&nbsp;</em></div>\r\n<div style=\"text-align: justify;\"><em>Поэтому начинается рейдерский захват всегда со сбора информации о компании, недвижимости, владельце имущества. Как правило, это информация о бухгалтерской отчетности, о реестре акционеров, об учредителях, учредительных документах, оценочной стоимости активов и другие данные. Часть этих сведений можно получить в налоговых органах, учреждениях юстиции по регистрации прав на недвижимость и других официальных источниках, часть же добывается с использованием методов промышленного шпионажа и т.д. По результатам полученной информации принимается решение о целесообразности захвата и методах его осуществления. </em></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong>Рейдеры всегда используют два метода: &laquo;силовой захват&raquo; и &laquo;правовой спектакль&raquo;. </strong>Но, так или иначе, все действия рейдеров должны в конечном итоге выйти наружу и оказаться в правовом поле, чтобы окончательно закрепить и узаконить успех. Если собственник своевременно распознал начало правового спектакля, то основой его тактики становится противодействие и ожидание силового захвата.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong>&laquo;Правовой спектакль&raquo; преследует две цели: </strong></div>\r\n<div style=\"text-align: justify;\">- Создание правовой и информационной легитимности приемлемого качества.</div>\r\n<div style=\"text-align: justify;\">- Подбор &laquo;оснований&raquo; для захода на предприятия силовым способом методом перехвата управления.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">Скрытность&nbsp;процесса нападения &mdash; залог успеха рейдеров, так как сокрытие намерений и действий приводит к невозможности ответного реагирования. Самым удачным нападением считается такое, при котором жертве потребуется максимальное время для &laquo;идентификации&raquo; правового спектакля и реакции на происходящие события.<br />\r\n<br />\r\nКомпаниям, ведущим прозрачный честный бизнес, приходится в этом плане труднее всего. Чем прозрачней компания, тем более легкой добычей она является, ибо такие компании не хотят и не могут предпринимать альтернативных (теневых) методов защиты, потому что они в этом случае выйдут за правовое поле и станут криминальными. Чиновники и правоохранительные органы, как правило, не спешат помогать в решении возникших проблем, ибо исполняют свои обязанности только при наличии дополнительного стимула, которым честные бизнесмены не располагают или не желают платить взятки, ведя бизнес прозрачно.</div>\r\n<div style=\"text-align: justify;\"><br />\r\nДействия рейдеров никогда не бывают прозрачными - они всегда завуалированы и конфиденциальны. Информационно-правовая атака планируется рейдерами по каскадному методу, создавая непрерывное обременение сразу по нескольким направлениям, следуя простой логике &ndash; где слабее всего, там и порвется. И тогда, как в Матрице, охотники проникают в систему и разрушают ее изнутри.&nbsp;</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong>В зависимости от ситуации, рейдеры запускают один или несколько каскадов в рамках &quot;правового спектакля&quot;: </strong></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong>Арбитражный каскад</strong> - возбуждение исполнительных производств, с еженедельным / ежедневным каскадом. Цель &mdash; не выигрыш в суде, а создание обеспечительных мер, которые снимаются через определенный срок. К этому моменту подходят другие дела.</div>\r\n<div style=\"text-align: justify;\"><strong>Уголовный каскад</strong> - возбуждение заказных уголовных дел по слабым и сильным статьям УК РФ. В рамках данных дел выносятся определения, постановления и аресты. Иногда дела возбуждаются по персоналиям. Цель &mdash; арестовать имущество в рамках следственных мероприятий и передать на ответственное хранение менеджеру, близкому рейдерам. Дела по персоналиям часто возбуждаются против руководителей жертвы для оказания психологического давления на них.</div>\r\n<div style=\"text-align: justify;\">Налоговый каскад - организация серии заказных налоговых проверок, как по предприятию жертвы, так и по ее контрагентам. Цель &mdash; ослабить финансово-хозяйственные показатели жертвы.</div>\r\n<div style=\"text-align: justify;\"><strong>Милицейский каскад</strong> - организация серии заказных проверок в рамках Закона о милиции. Проверки производятся как по персоналиям, так и по фирмам жертвы. Мотивы проверок &mdash; различны. От оружия и наркотиков, до обвинения в готовящихся преступлениях, в том числе и в экономической области.</div>\r\n<div style=\"text-align: justify;\">Прокурорский каскад - организация мер противодействия следствию и уголовным делам против самих рейдеров. Проверки, объединения и перемещения уголовных дел с целью их закрытия или приостановки. Часто при объединении дел, следственная документация перепрофилируется для использования против предприятий-жертв.</div>\r\n<div style=\"text-align: justify;\"><strong>Надзорный каскад</strong> - организация документальных проверок и проверок личного состава охранных предприятий и действий должностных лиц, по закону выступающих на стороне жертвы в целях их нейтрализации.</div>\r\n<div style=\"text-align: justify;\"><strong>Информационный каскад</strong> - организация мероприятий для формирования негативного имиджа жертвы и ее партнеров в глазах общественности и правоохранительной системы, а также информационная атака на союзы, ассоциации, государственные органы, так или иначе защищающие права законных собственников. Присвоение роли жертвы самим рейдерам.</div>\r\n<div style=\"text-align: justify;\"><strong>Международный каскад</strong> - организация мероприятий, уводящих арбитражные, административные и уголовные процессы, имущественные споры и платежи за пределы юрисдикции России в труднодоступные уголки мира (оффшоры).</div>\r\n<div style=\"text-align: justify;\"><strong>Регистрационный каскад </strong>- бесконечная смена собственников предприятия и менеджмента предприятия без их ведома, перевод реестров акционеров, подделка реестров, в результате чего число акций предприятия превышает 100%.</div>\r\n<div style=\"text-align: justify;\"><strong>Имущественный каскад </strong>- многократная перепродажа имущества, акций, векселей и прочих активов через технические фирмы, почти всегда являющимися собственностью рейдера. Цель - отмывание и отбеливание активов предприятия для формирования в конечном итоге добросовестного покупателя, права которого уже защищает закон.</div>\r\n<div style=\"text-align: justify;\"><strong>Силовой каскад </strong>- совершение криминально-наказуемых деяний, таких как: клевета, мошенничество, самоуправство, злоупотребление полномочиями, получение и дача взяток, коммерческий подкуп, шантаж, вымогательство, угроза применения насилия, причинение вреда здоровью различной степени тяжести, похищение людей и др.</div>\r\n<div style=\"text-align: justify;\"><strong>Каскад надежды </strong>- доброхоты, якобы случайно узнавшие о проблемах предприятия-жертвы, вступают с ним в контакт и предлагаются те или иные мероприятия по защите его интересов. Как правило, добрые люди, прикрываясь важными именами и названиями хорошо известных структур и общественных объединений, на самом деле действуют с рейдерами заодно и сообща. Они либо предлагают купить предприятие по заведомо низкой цене, мотивируя сложностью проблемы, либо предоставляют себя в качестве переговорщика с агрессором. Признак каскадности соблюдается за счет череды доброхотов. Один не помог, так поможет другой.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong>Корпоративное плутовство. Мошенничество. Контрафактный документооборот.</strong></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">Корпоративное плутовство - наиболее сильный инструмент рейдеров. Его цель &mdash; запутать все и всех на свете и создать &laquo;иллюзию сложного корпоративного спора&raquo;. Чтобы избежать персональной ответственности, рейдеры в качестве контрагентов спора подставляют &laquo;мертвые души&raquo;, фигурантов, потерявших свои документы, а иногда и самих директоров предприятий-жертв, якобы учредивших фирмы-однодневки по поддельным документам. Схема запутывания является основой для запуска основного, отвлекающего и юридического каскадов. Это, по сути, бизнес-план захвата предприятия.<br />\r\n<br />\r\nВ своих атаках рейдеры применяют комбинированный документооборот. Вход в процесс нападения происходит на основе документального контрафакта, т.е.&nbsp;изготовление заведомо ложных документов от однодневных фигурантов:</div>\r\n<div style=\"text-align: justify;\">- оффшорные фирмы с разрывами по собственности и российские однодневки;</div>\r\n<div style=\"text-align: justify;\">- &quot;мертвые души&quot; в качестве учредителей и директоров;</div>\r\n<div style=\"text-align: justify;\">- менеджеры и независимые директора, действующие по доверенностям;</div>\r\n<div style=\"text-align: justify;\">- юристы, действующие по доверенностям, полученным от доверенных лиц.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">&laquo;Среднее звено&raquo; процесса рейдерского нападения предназначено для отбеливания однодневок методом перевода собственности на площадку для продажи и для уничтожения документального контрафакта. Платежи являются чисто техническими и представляют собой перекладывание из одного кармана в другой с использованием банковских кредитов и прочих схем, в том числе зачетных.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">Для юридической зачистки рейдеры предпринимают следующие действия:</div>\r\n<div style=\"text-align: justify;\">- закрытие обременяющих арбитражных и уголовных дел;</div>\r\n<div style=\"text-align: justify;\">- частичная или полная утеря первичной документации;</div>\r\n<div style=\"text-align: justify;\">- отказы от претензий со стороны однодневок и ликвидация самоарестов;</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong>Закрепление успехов рейдерского нападения. </strong></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">Формирование добросовестного покупателя. Продажа собственности по остаточной стоимости, например, инвестору-застройщику с приличной репутацией. Комиссионные&nbsp;- всегда наличными. В интегральных схемах, где заказчик нападения и рейдер - одно лицо (бенефициар), платежи носят чисто технический характер. На данном этапе могут возникнуть копеечные налоговые платежи.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">Все звенья процесса рейдерского нападения разделены информационно: в цепочке документооборота никто не знает предыдущее и следующее звено. Кроме координатора и заказчика захвата, разумеется.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong>Документный контрафакт. Рейдерский портфель.</strong></div>\r\n<div style=\"text-align: justify;\"><br />\r\nВ рейдерских схемах документооборот создается хорошо организованным &laquo;печатным станком&raquo;. Печатный станок документного контрафакта является уникальным изобретением &quot;черных адвокатов&quot;. Это автоматизированная система, с помощью которой создается полный пакет заведомо поддельных документов в кратчайшие сроки и практически без ошибок. В офисе печатного станка имеются все необходимые бланки, печати и подписи. Печатный станок может создавать бумаги любого содержания, в том числе и на государственных бланках. Судебные решения, постановления, выписки, свидетельства на право собственности, резолюции чиновников различного уровня. Не говоря уже об уставах, хозяйственных договорах, актах, акциях и векселях! Управляется такой офис адвокатами нападения.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">В перекладывании бумажек с места на место участвует множество не знакомых друг с другом менеджеров, работа которых заключается иногда только в том, чтобы в нужное время взять бумагу и в нужное место ее отнести. Как правило, этих менеджеров ждут, и их документы получают ход с максимальной скоростью.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">В самых сложных, непробиваемых схемах весьма часто происходит замена бенефициара.<br />\r\nДанная процедура называется продажей ситуации с обременением. Иногда ситуация продается по несколько раз. В основном рейдеры предпочитают работать с портфелем заказов, состоящим из набора проектов различной сложности: от простых, до &laquo;высокорисковых&raquo;. Финансирование привлекается в рейдерский портфель, реже - в проект. В портфеле, в зависимости от проектных рисков, создаются от 50 до 1000% прибыли на сделку. Рейдерский портфель на сегодняшний день является, пожалуй, самым агрессивным и эффективным высокодоходным инструментом размещения средств.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;</div>\r\n<div style=\"text-align: justify;\"><u><strong>Комплекс мер по борьбе с рейдерским захватом и поглощением бизнеса:</strong></u></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">- выявление и проведение полного анализа работы компании-агрессора;</div>\r\n<div style=\"text-align: justify;\">- оценка угрозы рейдерского захвата, своевременное создание различных препятствий для захватчиков, выработка силовых, административных и корпоративных мер защиты предприятия и его активов от незаконных действий;</div>\r\n<div style=\"text-align: justify;\">- организация комплексных мер по защите информации компании;</div>\r\n<div style=\"text-align: justify;\">- осуществление мероприятий по защите корпоративных документов предприятия, ревизия уставных и учредительных документов компании, приведение документов в правовую норму, уничтожение юридических &laquo;лазеек&raquo; для рейдерского захвата, организация четкого документооборота;</div>\r\n<div style=\"text-align: justify;\">- кадровая чистка руководящего состава предприятия;</div>\r\n<div style=\"text-align: justify;\">- обременение активов и прав на имущество: купля-продажа, передача в залог или аренду, снижающих экономическую привлекательность и ценность захвата;</div>\r\n<div style=\"text-align: justify;\">- судебная защита прав собственности, а также активов и недвижимого имущества предприятия; подача исковых заявлений в суды и представительства по спорным сделкам, которые были заключены на незаконных основаниях;<br />\r\n- тесное сотрудничество с государственными и правоохранительными органами;<br />\r\n- разработка мер по охране прав от незаконного захвата органов управления в компании, создание препятствий и нейтрализация деятельности рейдеров;<br />\r\n- обязательный повышенный контроль за всеми процессами, которые происходят в фирме;</div>\r\n<div style=\"text-align: justify;\">- повышенный контроль за всеми видами задолженностей;</div>\r\n<div style=\"text-align: justify;\">- осуществление мероприятий по физической защите основных акционеров и руководителей предприятия, членов их семей;</div>\r\n<div style=\"text-align: justify;\">- организация охранных мероприятий по защите от силового захвата предприятия.</div>\r\n</span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div><span style=\"color: rgb(0, 0, 128);\">\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n</span></div>\r\n</div>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',`date` = '2010-10-31 18:14:42',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '23',`parent` = '5',`position` = '6',`autotext` = '',`page` = 'Закон о ЧД и ОД в РФ',`name` = 'Закон о ЧД и ОД в РФ',`title` = 'Закон о частной детективной и охранной деятельности в Российской федерации (с поправками) от 11 марта 1992 года N 2487-1',`description` = 'Закон о частной детективной и охранной деятельности в Российской федерации (с поправками) от 11 марта 1992 года N 2487-1\r\n\r\n',`keywords` = 'Закон о ЧД и ОД в РФ',`text` = '<div style=\"text-align: justify\">&nbsp;</div>\r\n<div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">РОССИЙСКАЯ ФЕДЕРАЦИЯ</span></span></strong></div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">&nbsp;</span></span></strong></div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">ЗАКОН</span></span></strong></div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">О ЧАСТНОЙ ДЕТЕКТИВНОЙ И ОХРАННОЙ</span></span></strong></div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">ДЕЯТЕЛЬНОСТИ В РОССИЙСКОЙ ФЕДЕРАЦИИ</span></span></strong></div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">&nbsp;</span></span></strong></div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">(в ред. Федеральных законов от 21.03.2002 N 31-ФЗ,</span></span></strong></div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">от 10.01.2003 N 15-ФЗ, от 06.06.2005 N 59-ФЗ,</span></span></strong></div>\r\n<div style=\"text-align: center\"><strong><span style=\"font-size: small\"><span style=\"color: #000080\">от 18.07.2006 N 118-ФЗ, от 24.07.2007 N 214-ФЗ)</span></span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Раздел I. ОБЩИЕ ПОЛОЖЕНИЯ</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 1. Частная детективная и охранная деятельность</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Настоящим Законом частная детективная и охранная деятельность определяется как оказание на возмездной договорной основе услуг физическим и юридическим лицам предприятиями, имеющими специальное разрешение (лицензию) органов внутренних дел, в целях защиты законных прав и интересов своих клиентов.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">На граждан, осуществляющих частную детективную и охранную деятельность, действие законов, закрепляющих правовой статус работников правоохранительных органов, не распространяется.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Граждане, занимающиеся частной детективной деятельностью, не вправе осуществлять какие-либо оперативно-розыскные действия, отнесенные законом к исключительной компетенции органов дознания.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 2. Правовая основа частной детективной и охранной деятельности</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Правовую основу частной детективной и охранной деятельности составляют Конституция РСФСР, настоящий Закон, другие законы и иные правовые акты Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 3. Виды частной детективной и охранной деятельности</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частная детективная и охранная деятельность осуществляется для сыска и охраны.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В целях сыска разрешается предоставление следующих видов услуг:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">1) сбор сведений по гражданским делам на договорной основе с участниками процесса;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">2) изучение рынка, сбор информации для деловых переговоров, выявление некредитоспособных или ненадежных деловых партнеров;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">3) установление обстоятельств неправомерного использования в предпринимательской деятельности фирменных знаков и наименований, недобросовестной конкуренции, а также разглашения сведений, составляющих коммерческую тайну;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">4) выяснение биографических и других характеризующих личность данных об отдельных гражданах (с их письменного согласия) при заключении ими трудовых и иных контрактов;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">5) поиск без вести пропавших граждан;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">6) поиск утраченного гражданами или предприятиями, учреждениями, организациями имущества;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">7) сбор сведений по уголовным делам на договорной основе с участниками процесса. В течение суток с момента заключения контракта с клиентом на сбор таких сведений частный детектив обязан письменно уведомить об этом лицо, производящее дознание, следователя или суд, в чьем производстве находится уголовное дело.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 24.07.2007 N 214-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В целях охраны разрешается предоставление следующих видов услуг:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">1) защита жизни и здоровья граждан;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">2) охрана имущества (в том числе при его транспортировке), находящегося в собственности, во владении, в пользовании, хозяйственном ведении, оперативном управлении или доверительном управлении;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(п. 2 в ред. Федерального закона от 18.07.2006 N 118-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">3) проектирование, монтаж и эксплуатационное обслуживание средств охранно-пожарной сигнализации;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">4) консультирование и подготовка рекомендаций клиентам по вопросам правомерной защиты от противоправных посягательств;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">5) обеспечение порядка в местах проведения массовых мероприятий.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Предприятиям, осуществляющим частную детективную и охранную деятельность, предоставляется право содействовать правоохранительным органам в обеспечении правопорядка, в том числе на договорной основе.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Физическим и юридическим лицам, не имеющим правового статуса частного детектива, индивидуального частного детективного предприятия или объединения, частного охранника или частного охранного предприятия либо охранно-сыскного подразделения, запрещается оказывать услуги, перечисленные в настоящей статье.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Раздел II. ЧАСТНАЯ ДЕТЕКТИВНАЯ (СЫСКНАЯ) ДЕЯТЕЛЬНОСТЬ</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 4. Частный детектив</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частным детективом признается гражданин Российской Федерации, получивший в установленном законом порядке лицензию на частную сыскную деятельность и выполняющий услуги, перечисленные в части второй статьи 3 настоящего Закона.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Сыскная деятельность должна быть основным видом занятости частного детектива, совмещение ее с государственной службой либо с выборной оплачиваемой должностью в общественных объединениях не разрешается.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 5. Действия частных детективов</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В ходе частной сыскной деятельности допускаются устный опрос граждан и должностных лиц (с их согласия), наведение справок, изучение предметов и документов (с письменного согласия их владельцев), внешний осмотр строений, помещений и других объектов, наблюдение для получения необходимой информации в целях оказания услуг, перечисленных в части первой статьи 3 настоящего Закона.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">При осуществлении частной сыскной деятельности допускается использование видео- и аудиозаписи, кино- и фотосъемки, технических и иных средств, не причиняющих вреда жизни и здоровью граждан и окружающей среде, а также средств оперативной радио- и телефонной связи.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В случае необходимости оказания частными детективами услуг, сопряженных с опасностью для их жизни и здоровья, им разрешается использование специальных средств, виды, порядок приобретения, учета, хранения и ношения которых устанавливаются Правительством Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 6. Выдача лицензий частным детективам</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Выдача лицензий на частную сыскную деятельность на определенной территории Российской Федерации производится соответствующим органом внутренних дел в пределах его компетенции.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Гражданин, претендующий на получение лицензии на работу в качестве частного детектива, обязан лично предоставить в соответствующий орган внутренних дел:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">анкету;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">фотографии;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">медицинскую справку о состоянии здоровья;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">документы, подтверждающие его гражданство, наличие юридического образования или прохождение специальной подготовки для работы в качестве частного сыщика, либо стаж работы в оперативных или следственных подразделениях не менее трех лет;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">сведения о потребности в специальных средствах, средствах связи и иных технических средствах и намерении их использовать.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Органы внутренних дел вправе устанавливать достоверность сведений, изложенных в представленных документах, необходимых для принятия решения о выдаче лицензии, в том числе путем собеседования с гражданином, претендующим на ее получение.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Лицензия не выдается:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">1) гражданам, не достигшим двадцати одного года;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">2) гражданам, состоящим на учете в органах здравоохранения по поводу психического заболевания, алкоголизма или наркомании;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">3) гражданам, имеющим судимость за совершение умышленного преступления;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">4) гражданам, которым предъявлено обвинение в совершении преступления (до разрешения вопроса об их виновности в установленном законом порядке);</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Пункт 5 части четвертой подлежит применению в соответствии с его конституционно-правовым смыслом, выявленным в определении Конституционного Суда РФ от 14.01.2003 N 32-О исходя из правовых позиций Конституционного Суда Российской Федерации, сохраняющих свою силу.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">5) гражданам, уволенным с государственной службы, из судебных, прокурорских и иных правоохранительных органов по компрометирующим их основаниям;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">6) бывшим работникам правоохранительных органов, осуществлявшим контроль за частной детективной и охранной деятельностью, если со дня их увольнения не прошел год;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">7) гражданам, не представившим документы, перечисленные в части второй настоящей статьи.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Часть пятая исключена. - Федеральный закон от 10.01.2003 N 15-ФЗ.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В случае отказа в выдаче лицензии орган внутренних дел обязан письменно информировать об этом гражданина, направившего заявление, с указанием мотивов отказа. Это решение или нарушение срока рассмотрения заявления могут быть обжалованы в вышестоящий орган внутренних дел или суд.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Гражданину, получившему лицензию на работу в качестве частного детектива, соответствующим органом внутренних дел одновременно выдается документ установленного образца, удостоверяющий его личность.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 7. Ограничения в сфере деятельности частного детектива</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частным детективам запрещается:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">1) скрывать от правоохранительных органов ставшие им известными факты готовящихся или совершенных преступлений;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">2) выдавать себя за сотрудников правоохранительных органов;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">3) собирать сведения, связанные с личной жизнью, с политическими и религиозными убеждениями отдельных лиц;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">4) осуществлять видео- и аудиозапись, фото- и киносъемку в служебных или иных помещениях без письменного согласия на то соответствующих должностных или частных лиц;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">5) прибегать к действиям, посягающим на права и свободы граждан;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">6) совершать действия, ставящие под угрозу жизнь, здоровье, честь, достоинство и имущество граждан;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">7) фальсифицировать материалы или вводить в заблуждение клиента;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">8) разглашать собранную информацию, использовать ее в каких-либо целях вопреки интересам своего клиента или в интересах третьих лиц;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">9) передавать свою лицензию для использования ее другими лицами.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Проведение сыскных действий, нарушающих тайну переписки, телефонных переговоров и телеграфных сообщений либо связанных с нарушением гарантий неприкосновенности личности или жилища, влечет за собой установленную законом ответственность.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 8. Создание объединений частных детективных предприятий</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 10.01.2003 N 15-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частные детективные предприятия могут создавать объединения, имеющие статус юридических лиц. Обязательным требованием для их руководителей является наличие высшего юридического образования и лицензий на частную сыскную деятельность.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Объединения частных детективов вправе открывать свои филиалы в других регионах Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 9. Договор между частным детективным предприятием (объединением) и клиентом</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частное детективное предприятие (объединение) обязано заключить с каждым из своих клиентов письменный договор на оказание услуг, в котором должны быть отражены сведения о договаривающихся сторонах, в том числе номер и дата выдачи лицензии, содержание поручения, срок его выполнения, ориентировочная сумма денежных расходов и гонорара за услуги, меры ответственности сторон, дата заключения договора.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Договор считается заключенным, если между сторонами на конфиденциальной основе достигнуто соглашение по всем его пунктам и он составлен с соблюдением условий, установленных в части первой настоящей статьи.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В договоре предусматривается обязанность частного детективного предприятия (объединения) предоставить клиенту письменный отчет о результатах проделанной работы, который должен включать ответы на основные вопросы, интересующие клиента в соответствии с договором.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">К отчету прилагается уточненный расчет гонорара и расходов частного детективного предприятия (объединения). Копия отчета подлежит хранению в архиве предприятия (объединения) в течение трех лет.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Споры по расчетам за услуги и другим основаниям, установленным договором между частным детективным предприятием (объединением) и клиентом, пользующимся его услугами, рассматриваются в судебном порядке.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 10. Исключена. - Федеральный закон от 10.01.2003 N 15-ФЗ.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Раздел III. ЧАСТНАЯ ОХРАННАЯ ДЕЯТЕЛЬНОСТЬ</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 11. Оказание услуг в сфере охраны</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Оказание услуг, перечисленных в части третьей статьи 3 настоящего Закона, разрешается только предприятиям, специально учреждаемым для их выполнения.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Предприятие, которое в соответствии со своим уставом занимается оказанием охранных услуг, обязано иметь на то лицензию, выдаваемую в порядке, установленном законодательством Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(часть вторая в ред. Федерального закона от 10.01.2003 N 15-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Охранная деятельность предприятий не распространяется на объекты, подлежащие государственной охране, перечень которых утверждается Правительством Российской Федерации. Охранным предприятиям разрешается оказывать услуги в виде вооруженной охраны имущества, а также использовать технические и иные средства, не причиняющие вреда жизни и здоровью граждан и окружающей среде, средства оперативной радио- и телефонной связи.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 18.07.2006 N 118-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Часть четвертая исключена. - Федеральный закон от 10.01.2003 N 15-ФЗ.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Руководителям охранных предприятий, а также охранникам не разрешается совмещать охранную деятельность с государственной службой либо выборной оплачиваемой должностью в общественных объединениях.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 10.01.2003 N 15-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Части шестая - седьмая исключены. - Федеральный закон от 10.01.2003 N 15-ФЗ.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Виды вооружения охранников, порядок приобретения, учета, хранения и ношения ими оружия регламентируются Правительством Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 11.1. Правовой статус частного охранника</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(введена Федеральным законом от 06.06.2005 N 59-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частный охранник работает по трудовому договору с охранной организацией, и его трудовая деятельность регулируется трудовым законодательством и настоящим Федеральным законом. Частный охранник выполняет трудовую функцию и пользуется предусмотренными настоящим Федеральным законом правами при наличии удостоверения частного охранника, выданного органами внутренних дел в порядке, установленном Правительством Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Не вправе претендовать на приобретение правового статуса частного охранника лица:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">1) не являющиеся гражданами Российской Федерации;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">2) не достигшие восемнадцати лет;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">3) признанные решением суда недееспособными или ограниченно дееспособными;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">4) имеющие заболевания, которые препятствуют исполнению ими обязанностей частного охранника. Перечень таких заболеваний устанавливается Правительством Российской Федерации;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">5) имеющие судимость за совершение умышленного преступления;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">6) которым предъявлено обвинение в совершении преступления (до разрешения вопроса об их виновности в установленном законом порядке).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 12. Дополнительные условия выдачи лицензий</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Для получения лицензии на оказание охранных услуг предприятие, создаваемое для этих целей, представляет в соответствующий орган внутренних дел записку, в которой перечисляются виды охранных услуг и указываются территория деятельности создаваемого предприятия охраны, данные о предполагаемой численности персонала, намерении использовать технические и иные средства, специальные средства, оружие и потребности в них.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 10.01.2003 N 15-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Обязательным требованием к руководителю предприятия является наличие высшего образования.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">При необходимости органы внутренних дел вправе устанавливать достоверность сведений, изложенных в представленных документах, необходимых для принятия решений о выдаче лицензий на охранную деятельность.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Заключение охранными предприятиями договоров с клиентами на оказание охранных услуг осуществляется в соответствии с положениями статьи 9 настоящего Закона.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(часть четвертая в ред. Федерального закона от 10.01.2003 N 15-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">На охранную деятельность распространяются ограничения, установленные статьей 7 настоящего Закона. Охранникам запрещается использовать методы сыска.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Лицо, совершившее противоправное посягательство на охраняемое имущество, может быть задержано охранником на месте правонарушения и должно быть незамедлительно передано в орган внутренних дел (милицию).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 18.07.2006 N 118-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Раздел IV. СМЕШАННЫЕ ФОРМЫ ДЕТЕКТИВНОЙ</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">И ОХРАННОЙ ДЕЯТЕЛЬНОСТИ</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 13. Смешанная охранно-сыскная деятельность</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частным охранным предприятиям и предприятиям (объединениям) частных детективов для осуществления охранно-сыскной деятельности разрешается создавать на договорной основе ассоциации с сохранением своей самостоятельности и прав юридических лиц.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 14. Охранно-сыскные подразделения на предприятиях</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Предприятия независимо от их организационно-правовых форм, расположенные на территории Российской Федерации, вправе учреждать обособленные подразделения для осуществления охранно-сыскной деятельности в интересах собственной безопасности учредителя, с правом открытия текущих и расчетных счетов (далее - службы безопасности).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Руководители и персонал служб безопасности обязаны руководствоваться требованиями настоящего Закона и действовать на основании своих уставов, согласованных с органами внутренних дел по месту своего учреждения.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(часть вторая в ред. Федерального закона от 10.01.2003 N 15-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Службе безопасности запрещается оказывать услуги, не связанные с обеспечением безопасности своего предприятия.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 15. Подготовка детективов и охранников</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Для подготовки, переподготовки и повышения квалификации кадров, осуществляющих детективную и охранную деятельность, могут создаваться негосударственные образовательные учреждения, имеющие право юридического лица.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Разработка программ обучения, лицензирование указанных в части первой настоящей статьи учреждений и контроль за их деятельностью осуществляются в порядке, устанавливаемом нормативными актами Министерства образования Российской Федерации, согласованными с Министерством внутренних дел Российской Федерации, Министерством здравоохранения Российской Федерации и Министерством финансов Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Раздел V. ПРИМЕНЕНИЕ СПЕЦИАЛЬНЫХ СРЕДСТВ</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">И ОГНЕСТРЕЛЬНОГО ОРУЖИЯ ПРИ ОСУЩЕСТВЛЕНИИ ЧАСТНОЙ</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">ДЕТЕКТИВНОЙ И ОХРАННОЙ ДЕЯТЕЛЬНОСТИ</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 16. Условия применения специальных средств и огнестрельного оружия</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В ходе осуществления частной детективной деятельности разрешается применять специальные средства, а при осуществлении частной охраной деятельности - специальные средства и огнестрельное оружие только в случаях и в порядке, предусмотренных настоящим Законом.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 10.01.2003 N 15-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Охранник при применении специальных средств или огнестрельного оружия либо частный детектив при применении специальных средств обязан:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">предупредить о намерении их использовать, предоставив при этом достаточно времени для выполнения своих требований, за исключением тех случаев, когда промедление в применении специальных средств или огнестрельного оружия создает непосредственную опасность его жизни и здоровью или может повлечь за собой иные тяжкие последствия;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">стремиться в зависимости от характера и степени опасности правонарушения и лиц, его совершивших, а также силы оказываемого противодействия к тому, чтобы любой ущерб, причиненный при устранении опасности, был минимальным;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">обеспечить лицам, получившим телесные повреждения, доврачебную помощь и уведомить о происшедшем в возможно короткий срок органы здравоохранения и внутренних дел;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">немедленно уведомить прокурора о всех случаях смерти или причинения телесных повреждений.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частные детективы и охранники, обладающие лицензией, обязаны проходить периодическую проверку на пригодность к действиям в условиях, связанных с применением специальных средств и огнестрельного оружия. Такая проверка осуществляется в порядке, определяемом Министерством внутренних дел Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Применение частным детективом или охранником специальных средств или огнестрельного оружия с превышением своих полномочий, крайней необходимости или необходимой обороны влечет за собой ответственность, установленную законом.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 10.01.2003 N 15-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 17. Применение специальных средств</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">На частную детективную и охранную деятельность распространяются правила применения специальных средств, установленные Правительством Российской Федерации для органов внутренних дел Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частные детективы и охранники имеют право применять специальные средства в следующих случаях:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">1) для отражения нападения, непосредственно угрожающего их жизни и здоровью, а охранники и для отражения нападения, непосредственно угрожающего жизни и здоровью охраняемых граждан;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">2) для пресечения преступления против охраняемого ими имущества, когда правонарушитель оказывает физическое сопротивление.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 18.07.2006 N 118-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Запрещается применять специальные средства в отношении женщин с видимыми признаками беременности, лиц с явными признаками инвалидности и несовершеннолетних, когда их возраст очевиден или известен частному детективу (охраннику), кроме случаев оказания ими вооруженного сопротивления, совершения группового либо иного нападения, угрожающего жизни и здоровью частного детектива (охранника) или охраняемому имуществу.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 18.07.2006 N 118-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 18. Применение огнестрельного оружия</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Охранники имеют право применять огнестрельное оружие в следующих случаях:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">1) для отражения нападения, когда его собственная жизнь подвергается непосредственной опасности;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">2) для отражения группового или вооруженного нападения на охраняемое имущество;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 18.07.2006 N 118-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">3) для предупреждения (выстрелом в воздух) о намерении применить оружие, а также для подачи сигнала тревоги или вызова помощи.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Запрещается применять огнестрельное оружие в отношении женщин, лиц с явными признаками инвалидности и несовершеннолетних, когда их возраст очевиден или известен охраннику, кроме случаев оказания ими вооруженного сопротивления, совершения вооруженного либо группового нападения, угрожающего жизни охранника или охраняемому имуществу, а также при значительном скоплении людей, когда от применения оружия могут пострадать посторонние лица.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(в ред. Федерального закона от 18.07.2006 N 118-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">О каждом случае применения огнестрельного оружия охранник обязан незамедлительно информировать орган внутренних дел по месту применения оружия.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Раздел VI. ГАРАНТИИ СОЦИАЛЬНОЙ И ПРАВОВОЙ ЗАЩИТЫ,</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">ОТВЕТСТВЕННОСТЬ ЛИЦ, ЗАНИМАЮЩИХСЯ ЧАСТНОЙ</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">ДЕТЕКТИВНОЙ И ОХРАННОЙ ДЕЯТЕЛЬНОСТЬЮ</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 19. Социальная и правовая защита частных детективов и охранников</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Частная детективная и охранная деятельность засчитывается в общий трудовой стаж и стаж для назначения пособий по государственному социальному страхованию при условии уплаты взносов в Пенсионный фонд Российской Федерации и в Фонд государственного социального страхования Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Граждане, занимающиеся частной детективной и охранной деятельностью, подлежат обязательному страхованию за счет средств соответствующего предприятия (объединения) на случай гибели, получения увечья или иного повреждения здоровья в связи с осуществлением сыскных или охранных действий.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">(часть вторая в ред. Федерального закона от 06.06.2005 N 59-ФЗ)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Оказание сопротивления, угроза или насилие в отношении лиц, занимающихся оказанием охранных услуг в связи с исполнением ими своих обязанностей, влечет ответственность в соответствии с законом.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Раздел VII. КОНТРОЛЬ И НАДЗОР ЗА ЧАСТНОЙ</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">ДЕТЕКТИВНОЙ И ОХРАННОЙ ДЕЯТЕЛЬНОСТЬЮ</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 20. Контроль и надзор за частной детективной и охранной деятельностью</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Контроль за частной детективной и охранной деятельностью на территории Российской Федерации осуществляют Министерство внутренних дел Российской Федерации, иные министерства и ведомства Российской Федерации и подчиненные им органы и подразделения в пределах, установленных настоящим Законом, другими законами и иными правовыми актами Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Должностные лица, уполномоченные осуществлять контроль за деятельностью частных детективных и охранных предприятий (объединений, ассоциаций), образовательных учреждений и служб безопасности, вправе требовать от них в рамках своей компетенции представления соответствующих документов и получать письменную или устную информацию, необходимую для выполнения контрольных функций.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Надзор за исполнением настоящего Закона осуществляют Генеральный прокурор Российской Федерации и подчиненные ему прокуроры.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Раздел VIII. ОТВЕТСТВЕННОСТЬ ЗА СОЗДАНИЕ НЕЗАКОННЫХ</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">ДЕТЕКТИВНЫХ И ОХРАННЫХ ПРЕДПРИЯТИЙ</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Статья 21. Ответственность за создание незаконных детективных и охранных предприятий</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Создание или деятельность частных детективных и охранных предприятий (объединений, ассоциаций), образовательных учреждений и служб безопасности, не предусмотренных настоящим Законом, влекут за собой ответственность в соответствии с законодательством Российской Федерации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Президент</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Российской Федерации</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">Б.ЕЛЬЦИН</span></strong></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Москва, Дом Советов России</span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">11 марта 1992 года</span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: #000080\">N 2487-1</span></strong></div>\r\n</div>',`date` = '2010-11-04 15:27:09',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '24',`parent` = '5',`position` = '7',`autotext` = '',`page` = 'Изменения в Закон - 2010',`name` = 'Изменения в Закон о ЧД и ОД в РФ - 2010',`title` = 'Изменения в Законе «О частной детективной и охранной деятельности в Российской Федерации»',`description` = 'Характеристика изменений, внесенных в Закон «О частной детективной и охранной деятельности в Российской Федерации» в 2010 году.\r\n',`keywords` = 'Изменения в Закон о ЧД и ОД в РФ - 2010',`text` = '<div style=\"text-align: justify\">&nbsp;</div>\r\n<div>\r\n<div><span style=\"font-size: small\"><strong><span style=\"color: #000080\">Изменения внесенные в Закон </span></strong></span><span style=\"font-size: small\"><strong><span style=\"color: #000080\">&laquo;О частной детективной и охранной деятельности в Российской Федерации&raquo; </span></strong></span><span style=\"font-size: small\"><strong><span><span style=\"color: #000080\">в 2010 году</span></span></strong></span></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В декабре 2008 г. принят Федеральный закон &laquo;О внесении изменений в некоторые законодательные акты РФ: Законы &laquo;О милиции&raquo;, &laquo;О частной детективной и охранной деятельности в Российской Федерации&raquo;, &laquo;Об оружии&raquo;; Федеральные законы: &laquo;О противодействии терроризму&raquo;, &laquo;Об оперативно-розыскной деятельности&raquo;, &laquo;Об обществах с ограниченной ответственностью&raquo;, &laquo;О государственной дактилоскопической регистрации в Российской Федерации&raquo;, &laquo;О лицензировании отдельных видов деятельности&raquo;, &laquo;О защите прав юридических лиц и индивидуальных предпринимателей при осуществлении государственного контроля (надзора); Кодексы: Налоговый кодекс Российской Федерации, Кодекс Российской Федерации об административных правонарушениях, Уголовный кодекс Российской Федерации. <br />\r\n<br />\r\nСуть новых требований к частным охранным организациям, касающаяся учреждения ЧОО, лицензирования и приведения учредительных документов в соответствие с законодательством заключается в следующем: <br />\r\n<br />\r\n1. В законе изменено название Частное охранное предприятие на Частную охранную организацию.<br />\r\n<br />\r\nИсключение из Федерального закона &laquo;О лицензировании отдельных видов деятельности&raquo; п.п. 79 и 80 статьи 17 повлекло за собой изменение названия охранной деятельности &ndash; из названия указанных видов деятельности убирается слово &laquo;негосударственная&raquo; и они, как и прежде, будут именоваться: &laquo;частная охранная деятельность&raquo;. <br />\r\n<br />\r\n2. Охранная организация может быть создана только в одной организационно-правовой форме &ndash; общества с ограниченной ответственностью. <br />\r\n<br />\r\n3. Частная охранная организация не может осуществлять иную деятельность, кроме охранной деятельности; <br />\r\n<br />\r\nПеречень охранных услуг дополнен двумя новыми услугами и внесены коррективы в некоторые другие услуги. <br />\r\nПосле внесения изменения, указанные услуги изложены соответственно следующим образом: <br />\r\n1) защита жизни и здоровья граждан;<br />\r\n2) охрана объектов и (или) имущества (в том числе при его транспортировке), находящихся в собственности, во владении, в пользовании, хозяйственном ведении, оперативном управлении или доверительном управлении, за исключением объектов и (или) имущества, предусмотренных пунктом 7 настоящей части;<br />\r\n3) охрана объектов и (или) имущества на объектах с осуществлением работ по проектированию, монтажу и эксплуатационному обслуживанию технических средств охраны, перечень видов которых устанавливается Правительством Российской Федерации, и (или) с принятием соответствующих мер реагирования на их сигнальную информацию;<br />\r\n4) консультирование и подготовка рекомендаций клиентам по вопросам правомерной защиты от противоправных посягательств;<br />\r\n5) обеспечение порядка в местах проведения массовых мероприятий;<br />\r\n6) новое! обеспечение внутриобъектового и пропускного режимов на объектах, за исключением объектов, предусмотренных пунктом 7 настоящей части;<br />\r\n7) новое! охрана объектов и (или) имущества, а также обеспечение внутриобъектового и пропускного режимов на объектах, которые имеют особо важное значение для обеспечения жизнедеятельности и безопасности государства и населения и перечень которых утверждается в порядке, установленном Правительством Российской Федерации.<br />\r\n<br />\r\nУпразднено положение, предоставляющее частным охранным организациям оказывать содействие правоохранительным органам в обеспечении правопорядка на договорной основе. Порядок оказания указанного содействия будет установлен Правительством Российской Федерации, проект которого в настоящее время готовится<br />\r\n<br />\r\n4. Уставный капитал частной охранной организации не может быть менее ста тысяч рублей, а при оказании услуги в виде вооруженной охраны имущества, охраны объектов и (или) имущества на объектах с осуществлением работ по проектированию, монтажу и эксплуатационному обслуживанию технических средств охраны &ndash; менее двухсот пятидесяти тысяч рублей. При этом предельный размер имущественных (неденежных) вкладов в уставный капитал частной охранной организации не может быть более 50% от размера уставного капитала. <br />\r\n<br />\r\nВнесение в уставный капитал частной охранной организации средств иностранными гражданами, гражданами Российской Федерации, имеющими гражданство иностранного государства, лицами без гражданства, иностранными юридическими лицами, а также организациями, в составе учредителей (участников) которых имеются указанные граждане и лица, запрещается, если иное не предусмотрено международными договорами Российской Федерации. <br />\r\n<br />\r\n5. Требования, предъявляемые к учредителю частной охранной организации. <br />\r\n<br />\r\n5.1 Для учредителя (юридического лица) частной охранной организации данный вид деятельности должен быть основным. Право учреждения частной охранной организации юридическим лицом, осуществляющим иную деятельность, кроме охранной, может быть предоставлено при наличии достаточных оснований в порядке, установленном Правительством Российской Федерации. <br />\r\n<br />\r\n5.2. Не могут являться учредителями (участниками) частной охранной организации: <br />\r\n1) общественные объединения; <br />\r\n2) физические и (или) юридические лица, не соответствующие требованиям, указанным в части четвертой настоящей статьи; <br />\r\n3) граждане, состоящие на государственной службе либо замещающие выборные оплачиваемые должности в общественных объединениях; <br />\r\n4) граждане, имеющие судимость за совершение умышленного преступления, а также юридические лица, в составе учредителей (участников) которых имеются указанные лица; <br />\r\n5) иностранные граждане, граждане Российской Федерации, имеющие гражданство иностранного государства, лица без гражданства, иностранные юридические лица, а также организации, в составе учредителей (участников) которых имеются указанные граждане и лица, при отсутствии соответствующего международного договора Российской Федерации. <br />\r\n<br />\r\n5.3. Участниками частной охранной организации могут оставаться учредившие ее лица, которые получили право на пенсию по старости в соответствии с законодательством Российской Федерации, перешли на работу в общественные организации, работающие в сфере частной охранной либо частной детективной деятельности, либо назначены (избраны) на государственные должности Российской Федерации. <br />\r\n<br />\r\nЛицам, назначенным (избранным) на указанные государственные должности, запрещается принимать участие в управлении охранной организацией.&nbsp; <br />\r\n<br />\r\n5.4. Введены ограничения на осуществление частной охранной и детективной деятельности иностранными гражданами, гражданами Российской Федерации, имеющими гражданство иностранного государства, иностранными юридическими лицами, а также организациями, в составе учредителей (участников) которых имеются указанные граждане и лица. <br />\r\n<br />\r\nУказанные юридические и физические лица теперь могут осуществлять частную охранную и детективную деятельность и (или) принимать в ней участие в ее осуществлении в любой форме, в том числе и в управлении частной охранной организацией, только на основаниях и в рамках, предусмотренных международными договорами Российской Федерации. <br />\r\n<br />\r\nОтчуждение долей (вкладов) учредителем (участником) частной охранной организации, повлекшее за собой появление в уставном капитале доли (вклада) с иностранным участием, не допускается, если иное не предусмотрено международными договорами Российской Федерации; <br />\r\n<br />\r\n5.5.Частная охранная организация не может являться дочерним обществом организации, осуществляющей иную деятельность, кроме охранной деятельности.<br />\r\n<br />\r\n6. Филиалы частной охранной организации могут создаваться только в том субъекте Российской Федерации, на территории которого частная охранная организация зарегистрирована. <br />\r\n<br />\r\n7. Из числа субъектов частной охранной и детективной деятельности исключены службы безопасности юридических лиц. <br />\r\n<br />\r\n8.&nbsp; Установлены требования к руководителю частной охранной организации: <br />\r\n1) Руководитель частной охранной организации должен иметь высшее профессиональное образование и пройти повышение квалификации для руководителей частных охранных организаций. <br />\r\n2) Обязательным требованием является наличие у руководителя частной охранной организации удостоверения частного охранника. <br />\r\n3) Руководитель также не вправе замещать государственные должности Российской Федерации, государственные должности субъектов Российской Федерации, должности государственной службы, выборные оплачиваемые должности в общественных объединениях и политических партиях, а также вступать в трудовые отношения в качестве работника, за исключением осуществления им научной, преподавательской и иной творческой деятельности. <br />\r\n<br />\r\n9.&nbsp; Для приобретения статуса охранника гражданин обязан: 1) пройти профессиональную подготовку; 2) сдать квалификационный экзамен; 3) пройти обязательную дактилоскопическую регистрацию; 4) иметь удостоверение частного охранника.&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Основания, при наличии которых гражданин не вправе претендовать на приобретение статуса частного охранника: <br />\r\n1) не являющиеся гражданами Российской Федерации;<br />\r\n2) не достигшие восемнадцати лет;<br />\r\n3) признанные решением суда недееспособными или ограниченно дееспособными;<br />\r\n4) имеющие заболевания, которые препятствуют исполнению ими обязанностей частного охранника. Перечень таких заболеваний устанавливается Правительством Российской Федерации;<br />\r\n5) имеющие судимость за совершение умышленного преступления;<br />\r\n6) которым предъявлено обвинение в совершении преступления (до разрешения вопроса об их виновности в установленном законом порядке);<br />\r\n7) не прошедшие профессиональной подготовки для работы в качестве охранника;<br />\r\n8) в отношении которых по результатам проверки, имеется подготовленное и утвержденное руководителем территориального органа федерального органа исполнительной власти, в ведении которого находятся вопросы внутренних дел, или лицом, исполняющим его обязанности,&nbsp; заключение о невозможности допуска этих лиц к осуществлению частной охранной деятельности в связи с повышенной опасностью нарушения прав и свобод граждан, возникновением угрозы общественной безопасности; <br />\r\n9) досрочно прекратившие полномочия по государственной должности или уволенные с государственной службы, в том числе из правоохранительных органов, из органов прокуратуры, судебных органов, по основаниям, связаным с совершением дисциплинарного проступка, грубым или систематическим нарушением дисциплины, совершением проступка, порочащего честь государственного служащего, утратой доверия к нему, если после такого досрочного прекращения полномочий или такого увольнения прошло менее трех лет; <br />\r\n10) у которых удостоверение частного охранника было аннулировано (за неоднократное привлечения в течение года частного охранника к административной ответственности за совершение административных правонарушений, посягающих на институты государственной власти, административных правонарушений против порядка управления и административных правонарушений, посягающих на общественный порядок и общественную безопасность) и, если после принятия решения об аннулировании прошло менее года; <br />\r\n11) не прошедшие обязательной государственной дактилоскопической регистрации в порядке, установленном законодательством Российской Федерации. <br />\r\n<br />\r\n10. Органам внутренних дел предоставлены полномочия выдавать удостоверение частного охранника и личную карточку охранника, а также возложена обязанность принимать меры по аннулированию удостоверения частного охранника. <br />\r\n10.1. Удостоверение частного охранника выдается сроком на пять лет. Срок действия удостоверения частного охранника может продлеваться в порядке, установленном Правительством Российской Федерации. Продление срока действия удостоверения частного охранника осуществляется только после повышения квалификации в образовательных учреждениях, указанных в статье 15.2&nbsp; Закона. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Размер пошлины (п.1 ст. 333.33 Налогового кодекса РФ) За выдачу удостоверения (дубликата удостоверения) частного охранника будет взиматься 600 рублей, за его переоформление в связи с продлением срока действия - 200 рублей, за внесение в него изменений в связи с изменением места жительства или иных данных, указываемых в удостоверении, - 100 рублей.&nbsp;<br />\r\n&nbsp;<br />\r\n10.2.Удостоверение частного охранника аннулируется по решению органа внутренних дел в случае: <br />\r\n1) неоднократного привлечения в течение года частного охранника к административной ответственности за совершение административных правонарушений, посягающих на институты государственной власти, административных правонарушений против порядка управления и административных правонарушений, посягающих на общественный порядок и общественную безопасность; <br />\r\n2) возникновения обстоятельств, при которых гражданин не может претендовать на приобретение правового статуса частного охранника; <br />\r\n3) окончания срока действия удостоверения частного охранника, добровольного отказа от такого удостоверения либо смерти гражданина, которому было выдано такое удостоверение. <br />\r\n<br />\r\n11. В соответствии с новой редакцией часть 2 статьи 19 Закона граждане, занимающиеся частной охранной деятельностью, подлежат страхованию на случай гибели, получения увечья или иного повреждения здоровья в связи с оказанием ими охранных услуг в порядке, установленном законодательством Российской Федерации. Указанное страхование осуществляется за счет средств соответствующей охранной организации и включается в состав ее затрат. <br />\r\n<br />\r\n12. Действие Федерального закона &laquo;О лицензировании отдельных видов деятельности&raquo; не распространяется на частную охранную деятельность, Закон &laquo;О частной детективной и охранной деятельности в Российской Федерации&raquo; дополнен статьями 11.2 -11.6, в которых прописан порядок лицензирования указанных видов деятельности. <br />\r\n<br />\r\n12.1. Порядок лицензирования частной охранной деятельности, не содержит требования, указывать в документе, подтверждающем наличие лицензии, адреса мест осуществления лицензируемого вида деятельности. <br />\r\nДействие лицензий на осуществление частной детективной и охранной деятельности, выданных до дня вступления в силу принятого Федерального закона, сохраняется до истечения срока действия таких лицензий, если лицензиат соответствует требованиям, установленным данным Федеральным законом. <br />\r\n-лицензия действует на всей территории Российской Федерации; <br />\r\n- в лицензии указываются вид (виды) охранных услуг, которые может оказывать лицензиат; <br />\r\n- решение о предоставлении либо об отказе в предоставлении лицензии принимается органом внутренних дел в срок не более сорока пяти дней; <br />\r\n<br />\r\n12.2. Основания для переоформления лицензии: <br />\r\n- необходимость продления срока действия лицензии; <br />\r\n- намерение лицензиата осуществлять новый (новые) вид (виды) охранных услуг, не указанный (не указанные) в предоставленной лицензии; <br />\r\n- реорганизации охранной организации; <br />\r\n- изменение наименования охранной организации или места ее нахождения. <br />\r\n<br />\r\nПереоформление лицензии осуществляется в срок не более тридцати дней. На период переоформления действие лицензии не приостанавливается. <br />\r\n<br />\r\n12.3. Установленный новый порядок лицензирования частной охранной деятельности предоставляет право органам внутренних дел приостанавливать действие лицензии в случае выявления неоднократных нарушений или грубого нарушения лицензионных требований и условий на срок не более одного месяца, который дается для устранения выявленных нарушений. Если в указанный срок нарушения не устранены орган внутренних дел обязан обратиться в суд с заявлением о приостановлении действия лицензии на срок до шести месяцев либо об аннулировании лицензии. Следовательно, орган внутренних дел вправе приостановить действие лицензии только на срок не более месяца, причем правом принятия такого решения наделены только руководитель федерального органа исполнительной власти, в ведении которого находятся вопросы внутренних дел, его заместители, руководитель подразделения данного федерального органа исполнительной власти, ведающего вопросами лицензирования частной охранной деятельности, его заместителям, а также руководитель территориального органа федерального органа исполнительной власти, в ведении которого находятся вопросы внутренних дел, или лицу, исполняющему его обязанности. <br />\r\n<br />\r\n12.4. Аннулирование лицензии, как и прежде, осуществляет суд по заявлению органа внутренних дел. <br />\r\n<br />\r\n13. На уровне закона закреплена обязанность охранной организации уведомлять органы внутренних дел о начале и об окончании оказания охранных услуг в порядке, установленном Правительством Российской Федерации. <br />\r\n<br />\r\n14. Дополнительные требования к осуществлению частной охранной деятельности: <br />\r\n14.1. Работникам частной охранной организации не разрешается совмещать охранную деятельность с государственной службой либо выборной оплачиваемой должностью в общественных объединениях. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Буквальное толкование данной нормы позволяет сделать вывод о том, что данный запрет относится не только к частным охранникам, но и ко всем работникам частной охранной организации. <br />\r\n<br />\r\n14.2. Работником частной охранной организации не может быть учредитель (участник), руководитель либо иное должностное лицо организации, с которой данной частной охранной организацией заключен договор на оказание охранных услуг.&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Эта норма означает, что, во-первых, учредитель (участник) организации, с которой заключен договор на оказание охранных услуг не вправе заключить трудовой договор с частной охранной организацией, во-вторых, не вправе это делать также и заказчик охранной услуги, если он является руководителем или иным должностным лицом частной охранной организации, с которой заключен договор на оказание охранных услуг. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Из содержания данной нормы также следует, что работником частной охранной организации могут быть все другие, кроме руководителя и иных должностных лиц, работники организации, с которой заключен договор на оказание охранных услуг, а также заказчики охранных услуг физические лица (граждане). <br />\r\n<br />\r\n14.3. К договору на оказание охранных услуг (речь идет об услугах, связанных с охраной имущества) должны быть приложены копии заверенных заказчиком документов, подтверждающих его право владения или пользования имуществом, подлежащим охране, в соответствии с законодательством Российской Федерации. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Введены ограничительные нормы, направленные на недопущение участия частных охранных структур в гражданско-правовых конфликтах, связанных с оспариванием прав собственников. <br />\r\n<br />\r\n15. Ограничена деятельность частных охранных организаций в зоне проведения контртеррористической операции.&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Так, руководитель контртеррористической операции вправе ограничить или приостановить осуществление частной охранной деятельности. <br />\r\n<br />\r\n16. Принятым Законом внесены существенные изменения в имеющийся порядок использования оружия в частной охранной деятельности.&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\nСуть указанных изменений: <br />\r\n<br />\r\n16.1. Изменился собственник служебного оружия, используемого в частной охранной деятельности. <br />\r\nВ настоящее время оружие является собственностью частных охранных организаций. После вступления в силу принятого Федерального закона они будут получать в органах внутренних дел во временное пользование служебное оружие в порядке, установленном Правительством Российской Федерации. За частными охранными организациями будет сохранено право приобретать и иметь в собственности только служебные пистолеты и револьверы, изготовленные под патрон травматического действия, гражданское оружие самообороны, за исключением огнестрельного гладкоствольного длинноствольного оружия. <br />\r\nВ случае отказа частной охранной организации от права собственности на принадлежащее ей служебное оружие и его безвозмездной передачи в федеральную государственную собственность (оперативное управление органов внутренних дел) данная частная охранная организация имеет право на получение того же оружия во временное (в бессрочное) пользование, если не имеется иных оснований для запрещения или ограничения оборота переданного служебного оружия в соответствии с законодательством Российской Федерации. В этом случае плата за временное пользование указанным служебным оружием не взимается. <br />\r\n<br />\r\n16.2. На уровне закона определена норма обеспечения частных охранных организаций служебным огнестрельным оружием: не может быть более одной единицы на двух частных охранников. <br />\r\n<br />\r\nЗапрещается вооруженная охрана имущества на территориях закрытых административно-территориальных образований, а также приобретение и использование оружия частными охранными организациями, зарегистрированными и (или) расположенными на их территориях. <br />\r\n<br />\r\n17. Впервые дан перечень грубых нарушений осуществления частной охранной деятельности (ст. 11.5 Закона): <br />\r\n<br />\r\n1) нарушение в охранной организации правил оборота оружия, установленных законодательством Российской Федерации, если такое нарушение: <br />\r\n&nbsp;&nbsp;&nbsp; повлекло за собой утрату, хищение оружия либо его использование в преступных целях; <br />\r\n&nbsp;&nbsp; выразилось в выдаче оружия работнику охранной организации, не имеющему разрешения на хранение и ношение служебного оружия, либо лицу, не являющемуся работником данной охранной организации; <br />\r\n<br />\r\n2) нарушение в охранной организации правил оборота оружия и (или) специальных средств, установленных законодательством Российской Федерации, если такое нарушение повлекло за собой нарушение прав, законных интересов, нанесение ущерба здоровью граждан либо иные тяжкие последствия; <br />\r\n<br />\r\n3) оказание лицензиатом охранных услуг в виде вооруженной охраны имущества без заключения соответствующего договора либо без уведомления органов внутренних дел о начале оказания охранных услуг, а также оказание лицензиатом охранных услуг с использованием специальных средств без заключения соответствующего договора и без уведомления органов внутренних дел о начале оказания охранных услуг; <br />\r\n<br />\r\n4) оказание лицензиатом услуг, не предусмотренных имеющейся у него лицензией, либо услуг, не предусмотренных частью третьей статьи 3 настоящего Закона; <br />\r\n<br />\r\n5) необеспечение доступа должностных лиц органа внутренних дел в ходе проведения ими проверочных мероприятий, предусмотренных статьей 20 настоящего Закона, в места хранения оружия, специальных средств и (или) служебной документации, отражающей учет и использование оружия и (или) специальных средств, либо воспрепятствование такому доступу. <br />\r\n<br />\r\n18. В принятом Федеральном законе прописаны дополнительные условия осуществления пропускного и внутриобъектового режимов. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В случае оказания охранных услуг в виде обеспечения внутриобъектового и (или) пропускного режимов персонал и посетители объекта охраны должны быть проинформированы об этом посредством размещения соответствующей информации в местах, обеспечивающих гарантированную видимость в дневное и ночное время, до входа на охраняемую территорию. Такая информация должна содержать сведения об условиях внутриобъектового и пропускного режимов. <br />\r\n<br />\r\n19. Новый Закон содержит нормы, касающиеся использования в охранной деятельности технических средств, форменной одежды, транспортных средств. <br />\r\n<br />\r\n19.1 Требования к использованию технических средств. <br />\r\n19.1.1. Перечень видов технических средств, используемых в охранной деятельности, устанавливается Правительством Российской Федерации. <br />\r\n19.1.2. Из числа технических средств исключены средства радио- и телефонной связи, которые были предусмотрены статьей 5 действующего Закона. <br />\r\n19.1.3. В заявлении о предоставлении лицензии на осуществление частной охранной деятельности соискатель лицензии обязан указать на намерение использовать технические средства и потребность в них. <br />\r\n19.1.4. В случае оказания охранных услуг с использованием видеонаблюдения персонал и посетители объекта охраны должны быть проинформированы об этом посредством размещения соответствующей информации в местах, обеспечивающих гарантированную видимость в дневное и ночное время, до входа на охраняемую территорию. <br />\r\n<br />\r\n19.2. Требования к форменной одежде частных охранников. <br />\r\n19.2.1. Работники частной охранной организации имеют право оказывать охранные услуги в специальной форменной одежде, если иное не оговорено в договоре с заказчиком. <br />\r\n19.2.2. Оказание работниками частной охранной организации услуг в специальной форменной одежде должно позволять определить их принадлежность к конкретной охранной организации. <br />\r\n19.2.3. Специальная форменная одежда и знаки различия работников частных охранных организаций не могут быть аналогичными форме одежды и знакам различия сотрудников правоохранительных органов и военнослужащих, а также сходными с ними до степени смешения. <br />\r\n19.2.4. Порядок ношения специальной форменной одежды при оказании различных видов охранных услуг устанавливается Правительством Российской Федерации. <br />\r\n<br />\r\n19.3. Требования к транспортным средствам, используемым в частной охранной деятельности.&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Специальная раскраска, информационные надписи и знаки на транспортных средствах частных охранных организаций подлежат согласованию с органами внутренних дел в порядке, определяемом Правительством РФ. <br />\r\n<br />\r\n20. В связи с тем, что действие Федерального закона &laquo;О защите прав юридических лиц и индивидуальных предпринимателей при проведении государственного контроля (надзора)&raquo; не распространяется на частную охранную деятельность, в принятом Законе содержатся нормы, регулирующие вопросы, касающиеся проведения проверок. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Так плановая проверка может проводиться не чаще одного раза в три года. Продолжительность ее проведения не должна превышать месяц. О проведении внеплановой проверки в обязательном порядке уведомляется&nbsp; прокурор&nbsp; субъекта Российской Федерации. <br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Внеплановая проверка проводится в следующих случаях: <br />\r\n1) если в результате проведения плановой проверки выявлены нарушения лицензионных требований и условий; <br />\r\n2) если от органов государственной власти и органов контроля (надзора) получена информация о создающем угрозу здоровью и жизни граждан нарушении лицензиатом законодательства Российской Федерации, регламентирующего деятельность частных детективов и частных охранных организаций; <br />\r\n3) если имеются обращения граждан и (или) юридических лиц с жалобами на нарушение их прав и законных интересов действиями (бездействием) лицензиата либо его работников, а также, если получена иная информация, подтверждаемая документами и другими доказательствами, свидетельствующими о наличии такого нарушения.&nbsp; <br />\r\n<br />\r\n21. Административная и уголовная ответственность за правонарушения в сфере охраны. <br />\r\n<br />\r\nПринятый Закон внес существенные изменения в ряд статей Кодекса об административных правонарушениях, Уголовный кодекс Российской Федерации, которые значительно усиливают ответственность за правонарушения в сфере частной охраны и сыска. <br />\r\n<br />\r\n21.1. Изменения, внесенные в Уголовный кодекс РФ. <br />\r\n21.1.1. Внесено дополнение в часть первую статьи 137 УК, предусматривающую ответственность за нарушение неприкосновенности частной жизни. <br />\r\nОтветственность по данной статье предусмотрена за незаконное собирание или распространение сведений о частной жизни лица, составляющих его личную или семейную тайну, без его согласия либо распространение этих сведений в публичном выступлении, публично демонстрирующемся произведении или средствах массовой информации. <br />\r\nСубъектом данного преступления может быть любой гражданин, а не только частный охранник или детектив. <br />\r\nПредложенное дополнение существенно усиливает наказание за данное деяние: до 4 лет лишения свободы, несмотря на то, что ныне действующая статья вообще не предусматривает наказания в виде лишения свободы. <br />\r\nВнесенное дополнение переносит данное деяние в раздел тяжких преступлений со всеми вытекающими из этого правовыми последствиями. <br />\r\n<br />\r\n21.1.2. Статья 138 УК РФ предусматривает ответственность за нарушение тайны переписки, телефонных переговоров, почтовых, телеграфных или иных сообщений. <br />\r\n<br />\r\nЧасть вторая данной статьи, в которую вносится дополнение, предусматривает ответственность за совершение указанного деяния лицом с использованием своего служебного положения или специальных технических средств, предназначенных для негласного получения информации. За это деяние будет предусмотрено наказание в виде лишения свободы на срок от одного года до четырех лет. Действующая часть указанной статьи не предусматривала наказание в виде лишения свободы. <br />\r\n<br />\r\nЧасть 3 указанной статьи, в которую также внесено дополнение, предусматривает ответственность за незаконные производство, сбыт или приобретение в целях сбыта специальных технических средств, предназначенных для негласного получения информации. В новой редакции она будет предусматривать ответственность за незаконное производство, сбыт или приобретение специальных технических средств, предназначенных для негласного получения информации. Наказание оставлено прежнее &ndash; лишение свободы до трех лет. <br />\r\n<br />\r\nСуть данного дополнения состоит в том, что теперь уголовная ответственность за приобретение указанных технических средств наступает независимо от цели их приобретения. <br />\r\n<br />\r\n21.1.3. Претерпела существенные изменения статья 203 УК РФ, предусматривающая ответственность превышение полномочий служащими частных охранных или детективных служб. <br />\r\n<br />\r\nУказанная статья изложена в новой редакции: <br />\r\nСтатья 203.&nbsp;&nbsp;&nbsp; Превышение полномочий частным детективом или&nbsp;&nbsp; работником частной охранной организации, имеющим удостоверение частного охранника, при выполнении ими своих должностных обязанностей <br />\r\n1. Совершение частным детективом или работником частной охранной организации, имеющим удостоверение частного охранника, действий, выходящих за пределы полномочий, установленных законодательством Российской Федерации, регламентирующим осуществление частной охранной и детективной деятельности, и повлекших существенное нарушение прав и законных интересов граждан и (или) организаций либо охраняемых законом интересов общества или государства, -наказывается штрафом в размере от ста тысяч до трехсот тысяч рублей или в размере заработной платы или иного дохода осужденного за период от одного года до двух лет, либо ограничением свободы на срок до двух лет, либо лишением свободы на срок до двух лет с лишением права занимать определенные должности или заниматься определенной деятельностью на срок до двух лет. <br />\r\n2. То же деяние, совершенное с применением насилия или с угрозой его применения либо с использованием оружия или специальных средств и повлекшее тяжкие последствия, - наказывается лишением свободы на срок от трех до семи лет с лишением права занимать определенные должности или заниматься определенной деятельностью на срок до трех лет&raquo;.&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Суть внесенных изменений в указанную статью. <br />\r\n1. Конкретизированы субъекты данного преступления. Ими могут быть: частный детектив, работник частной охранной организации, имеющий удостоверение частного охранника. <br />\r\n2. Уточнены или внесены новые условия наступления ответственности по данной статье: <br />\r\nуказанные субъекты могут быть привлечены к уголовной ответственности по данной статье только при условии совершения ими указанного преступления при выполнении своих должностных обязанностей; <br />\r\nуголовная ответственность наступает только в случае, если это деяние повлекло существенное нарушение прав и законных интересов граждан и (или) организаций либо охраняемых интересов общества или государства. <br />\r\nЗа это деяние предусмотрено наказание, в том числе, в виде лишения свободы на срок до двух лет с лишением права занимать определенные должности или заниматься определенной деятельностью на срок до двух лет. <br />\r\n3. Часть 2 статьи 203 УК предусматривает квалифицированный состав превышения полномочий. <br />\r\nКвалифицирующими признаками являются: <br />\r\n1) применение насилия или угроза его применения; <br />\r\n2) применение оружия или специальных средств; <br />\r\n3) наступление тяжких последствий.&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Непосредственным объектом данного преступления является установленный государством порядок осуществления частной охранной и сыскной деятельности. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дополнительным объектом являются права и законные интересы граждан, организаций, охраняемые законом интересы общества или государства. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Объективную сторону преступления образует совокупность следующих обязательных признаков: <br />\r\nсовершение действий, явно выходящих за пределы полномочий, установленных законодательством, регулирующим охранно-сыскную деятельность; <br />\r\nнаступление вредных последствий в виде существенных нарушений прав и законных интересов граждан или организаций либо охраняемых законом интересов общества или государства. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Субъект преступления &ndash; специальный. Им может быть руководитель охранной, частный охранник и детектив. <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Субъективная сторона преступления выражена прямым умыслом. Цели и мотивы преступления на квалификацию не влияют, но могут быть учтены судом при назначении наказания. <br />\r\n<br />\r\n21.2. Изменения, внесенные в Кодекс об административных правонарушениях. <br />\r\n21.2.1. Существенные изменения внесены в статью 20.16 Кодекса об административных правонарушениях (Незаконная частная охранная и детективная деятельность). <br />\r\n1. В связи с упразднением служб безопасности исключена ответственность за создание в организации службы безопасности без специального разрешения. <br />\r\n2. Части 1 и 2 указанной статьи изложены в новой редакции. Вследствие этого разнесены по разным частям статьи ответственность за незаконную частную охранную и детективную деятельность. При этом наказание за незаконную частную детективную деятельность установлено значительно больше, чем за незаконную частную охранную деятельность. <br />\r\n3. Часть 3 действующей статьи 20.16 дополнена словами &laquo;либо с нарушением установленных законом требований&raquo; и тем самым расширяет основания для привлечения к ответственности по данной статье негосударственные образовательные учреждения. Теперь они несут ответственность не только за осуществление образовательной деятельности без лицензии, но и в случае осуществления ее с нарушением. <br />\r\n4. Увеличен размер штрафа за оказание частных детективных или охранных услуг, либо не предусмотренных законом, либо с нарушением установленных законом требований (вместо штрафа до одной тысячи пятисот рублей, штраф в размере до двух тысяч пятисот рублей). <br />\r\n<br />\r\n21.2.1. Увеличено административное наказание за незаконное использование специальных технических средств, предназначенных для негласного получения информации, в частной детективной или охранной деятельности (статья 20.24 Кодекса об административных правонарушениях). Предельная сумма штрафа, установленная за данное правонарушение, установлена до пяти тысяч рублей, вместо трех тысяч рублей в действующей статье.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n</div>\r\n<p style=\"text-align: justify\">&nbsp;</p>',`date` = '2010-11-04 15:27:46',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '26',`parent` = '0',`position` = '8',`autotext` = '',`page` = 'Частная охрана',`name` = 'Выбираем ЧОП Москва',`title` = 'ЧОП ТАГГЕРД  Москва - предлагает услуги безопасности и охраны, а также технические системы безопасности',`description` = 'Группа частных охранных предприятий \"ТАГГЕРД\" предлагает различные услуги безопасности: от физической охраны объектов и личной охраны, до установки технических систем безопасности. Недорого, надежно, качественно!',`keywords` = 'чоп москва, услуги охраны, выбор чоп, лучший чоп москвы, услуги чоп москва, безопасность, охрана объектов, охранное агентство москва, охранное предприятие',`text` = '<h1><span style=\"font-size: xx-small\"><span><strong><span style=\"color: #000080\">СОВЕТЫ ПО ВЫБОРУ ОХРАННОГО ПРЕДПРИЯТИЯ / &quot;СЕКРЕТЫ&quot; БИЗНЕСА&nbsp;/ ЧОП МОСКВА /</span></strong></span></span></h1>\r\n<h2><span style=\"font-size: large\"><span><span style=\"color: #0000ff\"><span><span><span style=\"font-family: Arial\"><strong>ВЫБИРАЕМ ЧОП&nbsp;(</strong>ЧАСТНОЕ ОХРАННОЕ ПРЕДПРИЯТИЕ<strong>)&nbsp;&nbsp;</strong></span></span></span></span></span></span></h2>\r\n<div>\r\n<div>\r\n<div style=\"text-align: left\">&nbsp;&nbsp;</div>\r\n<div style=\"text-align: center\">\r\n<table cellspacing=\"9\" cellpadding=\"0\" width=\"220\" align=\"left\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img height=\"343\" alt=\"\" width=\"220\" src=\"/files/storage/images/chel.gif\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: justify\"><span style=\"color: #0000ff\"><strong><span><span>Итак,&nbsp; у&nbsp;Вас появилась необходимость в защите</span></span></strong></span><span style=\"color: #000080\"><span style=\"color: #000080\">, и Вы из всех вариантов остановились на частной негосударственной охране, а именно -&nbsp;на частном охранном предприятии&nbsp;(сокращенно ЧОП). Достаточно зайти в&nbsp; Интернет и набрать в поисковой строке&nbsp; &quot;<strong>ЧОП</strong>&quot; или &quot;<strong>охранная фирма</strong>&quot; и вам выдадут&nbsp;огромный список предприятий, оказывающих <strong>охранные услуги</strong>, от&nbsp;количества которых может закружиться голова. Но как из этого списка выбрать &quot;свой&quot; ЧОП, который будет соответствовать всем Вашим требованиям, не подведет и не подставит Вас в тяжелую минуту?&nbsp; Вы, начинаете перебирать рекламу за рекламой, сайт за сайтом, пытаясь разобраться в перспективах сотрудничества с той или иной компанией,&nbsp;стараясь&nbsp;определить&nbsp;уровень профессионализма и надежности,&nbsp; степень объективности хвалебных статей и отзывов, пытаясь взвесить все плюсы и минусы, разобраться в пестрой ценовой политике... Но, это совсем не просто! Многие сайты&nbsp;охранных агентств&nbsp;похожи друг на друга как братья-близнецы, хотя по своему внутреннему содержанию и качеству услуг имеют большие различия!&nbsp; -&nbsp; Мы готовы придти на помощь и помочь Вам разобраться&nbsp;с критериями оценки ЧОП, и даже&nbsp;приоткрыть некоторые &laquo;скрытые&raquo; стороны охранного бизнеса&hellip;\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #0000ff\"><strong>Прежде всего, будем честны и откровенны</strong></span> друг с другом - профессиональная охрана не может стоить дешево! К тому же &nbsp;ЧОП несет материальную ответственность и вынужден дополнительно страховать свои риски. Недорогая охрана, как и дешевый сыр, бывает только в мышеловке. Поэтому не спешите делать ставку на слишком дешевых вариантах и вот почему&hellip; Не секрет, что основные затраты любой ЧОП несет на заработную плату сотрудников, и от того, какой бюджет заложен на охрану, во многом зависит и уровень заработной платы охранников и соответственно подбираемый контингент, и уровень их профессиональной подготовки, и качество услуг.</div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #0000ff\"><strong>Для определения стоимости&nbsp;охраны</strong></span> за 1 месяц, давайте рассмотрим один простой пример. Допустим, Вам требуется&nbsp;один <strong>круглосуточный пост охраны</strong>.&nbsp; Для начала посчитаем количество охранников, требуемых для обеспечения ежедневной непрерывной работы поста. Для этого разделим общее количество часов функционирования поста охраны в месяц, на допустимое количество часов работы одного охранника, в соответствии с Трудовым кодексом РФ, т.е. 732 часа делим на 171 час - получается, четыре человека. Теперь перейдем к заработной плате... &laquo;Обычного&raquo;, лицензированного охранника с минимальным набором специальных навыков, на стационарный <strong>круглосуточный пост</strong>, сегодня можно нанять на зарплату не ниже 15 -20 тысяч рублей (для других категорий охранников зарплата намного выше). Умножим количество охранников используемых на посту на размер заработной платы, получим фонд заработной платы. Для определения общей стоимости поста охраны, прибавим к этой сумме затраты на налоги, накладные расходы (на аренду, оснащение, вооружение, обмундирование, страхование, обучение и т.д.) и хотя бы 10% на прибыль. Как вы видите, цена за охранные услуги получается не уж так мала&hellip;&nbsp;Фирмы конечно выкручиваются, как могут, и даже идут на некоторые нарушения, чтобы снизить себестоимость и привлечь побольше клиентов, но при этом может падать и качество&nbsp;услуг.</div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #0000ff\"><strong>А&nbsp;если Вы все-таки решаете сэкономить</strong></span>, и выбираете самый дешевый вариант, имейте в виду, что в охране у Вас будут либо &laquo;дедушки&raquo;, либо приезжие, работающие вахтовым методом (а то могут подсунуть и гастарбайтеров из зарубежья, от которых вообще можно ожидать чего угодно), тут уж не до качества услуг и гарантий безопасности. Если же Вы желаете видеть на входе высоких, крепких, высокопрофессионально подготовленных парней, придется раскошеливаться. Возможны, конечно, и усредненные варианты, но придется гибко лавировать на соотношении &laquo;цена &ndash; качество&raquo;.</div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\">\r\n<div><span style=\"color: #0000ff\"><strong>С целью снижения расходов,</strong></span>&nbsp;некоторые заказчики полагают, что рационально пригласить для охраны объекта (например, в Москве) ЧОП из региона (например, из Калуги или Пскова). В принципе это возможно, но во-первых ЧОП-у придется брать дополнительное разрешение для работы в Москве, а это не всегда возможно и сильно усложняет жизнь охраны со стороны контролирующих органов, во-вторых появляются сложности с осуществлением контроля и оперативного управления, из-за удаленности администрации ЧОП-а, в-третьих Вы можете рассчитывать только на вахтовый метод работы охранников с проживанием, которое Вы должны будете предоставить, в-четвертых,&nbsp;дешевую вахту сегодня возможно организовать и с любым Московским ЧОП-ом. Так что, если уж объект находится в Москве, выбирайте в&nbsp;разделе объявлений &quot;<a href=\"http://taggerd.su/\"><span style=\"color: #000080\">Услуги по охране объектов</span></a>&quot;&nbsp;&nbsp;<strong>охранные предриятия&nbsp;Москвы</strong>! Использование иных вариантов охраны бессмысленно и более того, чревато последствиями!</div>\r\n</div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\">\r\n<div>\r\n<div><span style=\"color: #0000ff\"><span style=\"font-size: small\"><strong>При выборе охранного предприятия, мы рекомендуем следующий алгоритм решения задачи: </strong></span></span></div>\r\n<div>&nbsp;</div>\r\n<div><strong>1.&nbsp;</strong> <strong>Для начала определите</strong>, какие &laquo;риски&raquo; Вы бы хотели обезопасить и насколько для Вас это важно?</div>\r\n<div><strong>2.</strong> <strong>Оцените защищенность</strong> своего объекта и необходимость&nbsp;в установке &nbsp;дополнительного оборудования безопасности (сигнализации, контроля доступа, видеонаблюдения)&nbsp;и&nbsp;систем инженерной защиты (замков, дверей, решеток, заборов и т.д.). Усиление защищенности объекта может значительно повлиять на количество привлекаемых охранников, а значит и на стоимость охранных услуг.</div>\r\n<div><strong>3.&nbsp;</strong>&nbsp;<strong>Выберите режим безопасности,</strong> который&nbsp;Вы считаете необходимым ввести на Вашем предприятии (низкий, стандартный, усиленный...) и решите, какие услуги<strong> </strong>охраны Вы бы хотели заказать?&nbsp;</div>\r\n<div><strong>4.</strong> <strong>Определите требования</strong>,&nbsp;которые&nbsp;Вы предъявляете к контингенту охранников&nbsp; - рост, возраст, профессиональная подготовка,&nbsp;прописка&nbsp;и др. Аргументируйте необходимость наличия у охранников служебного оружия и спецсредств (ибо это значительно утяжеляет стоимость услуг), использование <a href=\"http://taggerd.su/page/5/21/\"><span style=\"color: #000080\">сторожевых собак</span></a>.&nbsp;</div>\r\n<div><strong>5.</strong> <strong>После проведения&nbsp; анализа</strong> объекта охраны, рассмотрите варианты предприятий оказывающих&nbsp;<strong>услуги безопасности&nbsp;</strong>на рынке, обратите внимание на возраст ЧОП-а, наличие негативной информации, узнайте стоимость и порядок предоставления услуг, попросите выслать Вам договор на охрану и исследуйте его со знающим юристом. Встретьтесь с руководством фирм,&nbsp; узнайте о дополнительных возможностях, условиях, перспективах сотрудничества. Есть смысл провести переговоры с несколькими предприятиями, для получения более полной картины и оценки преимуществ того или иного охранного агентства.</div>\r\n<div><strong>6.</strong> <strong>Возьмите лист бумаги</strong>, выпишите названия охранных фирм, отметьте плюсами и минусами&nbsp;критерии оценки компаний (например, возраст, наличие круглосуточной оперативной службы и ГБР, стоимость услуг, предлагаемый режим охраны, симпатии&nbsp;и негатив&nbsp;и т.д.).&nbsp;</div>\r\n<div><strong>7.</strong> <strong>Взвесьте свои финансовые возможности </strong>и выберите оптимальный вариант охраны. И не скупитесь в мелочах и запомните - расходы на охрану окупаются стабильным бизнесом, спокойными нервами и сохраненным здоровьем!</div>\r\n<div>&nbsp;</div>\r\n</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">\r\n<div style=\"text-align: justify\"><img height=\"50\" alt=\"\" hspace=\"10\" width=\"50\" align=\"left\" src=\"/files/storage/images/info-9.png\" /></div>\r\n</span></div>\r\n</div>\r\n<div style=\"text-align: justify\">\r\n<div style=\"text-align: center\">\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\"><strong><span style=\"font-size: larger\"><span style=\"font-family: Arial\">Если Вам сложно самостоятельно оценить свои риски&nbsp;и угрозы, определить необходимость в тех или иных услугах охраны - обращайтесь к нам, в&nbsp;охранное агентство&nbsp;&quot;ТАГГЕРД&quot;! В&nbsp; соответствии с Вашими возможностями и требованиями, мы сможем подобрать для Вас оптимальный набор услуг безопасности, минимизировать расходную часть, обеспечить максимальное качество и самое главное снять с Вас головную боль, из-за которой Вы к нам обратились!&nbsp;</span></span></strong></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div style=\"text-align: center\"><span style=\"color: #ff0000\"><span style=\"font-size: medium\"><span><strong>ОКАЗАНИЕ&nbsp;УСЛУГ&nbsp;БЕЗОПАСНОСТИ&nbsp;- НАША&nbsp;ПРОФЕССИЯ!</strong></span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"font-size: medium\"><span style=\"color: #0000ff\"><span><strong>\r\n<div style=\"text-align: center\"><span style=\"color: #ff0000\"><span style=\"font-family: Comic Sans MS\"><span><span style=\"font-size: medium\"><strong><font size=\"3\">\r\n<table cellspacing=\"1\" cellpadding=\"9\" width=\"400\" align=\"center\" border=\"1\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\" style=\"text-align: center\"><span style=\"color: #ffffff\"><span style=\"font-size: medium\"><strong><a href=\"http://taggerd.su/page/0/19/\"><span style=\"color: #ffffff\">ЗАКАЗАТЬ&nbsp; УСЛУГИ&nbsp; ОН-ЛАЙН</span></a></strong></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div style=\"text-align: center\">&nbsp;</div>\r\n</font></strong></span></span></span></span></div>\r\n</strong></span></span></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n</span></span></div>\r\n</div>\r\n</div>\r\n</div>',`date` = '2010-11-08 16:51:57',`display` = 'Да';
insert into `taggerd__pages` set `id` = '27',`parent` = '0',`position` = '9',`autotext` = '',`page` = 'Объекты охраны',`name` = 'Охрана объектов',`title` = 'ЧОП \"ТАГГЕРД\" (Москва) - охрана объектов: магазинов, ресторанов, складов, школ, банков, офисов, детских садов,',`description` = 'ЧОП \"ТАГГЕРД\" (Москва) осуществляет профессиональную охрану объектов недвижимости: магазинов, ресторанов, складов, школ, банков, офисов, детских садов, жилых домов, гостиниц, общежитий, гипермаркетов, таможенных терминалов, коттеджных поселков.',`keywords` = 'охрана объектов, профессиональная охрана, охрана москва, безопасность объектов, охрана объектов москва',`text` = '<h1><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: xx-small;\">ОХРАНА ОБЪЕКТОВ В МОСКВЕ /&nbsp; ОХРАНА ОБЪЕКТОВ НЕДВИЖИМОСТИ / ОХРАНА ЗДАНИЙ И КОМПЛЕКСОВ</span></span></h1>\r\n<h2><span style=\"font-size: x-large;\"><span style=\"color: rgb(0, 0, 255);\"><strong>ОХРАНА ОБЪЕКТОВ</strong></span></span></h2>\r\n<div>&nbsp;</div>\r\n<table width=\"700\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/KPPobyekta.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\"><img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/ezopasnostyofisa.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/patrulssobakoy.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/ohrana teatra.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/ohranamagazina-4.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;</div>\r\n<div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><strong>&nbsp;ГРУППА ПРЕДПРИЯТИЙ &quot;ТАГГЕРД&quot;&nbsp;ПРЕДЛАГАЕТ </strong></span></span></div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><strong>&nbsp;ПРОФЕССИОНАЛЬНУЮ ОХРАНУ ОБЪЕКТОВ НЕДВИЖИМОСТИ</strong></span></span></div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><strong>В МОСКВЕ И ПОДМОСКОВЬЕ: </strong></span></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><strong>-&nbsp;&nbsp;</strong><span style=\"color: rgb(0, 0, 128);\"><strong> <a href=\"http://www.taggerd.su/page/27/31/\"><span style=\"color: rgb(0, 0, 128);\">Охрана офисов и&nbsp;бизнес-центров</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>-&nbsp; <a href=\"http://taggerd.su/page/27/35/\"><span style=\"color: rgb(0, 0, 128);\">Охрана коттеджей, загородных домов,&nbsp;дачных и&nbsp;садовых&nbsp;поселков</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>-&nbsp; <a href=\"http://taggerd.su/page/27/32/\"><span style=\"color: rgb(0, 0, 128);\">Охрана торговых центров, магазинов, гипермаркетов, универсамов, супермаркетов,&nbsp; бутиков</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"color: rgb(0, 0, 128);\"><strong>- Охрана вещевых, строительных, продовольственных рынков и базаров</strong></span></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>-&nbsp; <a href=\"http://taggerd.su/page/27/20/\"><span style=\"color: rgb(0, 0, 128);\">Охрана многоквартрных жилых домов, элитных кварталов, ТСЖ, гостиниц, общежитий, отелей</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>-&nbsp; <a href=\"http://taggerd.su/page/27/36/\"><span style=\"color: rgb(0, 0, 128);\">Охрана развлекательных центров, казино, дискотек, ночных и игровых клубов </span></a><br />\r\n</strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>- <a href=\"http://taggerd.su/page/27/36/\"><span style=\"color: rgb(0, 0, 128);\">Охрана ресторанов, кафе, баров</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>-&nbsp; <a href=\"http://taggerd.su/page/27/37/\"><span style=\"color: rgb(0, 0, 128);\">Охрана производственных предприятий и комплексов, цехов, заводов, комбинатов</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>- Охрана складских помещений, таможенных&nbsp;терминалов, логистческих комплексов</strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>-&nbsp; <a href=\"http://taggerd.su/page/27/30/\"><span style=\"color: rgb(0, 0, 128);\">Охрана детских, дошкольных, высших и средних&nbsp;учебных заведений</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>-&nbsp; Охрана банковских и финансовых учреждений, касс, хранилищ, сейфовых комнат&nbsp;и т.д.</strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>- Охрана медицинских учреждений, поликлиник, больниц, госпиталей, аптек</strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>- Охрана </strong></span><span style=\"color: rgb(0, 0, 128);\"><strong> театров, </strong></span><span style=\"color: rgb(0, 0, 128);\"><strong>концертных залов, кинотеатров, музеев, выставочных комплексов</strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>- <a href=\"http://taggerd.su/page/27/33/\"><span style=\"color: rgb(0, 0, 128);\">Охрана транспортных предприятий, автостоянок, гаражей и паркингов</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>- Охрана открытых и замкнутых обширных территорий:&nbsp; парков, домов отдыха, пансионатов, санаториев и т.д.</strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>- Охрана спортивных сооружений, стадионов, фитнес-центров, боулинг-залов</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>-&nbsp; <a href=\"http://taggerd.su/page/27/34/\"><span style=\"color: rgb(0, 0, 128);\">Охрана строительных объектов (стройплощадок)</span></a></strong></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><strong>и другие объекты...</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n</div>\r\n<table width=\"700\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/ohranakrupnyhobyektov.png\" /></td>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/telohraniteli.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/ohranaofisa.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\">&nbsp;<img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/ohranaobyektov-5.jpg\" /></td>\r\n            <td valign=\"middle\" align=\"center\"><img width=\"135\" height=\"100\" alt=\"\" src=\"/files/storage/images/ohranaKPP.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: medium;\"><strong>ОКАЗАНИЕ ОХРАННЫХ УСЛУГ - НАША ПРОФЕССИЯ!</strong></span></span></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>',`date` = '2010-12-22 15:24:01',`display` = 'Да';
insert into `taggerd__pages` set `id` = '28',`parent` = '0',`position` = '5',`autotext` = '',`page` = 'Инструкции',`name` = 'Инструкция по охране объекта',`title` = 'ЧОП ТАГГЕРД (Москва) - типовая инструкция по организации охраны объектов',`description` = 'ЧОП ТАГГЕРД (Москва) предлагает услуги безопасности, разработку комплексной концепции безопасности предприятия, инструкции по организации охраны объектов исходя из укрепленности, местарасположения и особенностей объектов.',`keywords` = 'инструкция по охране объекта',`text` = '<div>\r\n<div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #0000ff\"><span style=\"font-size: large\"><strong>ДОЛЖНОСТНАЯ ИНСТРУКЦИЯ ОХРАННИКА (типовая)</strong></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: medium\"><strong><span style=\"color: #000080\">1. Общие положения</span></strong></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Охранник должен знать:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">- твердо знать свои служебные обязанности, порядок несения службы по охране объекта и его особенности;<br />\r\n- правила внутриобъектового режима;<br />\r\n- структуру предприятия и режим работы его подразделений;<br />\r\n- законы и иные нормативно-правовые акты РФ, регламентирующие охранную деятельность.<br />\r\n- руководящие документы, определяющие организацию пропускного режима на охраняемом объекте;<br />\r\n- правила досмотра вещей и личного досмотра граждан согласно законодательства РФ;<br />\r\n- руководителей объекта, их служебный и домашний телефоны (адреса), порядок их вызова;<br />\r\n- номера телефонов дежурных служб института, руководства охраны, дежурной службы милиции, скорой помощи, пожарной охраны и порядок их вызова.<br />\r\n- настоящую инструкцию, приказы, другие нормативные документы, регламентирующие организацию работы по охране объекта.<br />\r\n- образцы различных видов пропусков, накладных, подписей должностных лиц, имеющих право давать распоряжения на проход на охраняемую территорию отдельных категорий работников, которым предоставлено право прохода по ведомственным служебным удостоверениям;<br />\r\n- порядок организации пропускного режима;<br />\r\n- должностных и материально ответственных лиц, от которых принимает под охрану объекты;<br />\r\n- правила проверок вывозимых грузов.<br />\r\n- порядок пропуска аварийных бригад при стихийных бедствиях, пожарах, аварий и других чрезвычайных ситуациях;<br />\r\n- правила досмотра (осмотра) вещей и личного досмотра, а также проверок вывозимого (ввозимого) груза;<br />\r\n- правила применения оружия, радиосредств и переговорных устройств;<br />\r\n- места расположения первичных средств пожаротушения, связи, порядок пользования ими;<br />\r\n- уязвимые технологические объекты на охраняемом участке, выход из строя которых способен привести к аварийной ситуации.<br />\r\n- места возможного сокрытия и признаки средств террора в автомобильном транспорте и ручной клади при реализации задач пропускного режима;<br />\r\n- назначение применяемых на предприятии средств охранно-пожарной сигнализации, порядок реагирования на ее срабатывание;<br />\r\n- порядок задержания лиц, совершивших хищение, посторонних лиц несанкционированно проникших на территорию объекта, оформления на них материалов задержания (в рамках установленной компетенции);<br />\r\n- правила техники безопасности и производственной санитарии;<br />\r\n- правила применения индивидуальных средств защиты;<br />\r\n- расположение первичных средств пожаротушения и связи, порядок пользования ими;<br />\r\n- правила пользования техническими средствами охранной и охранно-пожарной сигнализации.<br />\r\n- общие принципы оказания первой медицинской помощи.<br />\r\n- правила и нормы охраны труда, техники безопасности и противопожарной защиты.<br />\r\nОхранник подчиняется непосредственно _______________ (начальнику службы безопасности (охраны) иному должностному лицу).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Охраннику запрещается:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">- допускать на территорию охраняемого объекта посторонних лиц, не имеющих на это права, прибывших без документов или в неустановленное время, а также лиц в нетрезвом состоянии или проносящих спиртные напитки, не допускать на объект граждан, имеющих наркотические, взрывоопасные или ядовитые вещества, огнестрельное или холодное оружие;<br />\r\n- покидать пост без разрешения старшего смены, в случае внезапного заболевания, необходимо доложить дежурному _______________ и продолжать нести службу до прибытия замены.<br />\r\n- выходить с оружием и специальными средствами за пределы охраняемого объекта без специального на то разрешения;<br />\r\n- применять служебное оружие, специальные средства и физическую силу в случаях не предусмотренных законом:<br />\r\n1) в отношении женщин,<br />\r\n2) лиц с явными признаками инвалидности,<br />\r\n3) несовершеннолетних, когда их возраст очевиден или известен охраннику.<br />\r\nКроме случаев;<br />\r\n1) оказания ими вооруженного сопротивления,<br />\r\n2) совершения вооруженного либо группового нападения, угрожающего жизни охранника или охраняемому имуществу,<br />\r\n3) а также при значительном скоплении людей, когда от применения огнестрельного оружия могут пострадать посторонние лица.<br />\r\nО каждом случае применения огнестрельного оружия охранник обязан незамедлительно информировать орган внутренних дел по месту применения оружия.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">- самостоятельно изменять график дежурств;<br />\r\n- находиться на посту в нерабочее время;<br />\r\n- нарушать установленную форму одежды;<br />\r\n- передавать своё удостоверение личности охранника для использования его другими лицами;<br />\r\n- осуществлять иные охранные услуги (помимо охраны имущества) с огнестрельным оружием;<br />\r\n- поручать охрану объекта, даже на короткое время, другим лицам;<br />\r\n- передавать посторонним лицам ключи от охраняемых помещений;<br />\r\n- передавать или предъявлять кому бы то ни было табельное оружие, специальные средства, за исключением прямых начальников, чистить, разбирать, оставлять оружие и боеприпасы без присмотра;<br />\r\n- хранить на посту личные вещи.<br />\r\n- читать, курить на посту, отвлекаться от несения службы.<br />\r\n- допускать нахождение на посту и в служебном помещении посторонних лиц.<br />\r\n- использовать служебную телефонную связь в личных целях.<br />\r\n- вести посторонние разговоры, в том числе и по средствам мобильной связи, заниматься делами, не связанными с несением службы.<br />\r\n- принимать и передавать кому-либо какие-либо предметы, пакеты, свертки и т.п.<br />\r\n- выдавать себя за сотрудников правоохранительных органов.<br />\r\n- собирать сведения, связанные с личной жизнью, с политическими и религиозными убеждениями отдельных лиц.<br />\r\n- разглашать полученные в процессе служебной деятельности сведения, касающиеся порядка охраны объекта, способах и вида охраны, режима несения службы, видов вооружения, спецсредств, а также сведения являющииеся коммерческой тайной, разглашать конфиденциальную информацию, использовать ее в интересах &laquo;третьих&raquo; лиц.<br />\r\n- сообщать посторонним лицам какие-либо сведения об обстановке на объекте, пароль, а также присвоенные пультовые номера;<br />\r\n- разглашать сведения об особенностях объекта, порядке хранения ценностей, токсичных веществ и организации охраны;<br />\r\n- давать информацию о сотрудниках предприятия, адреса и телефоны сотрудников;<br />\r\n- осуществлять видео- и аудио запись, фото и кино съёмку в служебных или иных помещениях без письменного согласия на то должностных и частных лиц.<br />\r\n- прибегать к действиям, посягающим на права и свободы граждан.<br />\r\n- использовать методы сыска (осуществлять опрос граждан и должностных лиц, наводить справки, производить внешний осмотр строений, других объектов, производить наблюдения для получения необходимой информации).<br />\r\n- скрывать от правоохранительных органов ставшие им известные факты готовящихся или совершенных преступлений.<br />\r\n- совершать действия, ставящие под угрозу жизнь, здоровье, честь и достоинство сотрудников, а также наносящие ущерб их имуществу и материал, ценностям.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;&nbsp;</span><span style=\"font-size: medium\"><strong><span style=\"color: #000080\">2. Должностные обязанности охранника</span></strong></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">1. Охранник добросовестно выполняет свои служебные обязанности и несёт службу по охране объекта.<br />\r\n2. Своевременно прибыв на место службы, в присутствии сотрудника охраны предыдущей смены осмотривает охраняемый объект (путём обхода внешнего и внутреннего), обращая особое внимание на:<br />\r\n- целостность дверных и оконных запоров;<br />\r\n- исправность средств телефонной связи<br />\r\n- состояние внутреннего и наружного дежурного освещения;<br />\r\n- наличие служебной документации;<br />\r\n- исправность системы заграждений (решетки, жалюзи и т.п.)<br />\r\n- целостность мастичных печатей или пломб на отдельных помещениях, витринах и сейфах;<br />\r\n- целостность оконных стекол и закрытие окон и форточек;<br />\r\n- наличие и комплектность пожарного инвентаря, фонарей, прожекторов, ламп и иных средств освещения.<br />\r\n3. Если состояние объекта соответствует условиям приема, принимает его под охрану в соответствии с порядком, установленным договором.<br />\r\n4. Принимает под охрану оборудованные сигнализацией обособленные помещения от материально ответственных лиц;<br />\r\n5. Следит за оперативной обстановкой на охраняемом объекте и прилегающей территорией в зоне видимости;<br />\r\n6. Совершает действия по предупреждению и пресечению правонарушений на охраняемых объектах.<br />\r\n7. В случае обнаружения взлома дверей, окон, запоров, замков, сорванных пломб и печатей или других нарушений на охраняемом объекте, а также при срабатывании охранно-пожарной сигнализации &ndash; тщательно осмотривает объект, и при наличии злоумышленников принимает меры к их задержанию, о чем немедленно сообщает в органы милиции. С прибытием на место происшествия оперативной группы милиции передает ей задержанных и продолжаает дежурство;<br />\r\n8. В случае обнаружения отсутствия пломб или нарушения их целостности немедленно ставит в известность начальника службы безопасности в известность и принимает меры к усиленной охране данного объекта до приезда ответственных лиц;<br />\r\n9. Задерживает лиц, пытающихся незаконно вывезти (вынести) материальные ценности с охраняемого объекта, а также подозреваемых в совершении правонарушений. После задержания вносит в журнал задержаний : Ф.И.О. задержанных, примечание о характере правонарушения и передает задержанных в органы внутренних дел;<br />\r\n10. Выполняет требования законов, приказов, распоряжения и инструкции.<br />\r\n11. Своевременно получает закрепленное оружие, своевременно сдает его по окончании работы, немедленно докладывать руководству ЧОП о случае задержки сдачи оружия и причинах задержки, докладывает о состоянии дел на объекте и сохранности оружия при заступлении на пост и окончании несения службы;<br />\r\n12. Правильно применет специальные средства и оружие, строго соблюдает меры безопасности при обращении с ним;<br />\r\n13. Осуществляет контроль за работой установленных на предприятии приборов охранной и охранно-пожарной сигнализации.<br />\r\n14. Сообщает об их срабатывании начальнику караула (сторожевой группы), дежурному по объекту, а при необходимости - в орган внутренних дел или в пожарную часть.<br />\r\n15. Выясняет причины срабатывания сигнализации и принимает меры к задержанию нарушителей или ликвидации пожара.<br />\r\n16. Осуществляет проверку документов у проходящих на охраняемый объект лиц и контролирует ввоз и вывоз материальных ценностей.<br />\r\n17. Осуществляет проверку правильности оформления путевых листов выезжающей техники с последующей регистрацией в журнале.<br />\r\n18. Допускает въезд и выезд на территорию объекта постороннего транспорта по указанию директора, начальника охраны.<br />\r\n19. Допускаемый выезд (въезд) автотранспорта вне рабочее время, в выходные и праздничные дни только по указанию директора, автотранспорта выезжающего на устранение аварий - по распоряжению диспетчера, начальника охраны.<br />\r\n20. Осмотр транспортного средства производит только при полной его остановке и неработающем двигателе, чтобы исключить травматизм и несчастные случаи. При пропуске транспортного средства через въездные ворота, следит за тем, чтобы створки ворот были закреплены.<br />\r\nПримечание: охрана не может настаивать на осмотре (т.к. охранники не имеют закрепленного в Законе права досмотра), и лица, не согласившиеся добровольно предъявить ввозимое (вывозимое) имущество не допускаются (не выпускаются) с объекта, поскольку это требование установлено Заказчиком, согласовавшим данную инструкцию, как владеющим (управляющим) соответствующим имуществом;<br />\r\n21. Осуществляет допуск посетителей по разовым пропускам по согласованию с должностными лицами к которым они направляются.<br />\r\nРегистрирует всех посторонних лиц, прибывших на объект, в &laquo;Книге учета регистрации посетителей&raquo;, отмечая время прибытия и убытия, наличие ручной клади;<br />\r\n22. В случае прибытия на объект сотрудников правоохранительных органов (налоговая полиция, ФСБ, МВД и т.п.) сотрудник охраны обязан представиться, узнать цель их визита, проверить документы прибывших, обязательно зафиксировав данные (Ф.И.О., звание, наименование структуры) в &laquo;Книге приема и сдачи дежурства&raquo;. После проверки документов узнать у сотрудников правоохранительных органов номер телефона их непосредственного руководителя либо дежурного и перезвонить по нему, чтобы убедиться в том, что это те лица, которые направлены. В случае отказа &ndash; потребовать письменное предписание на проверку. Немедленно доложить о прибывших руководству, дежурному _______________ и действовать в соответствии с их указаниями.<br />\r\n23. Осуществляет охрану производственной территории путем обхода.<br />\r\n24. Своевременно сообщает начальнику охраны о всех происшествиях, замечаниях, недостатках и нарушениях имевших место в процессе дежурства.<br />\r\n25. Ведет документацию в соответствии с образцами.<br />\r\n26. Поддерживает санитарный порядок в служебном помещении и на закрепленной территории.<br />\r\n27. Допускает в помещение КПП только директора, его заместителей, начальника охраны.<br />\r\n28. Участвует в ликвидации пожара, предупреждении угрозы взрыва.<br />\r\n29. При несении дежурства обращает внимание на свертки, пакеты, коробки, оставленные без присмотра. При малейшем подозрении на возможность диверсии незамедлительно сообщает о находке начальнику службы безопасности или по тел. 02<br />\r\n30. Оказывает доврачебную помощь пострадавшим.<br />\r\n31. Немедленно докладывать начальнику охраны о происшествиях на объектах согласно перечню.<br />\r\n32. При приёме смены охранник обязан:<br />\r\n- ознакомиться с распоряжениями за время прошедшее с его предыдущего дежурства, какие объекты сданы под охрану;<br />\r\n- проверить состояние ограждения объекта, в случае порывов принять меры к их устранению.<br />\r\n- убедиться в исправности средств связи, сигнализации, освещении, пожаротушения.<br />\r\n- принять имеющуюся документацию (инструкции, журналы, план действий в случае возникновения чрезвычайных ситуаций, материальные ценности др.) согласно описи.<br />\r\n- оформить приём и сдачу дежурства соответствующими записями.<br />\r\n- не покидать места несения службы без сдачи смены.<br />\r\n33. При невозможности заступления на службу, охранник обязан сообщить об этом руководству охранного предприятия не менее чем за 18 часов до начала смены охраны.<br />\r\n34. При несении службы охранник обязан докладывать руководству охранного предприятия в рапортах на имя руководителя о всех нарушениях или о происшествиях, о которых ему стало известно по тем или иным причинам, своевременно ставить в известность руководителей предприятия, а также руководство Заказчика обо всех обстоятельствах, угрожающих сохранности материальных ценностей, изменениях в оперативной обстановке вокруг объекта охраны. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"font-size: medium\"><span style=\"color: #000080\">Права охранника</span></span></strong></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">\r\n<div>Охранник имеет право:</div>\r\n<div>1. Знакомиться с проектами решений руководства, касающимися его деятельности.<br />\r\n2. Требовать от руководства оказания содействия в исполнении им его должностных обязанностей и прав.<br />\r\n3. В пределах своей компетенции вносить предложения непосредственному руководителю об устранении выявленных в процессе исполнения своих должностных прав и обязанностей недостатков.<br />\r\n4. Производить досмотр транспортных средств на предмет соответствия вывозимого груза с указанным в сопроводительных документах.<br />\r\n5. Задерживать граждан вывозящих материальные ценности без соответствующих документов, а также лиц подозреваемых в совершении правонарушений и преступлений на охраняемом объекте с составлением акта задержания;<br />\r\nЗадерживать лиц с признаками алкогольного и наркотического опьянения.<br />\r\n6. Изымать пропуска с просроченным сроком действия, не установленного образца, с признаками подделки, несоответствующие личности предъявителя.<br />\r\n7. Разрешать ввоз (вывоз), внос (вынос) ТМЦ, имущества (оборудования) на основании материального пропуска и ТТН (товарно-транспортной накладной), подписанного должностными лицами предприятия с визой директора по безопасности, предварительно сверив образец подписи по списку должностных лиц, имеющих право подписывать материальные пропуска. На оборотной стороне пропуска делать отметку о времени ввоза (вывоза), вноса (выноса) материальных средств, заверив её подписью с указанием своей фамилии и инициалов. Изъять один экземпляр ТТН и материальный пропуск. Регистрировать ТТН в &laquo;Книге учета выезда (въезда) автомобильного (железнодорожного) транспорта&raquo;. По окончании дежурства, изъятые ТТН и материальные пропуска сдавать начальнику смены охраны.<br />\r\n8. Применять огнестрельное оружие в следующих случаях:<br />\r\n- для отражении нападения, когда его собственная жизнь подвергается непосредственной опасности,<br />\r\n- для отражения группового или вооруженного нападения на охраняемое имущество,<br />\r\n- для предупреждения (выстрелом в воздух) о намерении применить оружие, а так же для подачи сигнала тревоги и вызова помощи.</div>\r\n<div>Условия и пределы применения специальных средств.</div>\r\n<div>Применению специальных средств должно предшествовать предупреждение о намерении их применения и предоставление достаточного времени для выполнения требований охранника, кроме тех случаев, когда промедление в их использовании создает непосредственную опасность жизни и здоровью или может повлечь за собой иные тяжкие последствия.<br />\r\nВ ситуациях, когда применения специальных средств неизбежно, охранник обязан проявить сдержанность, действовать, исходя из складывающейся обстановки и той цели, которая должна быть достигнута, сводя к минимуму возможность причинения ущерба имуществу и нанесения телесных повреждений.<br />\r\nПри применении специальных средств должны быть обеспечены: предоставление до врачебной помощи лицам, получившим телесные повреждения и уведомление о происшедшем в возможно короткий срок органов здравоохранения и внутренних дел.<br />\r\nОхранники имеют право применять специальные средства в следующих случаях:<br />\r\n- для отражения нападения, непосредственно угрожающего их жизни и здоровью;<br />\r\n- для пресечения преступления против охраняемой ими собственности, когда правонарушитель оказывает физическое сопротивление.<br />\r\nЗапрещается применять специальные средства в отношении женщин с видимыми признаками беременности, лиц с явными признаками инвалидности и несовершеннолетних, когда их возраст очевиден или известен охраннику, кроме случаев оказания ими вооруженного сопротивления, совершения группового либо нападения угрожающего жизни здоровью охранника или охраняемой собственности.</div>\r\n<div>Особенности применения некоторых видов специальных средств:<br />\r\nРезиновые палки &ndash; запрещается нанесение ударов по голове, шее и ключичной области, животу, половым органам.<br />\r\nНаручники &ndash; требуют периодической (не реже, чем один раз в два часа) проверки состояния фиксации замков.<br />\r\nСлезоточивые вещества &ndash; запрещается прицельная стрельба по правонарушителям, повторное применение их в пределах зоны поражения в период действия этих веществ.</div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"font-size: medium\"><strong>Ответственность</strong></span></div>\r\n<div>Охранник несёт ответственность:</div>\r\n<div>1. За ненадлежащее исполнение или неисполнение своих должностных обязанностей предусмотренных настоящей должностной инструкцией.<br />\r\n2. За правонарушения, совершенные в процессе своей деятельности - в пределах, определенных действующим административным, уголовным и гражданским законодательством РФ.<br />\r\n3. За причинение материального ущерба - в пределах, определенных действующим трудовым и гражданским законодательством РФ.<br />\r\n4. За соблюдение действующих инструкций, приказов и распоряжений по сохранению коммерческой тайны и конфиденциальной информации.<br />\r\n5. За выполнение правил внутреннего распорядка, правил ТБ и противопожарной безопасности.<br />\r\n6. За сохранность и исправность оружия и экипировки, выданных для несения службы.</div>\r\n<div>Начальник охраны _________________</div>\r\n<div>С инструкцией ознакомлен: __________________</div>\r\n<div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: #0000ff\"><span style=\"font-size: large\"><strong>ИНСТРУКЦИЯ </strong></span></span></div>\r\n<div><span style=\"font-size: medium\"><span style=\"color: #0000ff\"><span><strong>ДЕЙСТВИЯ ОХРАННИКОВ ПРИ ВОЗНИКНОВЕНИИ ЧРЕЗВЫЧАЙНОЙ СИТУАЦИИ</strong></span></span></span></div>\r\n<div>&nbsp;<br />\r\n<br />\r\n&nbsp;<span style=\"font-size: small\"><span style=\"\"><strong>ДЕЙСТВИЯ СОТРУДНИКА ОХРАНЫ ПРИ ОБНАРУЖЕНИИ ВУ:</strong></span></span></div>\r\n<div>1. Обнаружить (установить местонахождение) ВУ.<br />\r\n2. Эвакуировать весь персонал.<br />\r\n3. Сообщить органам власти (отделение милиции, военные комендатуры, спец.отделы при ОМОН, ФСБ, МЧС).<br />\r\n4. Принять меры для локализации (снижения угрозы) возможных последствий взрыва.<br />\r\n5. Обеспечить изоляцию места возможного нахождения ВУ &ndash; установить ограждение, щитки, указательные надписи с дистанцией безопасного удаления.<br />\r\n6. Обеспечить контроль за допуском к месту возможной закладки до прибытия специалистов с необходимыми техническими средствами.</div>\r\n<div>Желательно обесточить внешние источники электропитания и газоснабжения. От места возможной закладки убрать предметы, которые при взрыве могут создать дополнительный поражающий эффект за счет разлетающихся осколков.<br />\r\nИногда преступники применяют классический прием &ndash; провоцирование. Суть его заключается в следующем: на объект подкидывается пакет-&quot;пустышка&quot;. Это может повторяться неоднократно, при этом преследуются две цели. Во-первых, проверить реакцию охраны, заметила ли она посторонний предмет и каким образом отреагировала. Во-вторых, при неоднократных ложных тревогах притупляется бдительность (если специалисты-взрывотехники и будут выезжать по вашему адресу два-три раза по ложным вызовам, то в последующем могут проигнорировать очередной вызов). Охранники, уверенные, что очередная находка &ndash; обычная &quot;пустышка&quot;, не примут меры к эвакуации людей и выносу ценного оборудования, и в это время прогремит взрыв. В связи с этим каждая ложная тревога требует проведения дополнительных, в том числе оперативных разведывательных мероприятий. Приближаясь к подозрительному на наличие ВУ предмету, необходимо учитывать тот факт, что замедлитель (таймер) настраивается, как правило, на какое-либо четное время (например, 10.00, 11.00, 12.00, и т.д., или 10.20, 10.30, 10.40 и т.д.) Поэтому если есть необходимость проверить подобные закладки (например, для локализации последствий взрыва, открытия окон и дверей, обесточивания, ограждения ВУ блокираторами, выноса ценного оборудования и т.д.), то лучше это делать в нечетные промежутки времени (например, 10.11-10.14, 10.16-10.19 ..)</div>\r\n<div>Расстояние (приблизительное) удаления людей от мест обнаружения ВУ, то есть дистанция кордона, должно составлять не менее 100 м &ndash; для СВУ, попадающих под &quot;почтовые отправления&quot;, и 200 м &ndash; для транспортных (в том числе, установленных под автомобиль).</div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"font-size: small\"><span style=\"\"><strong>ДЕЙСТВИЯ СОТРУДНИКА ОХРАНЫ ПРИ ПОЖАРЕ</strong></span></span></div>\r\n<div>Оказавшись на месте возникновения пожара до прибытия пожарных, следует в первую очередь отключить электрические приборы, накрыть их одеялом, пальто или курткой. Если загорелась электропроводка, необходимо вывернуть пробки или отключить электропитание на щите. Постарайтесь сбить огонь с горящих предметов. Сбросьте на пол и затопчите загоревшиеся занавески и другие предметы.<br />\r\n2. Необходимо организовать присутствующих граждан для доставки воды, тушения огня песком, землей и другими подручными средствами используя при этом имеющиеся первичные средства тушения пожара (багры, лопаты, кошму, огнетушители И Т.Д.).<br />\r\n3. Следует срочно вызвать пожарных, а при необходимости другие службы.<br />\r\n4. Важно оперативно организовать эвакуацию граждан из опасных зон задымления, обвала, возможного взрыва и т.п. В первую очередь вывести людей с верхних этажей, так как дым всегда устремляется вверх, и огонь может перекрыть пути вывода людей.<br />\r\n5. На месте пожара необходимо прикрывать нос и рот мокрой тканью (платком, шарфом, тряпкой). Оказавшись в задымленной зоне или проходя через нее, следует нагнуться пониже, а при сильном дыме - передвигаться ползком.<br />\r\n6. Оказавшись в изолированном помещении верхних этажей, отрезанном от путей эвакуации огнем и дымом, важно заделать щели влажными тряпками, одеждой, дышать следует нижними слоями воздуха. Окна лучше не открывать. Убедившись, что прибыла помощь и вас могут спасти, можно воспользоваться окном. При наличии балкона стойте на нем и зовите на помощь.<br />\r\n7. Открывая дверь, необходимо убедиться, что она не нагрелась. Всегда существует опасность, что из-за открытой двери вырвутся клубы дыма и огня. Поэтому открывать двери надо медленно, сидя на корточках или стоя у стены рядом с дверью, лицом в противоположную сторону во избежание ожогов.<br />\r\n8. Следует предпринять попытку покинуть помещение по пожарной лестнице, через балкон, по веревке или связанным гардинам ли предметам одежды.<br />\r\n9. Прыгать из окон 2-го этажа можно лишь убедившись, что внизу нет опасных предметов и камней. Перед прыжком необходимо сомкнуть зубы, чтобы не прикусить язык, и приземляться на обе ступни при полусогнутых коленях, но не пятки и не на носки. Если под рукой находятся подушки, матрасы или иные предметы, смягчающие удар о землю, следует бросить их на место вашего приземления. Прыжки с более высоких этажей опасны для жизни.<br />\r\n10. Необходимо обязательно проверить, не остались ли люди в других помещениях.<br />\r\n11. Нельзя входить в опасную зону при плохой видимости (10 м).<br />\r\n12. Следует опасаться оборванных проводов, в том числе после ликвидации пожара. Оказавшись возле такого провода, важно проходить осторожно, а чтобы избежать поражения электрическим током, необходимо применять способ движения &ldquo;нога к ноге&rdquo;, делать шаги не длиннее полуступни. Это необходимо для предупреждения так называемого шагового &ldquo;замыкания&rdquo;.<br />\r\n13. По прибытии пожарных необходимо выполнять все их команды.<br />\r\n14. Важно использовать внутренние пожарные краны, огнетушители, направляя струю на горящую поверхность, начиная сверху. Тушение горючих жидкостей водой не допустимо &ndash; это лишь увеличит очаг огня.<br />\r\n15. Покидая зону огня, следует оставить пожарные краны открытыми.<br />\r\n16. Выходя из опасной зоны, необходимо идти навстречу ветру (сквозняку). В зоне сильных промышленных пожаров не следует приближаться к огню, так как возникает движение воздуха в сторону увеличения очага пожара, образуя эффект затягивания предметов в огонь.</div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"font-size: small\"><span><strong>ДЕЙСТВИЯ СОТРУДНИКА ОХРАНЫ ПРИ ОТРАЖЕНИИ ВООРУЖЕННОГО НАПАДЕНИЯ НА ОБЪЕКТ</strong></span></span>&nbsp;</div>\r\n<div>В случае необходимости применения огнестрельного оружия при отражении вооруженного нападения на объект сотрудник охраны должен руководствоваться ст. 17, 18 Закона РФ &ldquo;О частной детективной и охранной деятельности в Российской Федерации, ст. 37, 39 УК, ст. 24 Закона РФ &ldquo;Об оружии&rdquo;.<br />\r\n2. В случае нападения на объект охранник обязан подать сигнал &ldquo;ТРЕВОГА&rdquo;, предпринять попытку по блокированию всех выходов, прекратить доступ возможных клиентов в помещения и принять меры к отражению вооруженного нападения.<br />\r\n3. Организовать оповещение центрального поста охраны о нападении. Охранник центрального поста немедленно сообщает о случившемся в милицию, руководству охранного предприятия оперативному дежурному, далее действует согласно поступившим указаниям.<br />\r\n4. Охранник поста должен впустить прибывший наряд милиции, указать место происшествия, дать краткую оперативную информацию и остаться у входа в здание (на территорию).</div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"font-size: small\"><strong>ДЕЙСТВИЯ СОТРУДНИКА ОХРАНЫ ПРИ ЗАХВАТЕ ЗАЛОЖНИКА НА ОБЪЕКТЕ</strong></span></div>\r\n<div>1. При захвате заложника важно в кратчайшее время получить максимально возможную информацию по существу дела. Оповестить ближайшее отделение милиции, сообщить дежурному отделения милиции имеющуюся информацию и при необходимости указать кратчайший маршрут до объекта. Оповещение других инстанций и должностных лиц осуществляется с строгом соответствии с планом оповещения.<br />\r\n2. Охранник принимает меры к локализации места противоправного посягательства и допускает к нему только сотрудников правоохранительных органов.<br />\r\n3. Охранник поста организует вывод сотрудников и клиентов в безопасное место, встречу сотрудников милиции.</div>\r\n<div>&nbsp;</div>\r\n</span></div>\r\n</div>\r\n<div><span style=\"color: #0000ff\"><span style=\"font-size: large\"><strong>ИНСТРУКЦИЯ (типовая)</strong></span></span></div>\r\n<div><span style=\"color: #0000ff\"><span style=\"font-size: medium\"><span><strong>ПО ОРГАНИЗАЦИИ ОХРАНЫ ОБЪЕКТА</strong></span></span></span></div>\r\n<div>&nbsp;</div>\r\n<div><strong><span style=\"color: #000080\">1. ВВЕДЕНИЕ</span></strong></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">Целью данной инструкции является упорядочение деятельности по обеспечению безопасности охраняемых объектов (далее &ndash; объект). Инструкция подготовлена с учетом нормативно-правовой базы и федерального законодательства.<br />\r\nТиповая инструкция предназначена для организации антитеррористической защиты объектов, а также для контролирующих, надзирающих и исполнительных органов при изучении и проверке обеспечения охраны и безопасности объектов.</span></div>\r\n<div><span style=\"color: #000080\">Типовая инструкция не носит нормативного характера, вместе с тем она устанавливает общие подходы к обеспечению защиты объектов, в т.ч. их инженерно-технической укрепленности, порядку организации охраны, осуществления пропускного и внутриобъектового режимов, а также ведению соответствующей документации.</span></div>\r\n<div><span style=\"color: #000080\">Основными задачами охраны являются:<br />\r\n- защита охраняемых объектов, предупреждение и пресечение противоправных посягательств и административных правонарушений на охраняемых объектах;<br />\r\n- обеспечение на охраняемых объектах пропускного и внутриобъектового режимов;<br />\r\n- участие в локализации и ликвидации возникших ЧС, в том числе вследствие диверсионно-террористических акций.</span></div>\r\n<div><span style=\"color: #000080\">Система охраны объекта включает в себя совокупность сил и средств для выполнения задач по охране и обороне объекта. Она должна соответствовать технологическим особенностям охраняемого объекта, уровню его оборудования инженерно-техническими средствами охраны (ИТСО), обстановке в его окружении и обеспечивать наиболее эффективное и экономически рациональное использование имеющихся сил и средств.<br />\r\nСистема охраны объекта должна строиться эшелонированно: на подступах к нему (в охранной зоне, определяемой ведомственными регламентирующими документами), по его периметру, на КПП и на наиболее важных производственных (технологических) уязвимых участках, обеспечивающих устойчивое функционирование объекта.</span></div>\r\n<div><span style=\"color: #000080\">Система и способ охраны отражаются в документации по организации охраны объекта.<br />\r\nВажная роль принадлежит системе профилактических мероприятий, которая, как правило, включает в себя:<br />\r\n- регулярное получение информации о состоянии защищенности объекта, выделенных участков (зон);<br />\r\n- своевременное вскрытие недостатков и нарушений технологических регламентов работы ИТСО,<br />\r\n- выявление лиц, пытающихся без видимых оснований или при подозрительных обстоятельствах проникнуть на объект. </span></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><strong><span style=\"color: #000080\">2. ОБЕСПЕЧЕНИЕ ОХРАНЫ ОБЪЕКТА</span></strong></div>\r\n<div><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div><span style=\"color: #000080\">2.1. Общие положения.</span></div>\r\n<div><span style=\"color: #000080\">2.1.1. Ответственность за обеспечение безопасности объекта несет его руководитель.</span></div>\r\n<div><span style=\"color: #000080\">2.1.2. Подразделения охраны несут ответственность согласно договору на охрану объекта.</span></div>\r\n<div><span style=\"color: #000080\">2.1.3. Под охраной объекта подразумевается комплекс мер, направленных на своевременное выявление угроз и предотвращение нападения на охраняемые объекты, совершения террористического акта, других противоправных посягательств в т.ч. экстремистского характера, а также возникновения чрезвычайных ситуаций.</span></div>\r\n<div><span style=\"color: #000080\">2.1.4. Охрана объектов может осуществляться милицейскими, военизированными (ВОХР) и сторожевыми подразделениями вневедомственной или ведомственной охраны, частными охранными предприятия (ЧОП) (далее &ndash; наряд охраны), с помощью технических средств посредством вывода сигналов тревоги на местные (автономные) пульты охраны с обязательной подачей экстренного сигнала тревоги в дежурную часть территориального органа внутренних дел (ДЧ ОВД) или на пункты централизованной охраны (ПЦО) территориальных ОВО либо сочетанием этих видов охраны.</span></div>\r\n<div><span style=\"color: #000080\">2.1.5. На объекте в соответствии с законодательством Российской Федерации может функционировать внутриобъектовая (собственная) служба безопасности, которая координирует свою деятельность с деятельностью подразделений охраны либо выполняет их функции.</span></div>\r\n<div><span style=\"color: #000080\">2.1.6. Для несения службы по охране объектов подбираются сотрудники (работники) ВОХР, сторожевой охраны, ЧОПов имеющие соответствующую подготовку, годные по состоянию здоровья, своим моральным и деловым качествам к этой работе.</span></div>\r\n<div><span style=\"color: #000080\">2.1.7. В целях повышения уровня защиты охраняемых объектов согласно договору нарядами охраны могут применяться служебные собаки.</span></div>\r\n<div><span style=\"color: #000080\">2.1.8. Лица, имеющие непосредственный доступ к организации системы охраны, предупреждаются администрацией объекта о недопустимости разглашения сведений о режиме охраны объекта и правилах пользования ТСО.</span></div>\r\n<div><span style=\"color: #000080\">2.1.9. Руководитель объекта обязан:<br />\r\n- организовать охрану объекта и проводить регулярные, а также внеплановые проверки организации его охраны, технической укрепленности, оснащенности средствами охранно-пожарной сигнализации (ОПС) и выполнение сторонами обязанностей по договору;<br />\r\n- проводить совместно с руководителем службы безопасности (охраны) (или лицом, назначенным приказом по учреждению ответственным за безопасность) детальный анализ особенностей охраны объекта с определением уязвимых мест.<br />\r\n- разрабатывать исходные требования на оборудование объекта ТСО;<br />\r\n- организовать разработку планов обеспечения безопасности объекта (текущий и перспективный), принимать меры организационного характера (издание соответствующих приказов, иной документации) по совершенствованию системы охраны;<br />\r\n- обеспечивать контроль за неразглашением особенностей функционирования аппаратуры сигнализации и связи; разъяснять персоналу объекта необходимость соблюдения этого требования;<br />\r\n- организовать соблюдение пропускного и внутриобъектового режимов;<br />\r\n- обеспечивать своевременный капитальный ремонт инженерных коммуникаций, кабельных линий, модернизацию ТСО;<br />\r\n- совместно с должностными лицами подразделения охраны организовать обучение руководящего состава, сотрудников службы безопасности (охраны) и персонала объекта действиям при возникновении чрезвычайных ситуаций;<br />\r\n- проводить совместно с руководителем службы безопасности (охраны) (или лицом, назначенным приказом по учреждению ответственным за безопасность) тренировки с сотрудниками охранных структур для выработки и приобретения навыков по осуществлению необходимых мероприятий, как при обнаружении подозрительных лиц и предметов, взрывных устройств, других признаков подготовки терактов, так и мер по локализации и минимизации его последствий.</span></div>\r\n<div><span style=\"color: #000080\">2.1.10. Обязанности сотрудника охраны объекта определяются должностной инструкцией, инструкцией по пропускному и внутриобъектовому режиму, планом охраны объекта, разрабатываемых администрацией объекта с учетом Типовой инструкции.<br />\r\nВ них сотруднику охраны определяется:<br />\r\n- место несения службы;<br />\r\n- задачи по несению службы и ответственность за их невыполнение;<br />\r\n- порядок приема и сдачи поста, его особенности;<br />\r\n- список ответственных лиц учреждения, имеющих право вскрытия помещений и доступа на объект в любое время суток, порядок связи с этими работниками;<br />\r\n- порядок допуска в охраняемые помещения в нерабочее время лиц из числа персонала объекта;<br />\r\n- порядок взаимодействия с персоналом службы безопасности и другими работниками объекта;<br />\r\n- порядок приема под охрану и снятия с охраны помещений объекта, выведенных на пульт;<br />\r\n- порядок проверки исправности ТСО и связи;<br />\r\n- порядок действий при возникновении чрезвычайных ситуаций, при нарушении посетителями правил посещения объекта;<br />\r\n- порядок действий при получении сигнала &quot;тревога&quot;, а также при проведении учебных тренировок персонала объекта;<br />\r\n- порядок связи с нарядами соседних постов, персоналом службы безопасности, дежурными ПЦО и территориальных органов внутренних дел;<br />\r\n- порядок (периодичность) доклада руководству подразделения охраны об обстановке и результатах несения службы;<br />\r\n- порядок действий при проведении на объекте массовых мероприятий;<br />\r\n- порядок действий и использования спецтехники для выявления мест возможного сокрытия средств террора в автомобильном транспорте и ручной клади при осуществлении пропускного режима;<br />\r\n- время и место приема пищи. </span></div>\r\n<div><span style=\"color: #000080\">2.1.11. Наряд охраны объекта непосредственно подчиняется начальнику охраны (старшему наряда).</span></div>\r\n<div><span style=\"color: #000080\">2.1.12. На постах охраны с учетом их функциональности рекомендуется иметь следующую документацию:<br />\r\n- должностную инструкцию (выписку из табеля постам, памятку) сотрудника охраны объекта;<br />\r\n- инструкцию по пропускному и внутриобъектовому режиму;<br />\r\n- правила пользования техническими средствами охраны;<br />\r\n- журнал приема и сдачи дежурства;<br />\r\n- журнал &quot;Об оперативной обстановке и принятых мерах&quot;<br />\r\n- журнал регистрации посетителей;<br />\r\n- журнал регистрации въезда (выезда) автотранспорта;<br />\r\n- список служебных и домашних телефонов руководящего персонала объекта и их адреса;<br />\r\n- список и телефоны различных служб города: энергоснабжающей организации, горгаза, пожарной охраны, органов МВД, ФСБ и МЧС,<br />\r\n- инструкцию по действиям должностных лиц и персонала объекта в случае возникновения аварийной ситуации на технологическом оборудовании объекта и других чрезвычайных ситуациях;<br />\r\n- другие необходимые документы по усмотрению руководителя объекта.</span></div>\r\n<div><span style=\"color: #000080\">В зависимости от характера объекта допускается объединение отдельных журналов в единый.</span></div>\r\n<div><span style=\"color: #000080\">2.2. Организация службы по охране объектов</span></div>\r\n<div><span style=\"color: #000080\">2.2.1. Подразделения охраны в соответствии с условиями договора обеспечивают:<br />\r\n- пропускной и внутриобъектовый режимы, взаимодействие со службой безопасности объекта, администрацией;<br />\r\n- охрану объекта или отдельных его помещений, материальных ценностей, выставление постов и маршрутов патрулирования согласно дислокации, контроль за действиями персонала, посетителей, охрану общественного порядка и пресечение правонарушений в зоне постов и маршрутов патрулирования в рамках своей компетенции;<br />\r\n- реагирование на сигналы срабатывания средств ОПС и тревожной сигнализации;<br />\r\n- пресечение несанкционированных проникновений на охраняемый объект;<br />\r\n- участие в локализации и ликвидации возникших ЧС, в том числе вследствие диверсионно-террористических акций.</span></div>\r\n<div><span style=\"color: #000080\">2.2.2. Сотрудники охраны выполняют служебные обязанности в составе наряда охраны в форменной одежде, экипированные средствами индивидуальной защиты и вооруженные в соответствии с нормативно-правовыми актами, регламентирующими деятельность службы осуществляющей охрану объекта. Применение оружия осуществляется в соответствии с действующим законодательством Российской Федерации.</span></div>\r\n<div><span style=\"color: #000080\">2.2.3. Должностными лицами подразделения охраны проверяется готовность наряда перед заступлением на службу к ее несению и проводится инструктаж. В инструктаже наряда охраны могут принимать участие руководители объектов, руководящий и инспекторский состав управлений (отделов) вневедомственной охраны при территориальных органах внутренних дел, милиции общественной безопасности и криминальной милиции органов внутренних дел.</span></div>\r\n<div><span style=\"color: #000080\">2.2.4. Контроль за несением нарядом службы по охране объекта осуществляется должностными лицами подразделения охраны и органов внутренних дел, руководителями объектов в соответствии с требованиями руководящих и иных регламентирующих документов.</span></div>\r\n<div><span style=\"color: #000080\">2.2.5. Должностные лица, уполномоченные на проверку, имеют право:<br />\r\n- знакомиться с документами делопроизводства по вопросам режима и организации охраны;<br />\r\n- проверять организацию службы охраны объектов и исправность ТСО;<br />\r\n- получать от должностных лиц подразделения охраны информацию о происшествиях и ЧС, связанных с охраной объектов;<br />\r\n- давать письменные предложения о временном усилении охраны объекта или его отдельных помещений (подразделений) объектов в пределах установленной штатной численности личного состава.</span></div>\r\n<div><span style=\"color: #000080\">Результаты проверки организации охраны объекта, предложения по устранению выявленных недостатков оформляются актом.</span></div>\r\n<div><span style=\"color: #000080\">2.3. Обязанности сотрудника охраны объекта.</span></div>\r\n<div><span style=\"color: #000080\">2.3.1. В компетенцию сотрудника охраны входит:<br />\r\n- проверка документов и пропусков у лиц проходящих на охраняемый объект или выходящих с объекта;<br />\r\n- проведение в установленном порядке досмотра (осмотра) вещей, задержание (недопущение прохода) нарушителей пропускного режима, а также лиц, пытающихся незаконно вывезти-ввезти (вынести-внести) оборудование, товарно-материальные ценности или оружие, боеприпасы, взрывные устройства, взрывчатые вещества, другие средства террора;<br />\r\n- контроль за работой приборов охранной, охранно-пожарной и тревожной сигнализации установленных на КПП;<br />\r\n- сообщение о срабатывании сигнализации непосредственному начальнику, диспетчеру предприятия, а при необходимости в подразделение охраны, орган внутренних дел, пожарную охрану;<br />\r\n- принятие мер к задержанию (в рамках установленных полномочий) правонарушителей;<br />\r\n- принятие мер к ликвидации аварии в соответствии с должностной инструкцией;<br />\r\n- участие в ликвидации пожара, предупреждении угрозы взрыва;<br />\r\n- прием под охрану от материально ответственных лиц помещений для хранения материальных ценностей, включенных в зону охраны поста.</span></div>\r\n<div><span style=\"color: #000080\">2.3.2. Сотрудник охраны должен знать:<br />\r\n- руководящие документы, определяющие организацию пропускного режима на охраняемом объекте;<br />\r\n- правила внутриобъектового режима;<br />\r\n- структуру предприятия и режим работы его подразделений;<br />\r\n- образцы различных видов пропусков, накладных, подписей должностных лиц, имеющих право давать распоряжения на проход на охраняемую территорию отдельных категорий работников, которым предоставлено право прохода по ведомственным служебным удостоверениям;<br />\r\n- порядок пропуска аварийных бригад при стихийных бедствиях, пожарах, аварий и других чрезвычайных ситуациях;<br />\r\n- правила досмотра (осмотра) вещей, а также проверок вывозимого (ввозимого) груза;<br />\r\n- места возможного сокрытия и признаки средств террора в автомобильном транспорте и ручной клади при реализации задач пропускного режима;<br />\r\n- правила пользования техническими средствами для обнаружения взрывчатых веществ, оружия, боеприпасов, средствами охранной и охранно-пожарной сигнализации;<br />\r\n- порядок задержания правонарушителей и оформления на них материалов задержания (в рамках установленной компетенции);<br />\r\n- правила техники безопасности и производственной санитарии;<br />\r\n- правила применения индивидуальных средств защиты;<br />\r\n- расположение первичных средств пожаротушения и связи, порядок пользования ими;<br />\r\n- знать уязвимые технологические объекты на охраняемом участке, выход из строя которых способен привести к аварийной ситуации.</span></div>\r\n<div><span style=\"color: #000080\">2.3.3. Сотрудник охраны в процессе несения службы обязан выполнять комплекс мероприятий, направленных на недопущение совершения на территории учреждения террористических актов и возникновения других чрезвычайных обстоятельств, для чего необходимо:</span></div>\r\n<div><span style=\"color: #000080\">а) При приеме (сдаче) дежурства: </span></div>\r\n<div><span style=\"color: #000080\">- совместно со сменяемым сотрудником охраны осуществить обход и осмотр контролируемой территории, а также обследование технической укрепленности подвалов, чердаков, окон, дверей с целью изучения оперативной обстановки и обнаружения подозрительных предметов. При обнаружении таковых или выявлении взломанных дверей, окон, замков, отсутствия пломб и печатей немедленно доложить руководителю учреждения (ответственному должностному лицу), диспетчеру предприятия; - ознакомиться с имеющимися инструкциями;<br />\r\n- уточнить систему экстренного вызова милиции, руководства учреждения, службы спасения и проверить работоспособность охранной, аварийной и пожарной сигнализаций, средств связи;<br />\r\n- принять имеющуюся документацию (инструкции, журналы, план действий в случае возникновения чрезвычайных ситуаций, материальные ценности др.) согласно описи;<br />\r\n- проверить исправность индивидуальных средств защиты; - с разрешения ответственного должностного лица администрации учреждения принять (сдать) дежурство.</span></div>\r\n<div><span style=\"color: #000080\">б) Во время дежурства: </span></div>\r\n<div><span style=\"color: #000080\">- осуществлять пропускной режим, регистрируя посторонних лиц в журнале посетителей;<br />\r\n- обеспечивать въезд и выезд автотранспорта на территорию учреждения на основании пропуска или другого разрешительного документа, определенного администрацией учреждения. При этом регистрировать в специальном журнале марку машины, государственный номер, время въезда и выезда;<br />\r\n- контролировать правомерность парковки автотранспорта в непосредственной близости от учреждения. В случае выявления нарушений правил стоянки и остановки автотранспорта и неповиновения со стороны водителя сообщать об этом в дежурную часть милиции и руководству учреждения;<br />\r\n- осуществлять в дневное и ночное время обход и осмотр территории учреждения согласно схемы-маршрута, а при выявлении каких-либо нарушений информировать руководителя учреждения или ответственное должностное лицо;<br />\r\n- вести журнал &quot;Об оперативной обстановке и принятых мерах&quot;, в котором отражать:<br />\r\n- результаты осмотра территории учреждения &ndash; кому доложено; <br />\r\n- выявленные в течение суток нарушения или ситуации, вызывающие подозрения &ndash; кому доложено и какие меры реагирования приняты;<br />\r\n- результаты обследования технической укрепленности объектов учреждения &ndash; кому доложено; <br />\r\n- информацию о проведенных учениях, тренировках по обеспечению безопасности &ndash; время, тема, руководитель, участники;<br />\r\n- результаты проверок несения службы &ndash; кто проверял, выявленные недостатки. В данном журнале может делаться отметка о приеме и сдаче дежурства.<br />\r\n- совместно с администрацией учреждения:<br />\r\n- контролировать обстановку в арендуемых в здании учреждения помещениях и на прилегающих к ним территориях;<br />\r\n- инструктировать работников учреждения о порядке действий при обнаружении брошенных (оставленных) предметов, мобильных телефонов, сумок, кошельков и т.п.<br />\r\n- содействовать правоохранительным органам при проведении ими оперативно-розыскных мероприятий на территории учреждения.</span></div>\r\n<div><span style=\"color: #000080\">2.3.4. Сотруднику охраны запрещается:<br />\r\n- оставлять пост без разрешения, в случае внезапного заболевания оповестить старшего наряда и продолжать нести службу до прибытия замены;<br />\r\n- принимать от любых лиц какие-либо предметы;<br />\r\n- передавать или предъявлять кому бы то ни было табельное оружие, за исключением прямых начальников;<br />\r\n- допускать на охраняемый объект лиц с оружием, за исключением случаев, предусмотренных инструкцией о пропускном режиме;<br />\r\n- самостоятельно сдавать на ПЦО под охрану и снимать из-под охраны помещения объекта;<br />\r\n- сообщать посторонним лицам какие-либо сведения об обстановке на объекте, пароль, а также присвоенные пультовые номера;<br />\r\n- разглашать сведения об особенностях объекта, порядке хранения ценностей, токсичных веществ и организации охраны. </span></div>\r\n<div><span style=\"color: #000080\">2.3.5. При нападении на объект наряд охраны (постовой), работники данного объекта или сотрудники службы безопасности с помощью тревожной сигнализации подают сигнал &quot;тревога&quot; в дежурную часть органа внутренних дел или на ПЦО ОВО или иным способом, используя все имеющиеся каналы связи. После подачи сигнала &quot;тревога&quot; наряд охраны (постовой) принимает меры для пресечения противоправных действий, задержания правонарушителей и усиления охраны, наблюдения за подходами к объекту. По прибытии наряда милиции поступает в распоряжение старшего наряда и действует по его указанию. Персонал объекта по сигналу &quot;тревога&quot; прекращает основную деятельность согласно инструкции по аварийной остановке технологического процесса, в дальнейшем действуют по заранее разработанной инструкции на случай чрезвычайных ситуаций.</span></div>\r\n</div>',`date` = '2010-11-08 14:34:35',`display` = 'Да';
insert into `taggerd__pages` set `id` = '29',`parent` = '0',`position` = '7',`autotext` = '',`page` = 'Меры безопасности',`name` = 'Концепция безопасности',`title` = 'ЧОП ТАГГЕРД (Москва), концепция безопасности предприятия, разработка мер и средств защиты от посягательств',`description` = 'Охранная фирма \"ТАГГЕРД\" предлагает услуги по комплексному решению проблем безопасности и охраны в Москве и области. Разработка концепции безопасности предприятия, выбор мер и средств защиты от противоправных посягательств и др.',`keywords` = 'концепция безопасности, меры безопасности, меры защиты, методы защиты',`text` = '<div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<h1><span style=\"font-size: large\"><span style=\"color: rgb(0,0,255)\"><span><strong>КОНЦЕПЦИЯ&nbsp;БЕЗОПАСНОСТИ ПРЕДПРИЯТИЯ</strong></span></span></span></h1>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><img height=\"233\" alt=\"\" hspace=\"10\" width=\"280\" align=\"left\" src=\"/files/storage/images/koncepciyabezopasnosti.jpg\" /></span><span style=\"color: rgb(0,0,128)\">Планирование мер по обеспечению безопасности - способ, с помощью которого обеспечивается координация действий всех сотрудников предприятия для достижения общей цели - обеспечения безопасности.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Для грамотного планирования мероприятий безопасности необходимо в первую очередь разработать концепцию безопасности предприятия, учитывающую специфику деятельности данной организации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Концепция безопасности предприятия </span></strong><span style=\"color: rgb(0,0,128)\">представляет собой официально утвержденный документ, в котором отражены цели, задачи, принципы и способы противодействия возможным угрозам,&nbsp;определена система требований и условий по организации мер обеспечения безопасности персонала и собственности предприятия.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">При разработке данной концепции следует помнить, что главным принципом создания системы безопасности является обеспечение заданного уровня защиты от различных угроз при минимизации затрат на охрану и безопасность.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Как проанализировать потребность компании в наборе мер по обеспечении безопасности? </span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Выработка всестороннего плана (концепции) обеспечения безопасности требует методичного и продуманного анализа. Начав с общего понимания угроз для конкретного бизнеса и дойдя до множества частных задач, вам придется применить структурный подход, чтобы собрать воедино и проанализировать этот план. Получаемые в результате рекомендации должны дополнять и поддерживать одна другую. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Задача эта не так проста, поскольку требует глубокого исследования, оптимизации и обоснования. И особое внимание в решении этой задачи следует уделить профилактике происшествий, а не реакции на произошедший инцидент. Фактически, многие из действий охраны предназначены для производства расследования по факту события, а не для предотвращения этого события. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Объектом анализа при составлении концепции безопасности являются всевозможные &laquo;уязвимости&raquo;, которые необходимо выявлять методично и всесторонне, чтобы программа безопасности имела в основе обширный анализ, имеющий в основе принцип предотвращения, а не локализации последствий. Аналитический подход позволяет гарантировать, что средства, затрачиваемые на обеспечение безопасности, расходуются в соответствии с конкретными нуждами, защищая прежде всего наиболее важные участки. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Следует также помнить, что цель концепции состоит не в том, чтобы разработать план безопасности с высокой степенью &quot;защиты от дурака&quot;. Полностью защитить объект стоит огромных денег, а это тяжкое бремя для бизнеса. Цель же ставится такая: сделать нарушение режима безопасности максимально &laquo;затруднительным&raquo; для злоумышленника. Степень трудности зависит от того, насколько ценен данный объект и насколько организация-заказчик готова к расходам для защиты от возможных угроз.&nbsp;<br />\r\n&nbsp; <br />\r\n<strong>Процесс анализа делится на пять фаз</strong> - разделение объекта защиты на подобъекты, оценка возможных угроз, анализ уязвимости, выбор мер противодействия и их внедрение. Процесс этот рассчитан на тщательный анализ, и приступать к каждой следующей фазе следует, лишь полностью завершив предыдущую.</span></div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"font-size: medium\"><span style=\"color: rgb(0,0,255)\"><span><strong>СТРУКТУРА КОНЦЕПЦИИ БЕЗОПАСНОСТИ:</strong></span></span></span></div>\r\n<div>&nbsp;</div>\r\n<div><strong><span style=\"color: rgb(0,0,128)\">1. Описание проблемной ситуации в сфере безопасности предприятия:</span></strong></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Перечень потенциальных и реальных угроз безопасности, их классификация и ранжирование.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Причины и факторы зарождения угроз.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Негативные последствия угроз для предприятия.</span></div>\r\n<div><strong><span style=\"color: rgb(0,0,128)\">2. Механизм обеспечения безопасности:</span></strong></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Определение объектов безопасности и объектов защиты предприятия.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Формулирование политики и стратегии безопасности.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Принципы обеспечения безопасности.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Цели обеспечения безопасности.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Задачи обеспечения безопасности.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Критерии и показатели безопасности предприятия.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Создание структуры по управлению системой безопасности предприятия.</span></div>\r\n<div><strong><span style=\"color: rgb(0,0,128)\">3. Мероприятия по реализации мер безопасности:</span></strong></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Формирование подсистем общей системы безопасности предприятия.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Определение субъектов безопасности предприятия и их роли.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Расчет средств и определение методов обеспечения безопасности.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">- Контроль и оценка процесса реализации концепции.</span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: small\"><strong>МЕРЫ ПО ОБЕСПЕЧЕНИЮ БЕЗОПАСНОСТИ МАТЕРИАЛЬНЫХ РЕСУРСОВ</strong></span></span></div>\r\n<div><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Для оценки степени безопасности материальных ресурсов предприятия и дальнейшего планирования работ по их охране необходимо ответить на следующие вопросы:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Достаточно ли надежно охраняется ваше оборудование, ценности, информация? </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Насколько просматриваются ваши помещения снаружи, и из каких зон? </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Работает ли сигнализация? </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Какие противопожарные мероприятия вы проводите? </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Как будет действовать охрана, если сработает сигнализация? </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Имеете ли вы резервные копии информации? </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Где и как хранятся документы и резервные копии информации? </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Как скоро вы сможете восстановить работоспособность своей фирмы, если случится ограбление? </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Это не банальные вопросы. Ответив на них, вы можете начинать выстраивать план обеспечения безопасности. А ряд простых превентивных мер может снизить уровень потенциальных угроз. Эти меры просты и эффективны для любого предприятия. Итак, необходимо:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- обустроить изолированные помещения или &ldquo;зоны&rdquo; для конфиденциальных бесед с клиентами; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- совершать любые действия (переговоры, финансовые операции и т.д.) с &quot;крупными&quot; и &quot;мелкими&quot; клиентами в разных помещениях; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- оборудовать защищенные помещения для операций с крупной наличностью; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- оборудовать внешний периметр предприятия и его внутренние помещения современными средствами сигнализации и защиты; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- как можно дальше от входа располагать зоны, где хранятся наличные деньги; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- разработать систему доступа лиц, обслуживающих технические средства охраны и компьютеры; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- продумать расположение постов охраны в рабочее и нерабочее время. </span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Наибольшую часть в решении этих вопросов может взять на себя</span><strong><span style=\"color: rgb(0,0,128)\"> охранная фирма (ЧОП).</span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Служба безопасности</span></strong><span style=\"color: rgb(0,0,128)\"> <strong>(охранная фирма)</strong> вашего предприятия должна быть хорошо подготовлена, осведомлена и иметь четкое представление о том:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- могут ли грабители незаметно покинуть предприятие после налета в дневное время (например, влившись в поток транспорта или пешеходов); </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- останавливают ли сотрудники патрульно-постовой службы милиции подозрительных лиц и автомобили, появляющиеся в районе в ночное время и в выходные дни; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- где расположено ближайшее отделение милиции и сколько времени потребуется его сотрудникам, чтобы прибыть в случае сигнала тревоги или вызова по телефону (практика показывает, что задержание налетчиков по &ldquo;горячим следам&rdquo; возможно лишь тогда, когда милиция прибывает на место не позднее чем через 5 минут после подачи сигнала); </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- как обеспечить срабатывание охранной сигнализации при любом варианте ограбления со взломом или вооруженного налета; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- как помешать грабителям исчезнуть, в условиях всеобщего замешательства; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- как предотвратить возможность захвата заложников из числа клиентов или персонала. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Все эти меры направлены на снижение рисков в случае &quot;силового&quot; покушения на вашу собственность и в некоторой степени на предотвращение утечки информации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Конечно, не все потенциальные угрозы можно уменьшить подобными мерами. Например, внутреннее мошенничество и злоупотребления не могут быть устранены в рабочем порядке, но убытки от них также могут быть сокращены применением определенных мер.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Давно известно, что доверие &ndash; это краеугольный камень внутреннего мошенничества и злоупотреблений, поэтому администрация должна найти баланс доверия со своими служащими. Так сказать, доверяй, но проверяй&hellip;&nbsp;<span style=\"color: rgb(0,0,128)\">Одним из наиболее качественных и надежных каналов выявления на предприятии мошенников, взяточников, а также агентуры конкурентов является, контроль над доходами и расходами сотрудников, имеющих право финансовой подписи или допущенных к конфиденциальной информации. Появление у работника сумм, не отраженных в его налоговой декларации, должно стать сигналом к проведению мероприятий по установлению источников его непомерно возросших доходов. </span></span><span style=\"color: rgb(0,0,128)\">Но, часто сотрудники совершают внутренние мошенничества и злоупотребления в качестве мести за несправедливость и жесткость администрации, притеснение в коллективе. Создавая благоприятный психологический климат в коллективе, организация может уменьшать такие побуждения своих сотрудников. </span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Одним из эффективных приемов по предотвращению внутреннего мошенничества является недопущение узурпации власти одним из топ-менеджеров. В состав всех подразделений или групп, участвующих в решении стратегических вопросов, должны включаться юристы, экономисты и сотрудники службы безопасности. Это эффективно с двух сторон, во-первых, идет квалифицированный сбор информации о сотрудниках и его возможных криминальных устремлениях, а также осуществляется профилактическая отработка возможных негативных связей сотрудников предприятия.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Цепочка связей по адресам, телефонам, совместным командировкам, проведенным переговорам и заключенным договорам может привести к очень интересным выводам в виде достаточно разветвленной причинно-следственной цепочки.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: small\"><strong>Меры по обеспечению безопасности информационных ресурсов</strong></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Если вы только начинаете собственное дело и если у вас в штате всего несколько сотрудников, если вы материально не можете провести все предполагаемые мероприятия, то вы должны предпринять хотя бы следующий минимум защитных мер:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- разработать соглашение о найме персонала, которое обеспечивает защиту частной информации и неразглашение идей; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- использовать штамп &quot;секретно&quot; на деловых планах, списках покупателей, чертежах, и другой технической документации; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- разработать простые соглашения о неразглашении тайны для продавцов, покупателей, потенциальных лицензиатов и др. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Если вопросом защиты информации занимается более крупное предприятие, и руководством принято решение о создание системы защиты информации с соблюдением всех требований российского законодательства, то необходимо сделать следующее:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Оценить важность и ценность информации. Понять, какую информацию, для чего и от кого нужно защищать. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Внести дополнения в уставные документы, подготовить перечни сведений, составляющих коммерческую тайну. Ввести договорные отношения с сотрудниками фирмы. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Обозначить защищаемую информацию соответствующими указателями на ее носителях, организовать их учет. Разграничить доступ к информации сотрудников. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Организовать минимум физической защиты помещений и хранилищ информации. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Регламентировать использование средств связи и передачи информации. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Подобрать специалистов и начать работу по защите информации на средствах вычислительной техники. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- Заручиться поддержкой опытного профессионала в области защиты информации и юриста.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Пункт № 2 требует дополнительного пояснения: Устав предприятия является исходной правовой базой для организации защиты коммерческой тайны. Именно в нем, прежде всего, необходимо зарегистрировать Ваше право на защиту коммерческой тайны. Содержание дополнения в Устав может быть следующим: &quot;Предприятие определяет состав, объем и порядок защиты сведений, составляющих коммерческую тайну и имеет право требовать от сотрудников соблюдения установленных правил ее сохранности. Предприятие и его сотрудники обязаны обеспечить сохранность коммерческой тайны, а также информации, охраняемой патентным, лицензионным и авторскими правами&quot;. Такое положение, закрепленное в Уставе, будет основой для всех последующих шагов по организации защиты информации фирмы.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Необходимо разработать &quot;Перечень сведений, составляющих коммерческую тайну предприятия и основные требования к сотрудникам по ее защите&quot;, а также &quot;Перечень сведений, которые не должны разглашаться посторонним лицам в целях личной безопасности сотрудников фирмы&quot;. Оба эти документа необходимо закрепить в приказе и дать под расписку всем сотрудникам фирмы. Первый документ - прямая основа для возникновение юридической ответственности в соответствии с гражданским законодательством. Второй нужен в связи с тем, что некоторую информацию трудно отнести к коммерческой тайне, но сохранять ее весьма важно (например, место нахождения руководителя фирмы в конкретное время, планируемые совещания, переговоры, сведения о личной жизни сотрудников и т.п.). </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Следующим необходимым документом является &quot;Договор-обязательство о сохранении коммерческой тайны и другой служебной информации&quot;. Это может быть как отдельный документ, так и специальный раздел в контракте о найме на работу. Предложив сотруднику подписать договор-обязательство, руководство фирмы предупреждает сотрудника о том, что в дело вступает целая система мероприятий по защите информации: правовых, организационных, технических. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Естественно, что, как и любая другая работа, обязанности по защите коммерческой тайны (соблюдению конфиденциальности) должны соответствующим образом оплачиваться - это положение также желательно отразить в контракте.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Для построения системы защиты коммерческой тайны предприятия очень важно определить источники и соответствующие им каналы утечки информации. На каждом конкретном предприятии перечень таких каналов носит индивидуальный характер, что определяется спецификой его деятельности. Мероприятия по обеспечению сохранности информации также носят индивидуальный характер. Сущность защитных мероприятий сводится к перекрытию возможных каналов утечки защищаемой информации, которые появляются в силу объективно складывающихся условий ее распространения и возникающей у конкурентов заинтересованности в ее получении.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: larger\"><span style=\"color: rgb(0,0,128)\"><span style=\"\"><strong>Носителями коммерческой тайны могут быть:</strong></span></span></span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">- люди, </span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">- документы, </span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">- изделия (образцы товаров либо продукции) </span></strong></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">- процессы. </span></strong></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: small\"><strong><span style=\"color: rgb(0,0,128)\">Люди</span></strong></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Придерживайтесь &ldquo;железного&rdquo; правила: круг лиц, причастных к коммерческим тайнам Вашей фирмы, должен быть максимально сужен. Необходимо также установить строгий порядок допуска к переговорам и к подписанию различных документов с партнерами и заказчиками - поручите это сотрудникам, в надежности которых не сомневаетесь.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Получить сведения о надежности сотрудников фирмы можно из их личных дел, а также путем психологического тестирования, собеседований, негласного наблюдения, конфиденциального опроса коллег, знакомых с ними по прежнему месту работы. Этим должны заниматься специалисты службы безопасности в тандеме с психологами и кадровиками.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Нужно сделать все возможное, чтобы сотрудники коллективно переживали и несли ответственность за порученное дело. Желательно, чтобы они испытывали потребность консультироваться с вами. Привить своим сотрудникам привычку делиться горестями и радостями, воспитать из них настоящих патриотов фирмы руководитель может, грамотно применяя систему материальных и моральных стимулов, придерживаясь психологически выверенной линии поведения. Хорошо если над подходами к решению этой задачи по вашему заказу поработают специалисты.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: small\"><strong><span style=\"color: rgb(0,0,128)\">Документы</span></strong></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Сотрудник, занятый деловыми бумагами фирмы, должен быть вашим доверенным лицом. Его обязанность - организовать работу с документами таким образом, чтобы исключить ее утечку на всех этапах - от подготовки текстов до учета и хранения поступающих и исходящих телеграмм, писем, образцов и т.п. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Для учета документов необходимо завести специальный журнал учета входящих, исходящих и внутренних документов, содержащих конфиденциальную информацию. С помощью такого журнала вы сможете периодически проводить инвентаризацию и своевременно уничтожать бумаги, потерявшие для вас (но не для ваших конкурентов) важность.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Важно защитить компьютерные сети и доступ к индивидуальным компьютерам посредством различных ограничений доступа к информационным базам, введением паролей и кодов доступа. Наличие подобных мер - достаточно надежная защита от &ldquo;охотников&rdquo; за вашими секретами. Не пренебрегайте ими.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: small\"><strong><span style=\"color: rgb(0,0,128)\">Изделия</span></strong></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Организуйте надежный учет всех товаров, промышленных и экспериментальных образцов, сведения о которых не должны стать достоянием ваших конкурентов.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Охрана изделий необходима на всех этапах их &ldquo;движения&rdquo; внутри фирмы (приобретение или изготовление, испытания либо проверка качества, транспортировка, хранение, эксплуатация, ремонт и т.п.).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: small\"><strong><span style=\"color: rgb(0,0,128)\">Технические каналы передачи информации</span></strong></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Установите систему ограничений на использование техническими каналами связи (телефон, Интернет, компьютерные сети) для передачи конфиденциальных сведений. Охраняйте от возможного подслушивания или сканирования свои телефоны, факсы, компьютеры, офисы, автомобили, квартиры. Используйте для этого засекречивающую аппаратуру связи, генераторы помех, приборы для обнаружения &ldquo;жучков&rdquo; и сканирующих устройств, применяйте условные кодовые обозначения. Какой бы технически сложной ни казалась вам &ldquo;круговая оборона&rdquo; против электронной &ldquo;заразы&rdquo;, в нынешних условиях как раз сложности в конечном итоге может и не хватить. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Все элементы системы защиты коммерческих секретов фирмы необходимо постоянно анализировать для оценки ее фактического состояния, выявления недостатков и нарушений. Такой анализ должен также включать в себя моделирование вероятных каналов утечки информации, возможных приемов и способов ее несанкционированного получения конкурентами.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Анализ системы защиты коммерческих секретов, моделирование вероятных угроз позволяет намечать и при необходимости вводить дополнительные меры безопасности. При этом степень их целесообразности определяется достаточно просто: затраты на обеспечение надлежащей секретности должны быть существенно меньше, чем возможный экономический ущерб.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"font-size: small\"><strong><span style=\"color: rgb(0,0,128)\">Организационные меры в области информационной безопасности</span></strong></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Однако, &ldquo;степень надежности любого шифра определяется не его сложностью, а неподкупностью шифровальщика&rdquo;. Иными словами, ключевыми фигурами систем защиты коммерческих секретов являются сотрудники фирм, причем не только те, которые работают с закрытой информацией. Рядовой сотрудник, не имеющий доступа к коммерческой тайне, тоже может оказать помощь конкурентам.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Поэтому, для ограничения круга лиц, имеющих доступ к коммерческой тайне предприятия, существует объективная необходимость в организации специального закрытого делопроизводства.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Для документов, содержащих сведения, разглашения которых Вы хотели бы избежать, следует установить специальный порядок подготовки, маркировки (т.е. присвоения соответствующего грифа), размножения, рассылки, приема и учета, группировки в дела, использования, хранения, уничтожения и проверки наличия. Следует также назначить ответственное лицо по данному вопросу.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Получать конфиденциальные документы должны только те лица, кому действительно необходимо знать содержащуюся в них информацию. Если у вас не отработана система определения такого круга лиц, то любая система контроля за движением документов будет бессмысленной и вам не удастся сохранить вашу коммерческую тайну. Необходимо создать соответствующую систему, гарантирующую правильную циркуляцию документов, исключающую доступ к информации для тех, кому не нужно ее знать. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">В договорах с контрагентам предприятия также необходимо специально оговаривать обязательства о соблюдении условий конфиденциальности и предусмотреть последствия нарушения принятых обязательств. Являясь неотъемлемыми условиями договора, требования конфиденциальности сохраняют свою автономность и должны соблюдаться после прекращения действия договора.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Помимо рассмотренных выше организационных мероприятий, существуют меры специфического характера, направленные на предотвращения утечки информации по агентурным и технологическим каналам.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: small\"><strong>МЕРЫ ПО ОБЕСПЕЧЕНИЮ ЛИЧНОЙ БЕЗОПАСНОСТИ</strong></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Личная безопасность в определяющей степени зависит от самого человека, соблюдения им соответствующих правил предосторожности и форм поведения, которые усваиваются в процессе воспитания, обучения и накопления опыта.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Как известно, не существует однозначно определенных, универсальных методов защиты на все случаи жизни, особенно, если это касается таких ситуаций, когда нападение планируется скрытно, конспиративно. Тем не менее, наличие высокого уровня защиты, готовность к выявлению и отражению враждебных действий поможет нейтрализовать возможные угрозы жизни и здоровью. При этом необходимо учитывать, что угроза жизни и здоровью собственников и руководителей бизнеса может исходить как изнутри структуры, так и извне.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Одним из основных признаков подготовки преступления, выбора его способа, места и времени является выявление лиц ведущих сбор соответствующих сведений о собственнике или руководителе. Соответственно, важнейшей стратегией личной безопасности является информационная защита, которая включает в себя:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- сокрытие личной информации, &nbsp;которая в случае ее использования преступником может нанести ущерб; к этой выполнению этой задачи должны быть подключено все ближайшее окружение, члены семьи, и непосредственно сам собственник (руководитель);</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- четкий инструктаж родственников и ближайшего окружения о том, какие сведения о предпринимателе им не следует сообщать; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- своевременное информирование службы безопасности предприятия обо всех фактах подозрительного, необоснованного интереса, указывающих на сбор сведений; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- доведение до окружения заранее обработанных данных, которые могут ввести преступника в заблуждение или усилить у него чувство неуверенности (страха) в возможности реализации преступных намерений. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Обязательным условием обеспечения личной безопасности является надежность ближайшего окружения предпринимателя, обеспечиваемая, в том числе и их проверкой.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">По &quot;месту обеспечения&quot; безопасности условно можно выделить три группы:</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- безопасность на рабочем месте; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- безопасность при передвижении за территорию предприятия; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- безопасность членов семьи. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Для обеспечения безопасности в помещении возможно использование различных технических средств и систем охраны.</span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Для обеспечения безопасности передвижений, чаще всего, при существовании реальной угрозы жизни, привлекаются телохранители.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: medium\"><strong>Выбор мер противодействия</strong></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Точно так же, как это происходит в здравоохранении, когда самочувствию пациента может быть нанесен ущерб неправильным лечением, уровень безопасности организации может быть ослаблен либо поставлен под вопрос неверным применением мер противодействия. Выработка таких мер - скорее искусство, чем сухая наука, и потому требует объединения усилий персонала управления и службы охраны; только в этом случае способна появиться на свет программа, соответствующая нуждам организации.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">При этом, применяемые меры должны зависеть от фазы угрозы &ndash; от профилактики, до локализации уже произошедших событий. По каждому из элементов реагирования необходимо создать детальное описание предпринимаемых действий на всех четырех фазах происшествия:</span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">ФАЗА ПРЕДУПРЕЖДЕНИЯ: Определите процедуры эвакуации и завершения работы системы, а также расположение укрытий и убежищ. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">ФАЗА СОБЫТИЯ: Определите технологии укрепления и обеспечения выживания, а также методы локализации зоны инцидента - такие, как выставление нее кордонов для предотвращения возможности расширения круга участвующих в ситуации лиц. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">ПО ГОРЯЧИМ СЛЕДАМ: Определите процедуры оказания первой помощи, уведомления властей и экстренных служб, ограничения доступа к месту преступления, управления движением, эвакуации пострадавших и стратегию сдерживания распространения ситуации. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">ПОСЛЕ СОБЫТИЯ: Определите процессы постановки в известность родственников, очистки и ремонта, деятельности на запасной территории и проведения разъяснительной работы.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">В качестве мер противодействия могут выступать </span></strong><span style=\"color: rgb(0,0,128)\">электронные системы безопасности, физические барьеры, охранники, политики и процедуры. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Электронные системы безопасности </span></strong><span style=\"color: rgb(0,0,128)\">объединяют системы контроля доступа, обнаружения, наблюдения и сбора свидетельских показаний. В число подсистем могут входить обнаружение вторжения, тревожные кнопки, охранное телевидение, проводные и радиопереговорные устройства, громкоговорящие системы, средства индивидуальной защиты и телефонные системы. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Физические и психологические барьеры </span></strong><span style=\"color: rgb(0,0,128)\">применяются для затруднения доступа к объекту. В число физических барьеров входят: хранилища, сейфы, шлагбаумы, ограждения и ворота, изделия из пуленепробиваемых материалов, колючая проволока, капканы, ловушки для транспортных средств, бронезащита автомобилей, механические замки, &quot;лежачие полицейские&quot; и бордюры, бомбоубежища, средства освещения, щиты, панели из прочных материалов и специальные элементы ландшафта и др.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Персонал службы безопасности</span></strong><span style=\"color: rgb(0,0,128)\"> исполняет различные охранные функции, включая оперирование электронными системами, несение вахты на постах и осуществление патрулирования. Большинство действий охраны предусматривают наблюдение за событиями и - в случае инцидента - постановка в известность правоохранительных органов. В некоторых случаях охранники могут быть вооружены, иметь специальную подготовку и право вмешиваться в ход событий. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Политики</span></strong><span style=\"color: rgb(0,0,128)\"> устанавливают позицию управляющего звена и общий подход к практике разрешения бизнес-проблем. Процедуры определяют средства осуществления политики. Это наиболее критичная часть программы безопасности. Здесь определяются программы и процессы, необходимые для того, чтоб механизмы обеспечения безопасности работали эффективно. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: medium\"><strong>Внедрение </strong></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">В этой фазе рекомендации преобразуются в спецификации и инструкции, направленные на действия людей и систем, а также формирование правил и политики компании. Задача внедрения - перевести план безопасности в запросы и приобретение документов, процедур, организационных программ и процессов.</span></div>\r\n</div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: rgb(0,0,128)\"><strong>Если вам сложно самостоятельно разобраться в хитросплетениях проблем безопасности - обращайтесь в компанию &quot;ТАГГЕРД&quot;! Наша охранная фирма поможет вам решить сложные проблемы безопасности и защитить себя и свой бизнес от любых враждебных поползновений! Оказание услуг безопасности - наша профессия! Обращайтесь только к профессионалам своего дела!</strong></span></div>\r\n</div>',`date` = '2010-11-08 18:57:56',`display` = 'Да';
insert into `taggerd__pages` set `id` = '30',`parent` = '27',`position` = '1',`autotext` = '',`page` = 'Охрана образовательных учреждений',`name` = 'Охрана образовательных учреждений',`title` = 'ЧОП ТАГГЕРД (Москва) - охрана детских и учебных заведений (школы, ВУЗ-а, колледжа, техникума, детского сада)',`description` = 'ЧОП ТАГГЕРД (Москва) - осуществляет охрану детских дошкольных и образовательных учреждений: школы, ВУЗ-а, колледжа, техникума, детского сада, детского санаториев и лагеря.',`keywords` = 'охрана детского сада, охрана школы, охрана коледжа, охрана техникума, охрана вуза, охрана института, охрана учебных заведений, охрана образовательных учреждений, охрана детских дошкольных учреждений',`text` = '<h1><span style=\"font-size: xx-small\"><strong><font color=\"#000080\">охрана детских дошкольных, средних и высших учебных заведений: школы, колледжа, техникума, детского сада, вуза, пионерского лагеря, санатория</font></strong></span></h1>\r\n<h2><span style=\"color: #0000ff\"><span style=\"font-size: large\"><strong>ОХРАНА ДЕТСКИХ И ОБРАЗОВАТЕЛЬНЫХ УЧРЕЖДЕНИЙ</strong></span></span></h2>\r\n<div style=\"text-align: justify\"><font color=\"#000080\">\r\n<p><img height=\"244\" hspace=\"9\" width=\"350\" align=\"left\" alt=\"\" src=\"/files/storage/images/ohranashkoly(1).jpg\" /></p>\r\nОхрана школы, детского сада, да и любого среднего и высшего учебного заведения &ndash; задача не простая, ответственная и хлопотная, требующая особого подхода. Ведь дети, а тем более перешагнувшие 18-летний рубеж юноши и девушки, очень насторожено и критично относятся к различного рода ограничениям и запретам, т.е. как раз к тем вопросам, за которые отвечает охрана.</font>&nbsp;</div>\r\n<div>\r\n<div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Поэтому одним из основных вопросов в деле обеспечения безопасности в учебном заведении является особый качественный подбор кадров охранников. Помимо того, что охранник в таком заведении должен быть требовательным и суровым, при этом необходимо чтобы он был добрым и вежливым, подтянутым и опрятным. От охранника требуется особый такт и знание особенностей детской и юношеской психологии и мотивации поведения.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Детский сад, школа, институт и иные образовательные учреждения, не только в Москве, в России, но и во всем мире являются традиционными объектами охраны, с традиционными задачами, стоящими перед сотрудниками охранного предприятия. Это, прежде всего, обеспечение круглосуточного контрольно-пропускного режима, контроль соблюдения правил поведения установленных внутренним распорядком образовательного учреждения, охрана личного имущества воспитанников и преподавателей, охрана имущества учебного заведения.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;Школа, колледж или институт являются во многом особым миром, со своими социально-психологическими законами &ndash; и это вносит соответствующие коррективы при разработке Концепции безопасности учебного заведения. Например, к этим проблемам можно отнести детскую преступность (в т.ч. воровство и вымогательство, хулиганство, порчу имущества, проблему распространения наркотиков в школьной и вузовской среде и др.)</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Способы и методы охраны&nbsp; образовательных учреждений, должны обеспечивать безопасное и, в то же время, четкую организацию учебного процесса, стабильную работу преподавательского состава и технических служб учреждения. Так как зачастую образовательные учреждения не обладают достаточными средствами, чтобы содержать большой штат охраны, то основной упор при создании Концепции безопасности делается на техническую укрепленность&nbsp;и оснащение объектов системами безопасности (оповещения, жизнеобеспечения, видеонаблюдения, эвакуации и пожаротушения). Важным моментом также является организация тесного взаимодействия с местными правоохранительными органами, детской комнатой милиции, отработка вариантов оказания экстренной помощи, установка &quot;тревожной кнопки&quot;.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Немаловажную роль в процессе охраны учебного заведения играет комплекс профилактических мер, таких, как проведение совместных учебных занятий по основам безопасности жизнедеятельности учащихся, организация тренировочных мероприятий по отработке действий учащихся и технического и преподавательского состава школы в случае возникновения чрезвычайных ситуаций (&quot;учебной тревоги&quot;).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">В целом, охрана учебных заведений, в т.ч. школ, вузов, колледжей является не только актуальной общественной задачей, но и важнейшей обязанностью руководства любого образовательного учреждения. Охрана образовательных учреждений (школьных, дошкольных, средних и высших) в Москве &ndash; специфическая задача, решение которой под силу только грамотным профессионалам. Мы готовы взять на себя решение проблем охраны и безопасности учебных заведений и выполнить данную задачу на высоком профессиональном уровне.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #000080\">Опираясь на богатый опыт работы по охране учебных заведений, мы&nbsp;можем предложить вам варианты женской, мужской и смешанной охраны. Все сотрудники и сотрудницы охраны, обеспечивающие безопасность образовательных учреждений обучены и проверены, имеют статус частного охранника и опыт работы от 2-х лет. ЧОП имеет возможность предоставить дополнительный персонал по требованию заказчика, на период проведения массовых мероприятий, праздников, соревнований, собраний и т.д. Мы ручаемся за наших сотрудников, осуществляющих охранные функции в учебных заведениях, и гарантируем высокое качество охранных услуг!</span></div>\r\n</div>\r\n</div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"color: #ff0000\"><span style=\"font-size: large\"><strong>ОХРАНА ОБЪЕКТОВ - НАША ПРОФЕССИЯ!</strong></span></span></div>\r\n<p>&nbsp;</p>',`date` = '2010-11-10 09:26:49',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '31',`parent` = '27',`position` = '2',`autotext` = '',`page` = 'Охрана офисов и офисных центров',`name` = 'Охрана офисов и офисных центров',`title` = 'ЧОП ТАГГЕРД (Москва) - охрана офисов,  бизнес-центров, административных и офисных центров',`description` = 'ЧОП ТАГГЕРД оказывает профессиональные услуги по обеспечению безопасности офисов,  бизнес-центров, административных и офисных центров. Охрана ЧОП - недорого, надежно, качественно!',`keywords` = 'охрана офиса, охрана бизнес-центра, охрана офисного центра, охрана административного центра, безопасность офиса',`text` = '<div><span style=\"color: rgb(0, 0, 128);\"><br />\r\n</span></div>\r\n<h1><span style=\"font-size: xx-small;\"><span style=\"color: rgb(0, 0, 128);\">ОХРАНА ОФИСОВ, БИЗНЕС-ЦЕНТРОВ, АДМИНИСТРАТИВНЫХ И ОФИСНЫХ ЦЕНТРОВ / БЕЗОПАСНОСТЬ ОФИСНЫХ ПОМЕЩЕНИЙ </span></span><span style=\"color: rgb(0, 0, 128);\"><br />\r\n</span></h1>\r\n<h2><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: large;\"><strong>БЕЗОПАСНОСТЬ ОФИСОВ И БИЗНЕС-ЦЕНТРОВ</strong></span></span></h2>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;<img width=\"300\" hspace=\"10\" height=\"225\" border=\"1\" align=\"left\" alt=\"\" src=\"/files/storage/images/ohranabiznescentrov.JPG\" /></span><span style=\"color: rgb(0, 0, 128);\">Сегодня невозможно себе представить ни одно предприятие <strong>Москвы</strong> без офиса. От того, насколько он презентабелен, удобен и безопасен, зависит не только работоспособность персонала, но и имидж компании на рынке, отношение к ней партнеров, клиентов, будущая перспектива, а значит и успех компании в целом. Защита от внутренних и внешних угроз - необходимое условие для процветания бизнеса. Гарантом стабильного и безопасного функционирования офиса является профессиональная охрана.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Охрана офиса и офисного центра</span></strong><span style=\"color: rgb(0, 0, 128);\"> на первый взгляд может показаться работой не пыльной, простой, спокойной, без особого напряжения. Однако это мнение ошибочно, ведь именно офис является сердцем и двигателем любой компании. Именно здесь хранятся все ее коммерческие секреты, именно здесь находится руководство, бухгалтерия, касса, печать организации. Именно здесь проходят переговоры с партнерами, решаются стратегические вопросы продвижения, финансирования, распределения, внедрения. Здесь хранятся ценные архивы, реализуются новые идеи, внедряются ноу-хау. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Современный офис</span></strong><span style=\"color: rgb(0, 0, 128);\"> является зоной особой привлекательности для криминальных элементов. Наличие дорогой оргтехники, элементов интерьера, мебели, денежных средств делает офис лакомым куском для различного рода криминала. Но еще более значимой опасностью является возможная утечка конфиденциальной информации, ведь попадание коммерческой и персональной информации в руки конкурентов и недоброжелателей может нанести огромный вред компании, и даже привести к ее краху. Защита информации не менее важна, чем охрана имущества и денежных средств. Также в офисе проводят большую часть времени руководящие лица и менеджмент компании, физическая защита которых является одним из условий успешной деятельности предприятия. В офисе, как правило, выдается заработная плата сотрудникам, хранятся деньги на различные нужды, поэтому возникает еще одна задача &ndash; охрана зон с особым режимом доступа, мест хранения материальных ценностей, касс, бухгалтерии, сейфов и др. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Исходя из вышеперечисленных опасностей, должна выстраиваться <strong>система охраны офисных помещений</strong>. Выбор эффективной стратегии и тактики защиты от противоправных посягательств и неправомерного использования материальных, экономических, информационных ценностей, определяющих положение бизнеса на рынке, требует грамотного подхода, профессионализма и опыта специалистов, которые будут ее создавать. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Самым цивилизованным и логичным решением проблемы охраны офиса является сотрудничество с ЧОП, т.к. только охранное предприятие обладает необходимыми знаниями, опытом и средствами для организации надежной охраны офиса.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Оптимальной стратегией</span><strong><span style=\"color: rgb(0, 0, 128);\"> защиты офисных помещений</span></strong><span style=\"color: rgb(0, 0, 128);\"> является применение комплексных мер: современных технических средств безопасности, физической охраны и административных ограничений. К арсеналу технических средств могут быть отнесены системы видеонаблюдения, ограничения и контроля доступа, охранно-пожарная сигнализация, средства защиты информации. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">На охранниках офиса </span></strong><span style=\"color: rgb(0, 0, 128);\">(офисного центра) лежит особая роль &ndash; они являются не просто частью компании, которую охраняют, а своеобразным лицом компании. Охранник первым встречает и последним провожает не только персонал фирмы, но и важных гостей - клиентов и партнеров, отвечает на вопросы, входящие в его компетенцию, ориентирует и направляет, принимает почту, следит за&nbsp;правопорядком и соблюдением режима&hellip; С учетом данной специфики и пожеланий конкретного Заказчика, ЧОП производит отбор сотрудников для охраны офисов, таким образом, чтобы охранники не только четко исполняли свои должностные обязанности, но и&nbsp;могли гармонично вписаться в структуру и имидж охраняемой компании. Помимо этого с Заказчиком согласуется форма одежды охранников (форменный или строгий костюм, с учетом требований корпоративной политики компании), а также оснащение, вооружение.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: small;\"><strong>Охрана офисов в Москве</strong> <strong>может включать в себя комплекс следующих услуг:</strong></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- разработка Концепции безопасности офиса, <br />\r\n- осуществление контрольно-пропускного режима ( для сотрудников, посетителей, автотранспорта, вноса/выноса имущества), </span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- фейс-контроль сотрудников и посетителей объекта, </span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- обеспечение сохранности имущества,</span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- обеспечение неприкосновенности помещений в нерабочее время, </span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- защита жизни и здоровья персонала,</span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- патрулирование территории,&nbsp;</span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- контроль соблюдения правил внутреннего распорядка и дисциплины,</span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- комплекс мероприятий по защите конфиденциальной информации,</span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- проведение агентурной работы,</span></strong></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- противопожарные и антитеррористические меры. </span></strong></em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">В зависимости от специфики работы офиса либо места его расположения возможны различные варианты охранных мероприятий, а также выбор режима охраны: дневной, ночной, круглосуточный. Способами профилактики возможных угроз для офиса является: выявление и устранение уязвимых мест для проникновения; установка дополнительной сигнализации, видеокамер, освещения; применение системы IP-телевидения, позволяющей наблюдать на удаленном расстоянии за обстановкой на объекте в режиме реального времени; использование &laquo;тревожной кнопки&raquo; и группы быстрого реагирования; регулярные отчеты по телефону на пульт оперативного дежурного ЧОП об обстановке на объекте. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Специалисты нашего ЧОП готовы предоставить услуги охраны офиса любого масштаба и специфики. Применение комплексного интегрированного подхода, опыт и профессионализм сотрудников &laquo;ТАГГЕРД&raquo;, подкрепленные многолетней практикой охраны предприятий, поможет сделать функционирование вашего офиса или офисного центра действительно эффективным и безопасным!</span></strong></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: medium;\"><strong>ОКАЗАНИЕ УСЛУГ БЕЗОПАСНОСТИ И ОХРАНЫ - НАША ПРОФЕССИЯ!</strong></span></span></div>\r\n<p><span style=\"color: rgb(0, 0, 128);\"><br />\r\n</span></p>',`date` = '2010-11-13 17:27:18',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '32',`parent` = '27',`position` = '3',`autotext` = '',`page` = 'Охрана магазина',`name` = 'Охрана магазина',`title` = 'ЧОП ТАГГЕРД (Москва) предлагает профессиональные услуги по охране магазинов. торговых центров, супермаркетов, бутиков',`description` = 'Группа охранных предприятий \"ТАГГЕРД\" (Москва) предлагает услуги по охране магазинов, торговых центров, супермаркетов, многофункциональных комплексов, гипермаркетов. Недорого, надежно, качественно!',`keywords` = 'охрана магазина, охрана супермаркета, охрана торгового центра, безопасность магазина, охрана гипермаркета, охрана универсама',`text` = '<h1><strong><span style=\"font-size: xx-small;\"><span style=\"color: rgb(0, 0, 128);\">безопасность и охрана магазина / торгового центра / бутика / супермаркета /&nbsp;универсама / гипермаркета&nbsp;</span></span></strong></h1>\r\n<h2><span style=\"font-size: large;\"><strong><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: x-large;\">ОХРАНА МАГАЗИНА</span></span></strong></span></h2>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><img width=\"350\" hspace=\"10\" height=\"263\" align=\"left\" src=\"/files/storage/images/ohranasmarketa.jpg\" alt=\"\" />Москва приближается к числу мировых лидеров по количеству магазинов на душу населения. Но даже такого числа&nbsp;магазинов явно не достаточно чтобы удовлетворить растущий покупательский спрос, именно поэтому год от года растет количество сетевых магазинов, супермаркетов, универсамов, торговых комплексов.</span></div>\r\n<div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Одновременно с развитием сети оптовой и розничной торговли в Москве растут и проблемы, связанные с обеспечением безопасности данного бизнеса, ведь любое торговое предприятие изначально является потенциально привлекательным местом для преступников. Большое скопление людей, наличие в кассах крупных денежных сумм, обилие товаров, невнимательность покупателей и продавцов &ndash; все это как магнит притягивает различного рода &laquo;преступных элементов&raquo; и приводит к повышенному уровню опасности.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">В наибольшей степени различным угрозам подвержены <strong>супермаркеты,</strong> работающие по системе круглосуточного самообслуживания. Такие магазины требуют особого внимания и контроля, а наличие в магазине &laquo;тревожной кнопки&raquo; не решает всех проблем безопасности, и не защищает бизнес от воровства и мошенничества. Самостоятельно справиться с внутренними и внешними угрозами, с хищениями, хулиганскими выходками и конфликтными ситуациями бывает очень сложно, поэтому, для решения вопросов безопасности, целесообразно воспользоваться услугами профессиональных частных охранных предприятий.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Какие же опасности угрожают магазинам? &ndash; Их великое множество &ndash; от банальной мелкой кражи, до террористической угрозы. Например, в отдельно стоящих магазинах и бутиках наиболее часто происходят грабежи, хищения и порча имущества. Риск ограбления тем выше, чем выше &laquo;статус&raquo; заведения. Так, ювелирный магазин в списке привлекательности для злоумышленников стоит гораздо выше, чем, например, булочная.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">В торговых центрах и супермаркетах основным бичом безопасности является воровство. Специалисты условно разделяют проблему потерь от хищений в магазинах на два типа: <strong>воровство персонала и воровство покупателей</strong>. По экспертным данным, потери магазинов от воровства покупателей и хищений, совершаемых персоналом, составляют от 0,5 до 5 % товарооборота, а в неохраняемых магазинах показатель таких потерь может достигать уровня 10-20%.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Эксперты рекомендуют</span></strong><span style=\"color: rgb(0, 0, 128);\"> не забывать распространенную формулу, говорящую о том, что 10% людей никогда не совершат преступления, 10% совершат его, какие бы кары им за это не грозили; ну а 80% пойдут на правонарушение в случае создания соответствующих условий и при наличии уверенности в своей безнаказанности. Воруют в магазинах все подряд - от жевательной резинки до телевизоров, причем статистика показывает, что люди, ворующие товар, это не обязательно бедные и безработные граждане, это могут быть вполне обеспеченные лица, разные по возрасту и полу &ndash; подростки, мужчины и женщины среднего и преклонного возраста, да-да, наши дорогие бабушки и дедушки&hellip;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Убытки от воровства покупателей</span></strong><span style=\"color: rgb(0, 0, 128);\">, по оценкам экспертов, в зависимости от типа магазина составляют в среднем от 30 до 50% всех потерь. Наиболее страдают от воровства, по мировой статистике, специализированные винные магазины (54%). В продовольственных магазинах и крупных супермаркетах эта цифра составляет более 34% .</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Не меньшую (если не большую) опасность для магазина представляет воровство персонала. По данным зарубежных специалистов, в США на долю персонала небольших супермаркетов приходится от 30 до 60% всех потерь. Поэтому подбору и обучению персонала магазина должно уделяться особое внимание, ибо это основа безопасности. Решающими факторами для приема на работу должны служить не столько внешние данные, а в большей степени добросовестность, профессиональные качества, рекомендации прежних работодателей.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">В связи с большими потерями от хищений в магазинах сети &laquo;Метро&raquo;, даже в западных странах, с конца 70-х годов дорогие эксклюзивные спиртные напитки и шампанское стали выставляться в закрытых стеклянных витринах. При этом руководство магазина понимает, что в случае их отказа от действующей системы, оборот по спиртным напиткам возможно увеличится, однако не идут на подобные меры, так как следствием этого станет непропорциональное увеличение хищений. Аналогичные схемы действуют в большинстве российских супермаркетах по другим группам дорогостоящих товаров. Например, при покупке икры, баночка передается покупателю для оплаты не в руки, а на кассу.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Для борьбы с хищениями посетителей применяют различные способы, но эффективнее всего действуют профилактические меры психологического и упреждающего характера, и одна из самых действующих мер - постоянная демонстрация присутствия охраны и контроля за посетителями.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 255);\"><strong>Наряду с организацией качественной физической охраны торгового зала, специалисты ЧОП рекомендуют использовать следующие меры:</strong></span></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- внедрение противокражных систем - электронные системы для защиты от краж (EAS)</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- использование интеллектуального кассового оборудования, ведущего контроль кассовых операций, для выявления махинаций кассиров,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- видеонаблюдение за торговыми залами, складами и прилегающей территорией,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- особая планировка стеллажей по категориям товаров,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- продуманная система учета ТМЦ.</span></strong></div>\r\n<div><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Применяются и различные нестандартные решения. Например, в США и странах Европы получили распространение специальные посты наблюдения, закрытые стеклом с односторонней видимостью. Для контроля за посетителями, широко применяются изометрические обзорные зеркала, размещенные в удобных для наблюдения местах торгового зала, в том числе на кассе, где покупатели не всегда выкладывают для оплаты весь товар из тележки, а кассир со своего рабочего места не видит, что в ней находится. Также широко используются услуги детективов под видом покупателей, которые по ряду признаков устанавливают лиц нацеленных на хищение товара и совместно с охраной задерживают &laquo;несунов&raquo; при выходе из магазина.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Система видеонаблюдения в магазинах выполняет особую роль. Специфика работы охраны магазина подразумевает наличие большого количества камер видеонаблюдения не только открытого, но и скрытого типа, для надзора за персоналом и охраной. Помимо передачи оперативной информации видеокамеры имеют и хороший психологический эффект. В этой связи, на входе в магазины сети &laquo;Метро&raquo; посетители через стеклянную перегородку имеют возможность видеть мониторную охраны магазина. Дополнительной мерой психологического воздействия, могут стать специальные объявления, информирующие о том, что контроль в торговом зале осуществляется с помощью систем видеонаблюдения.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Помимо борьбы с хищениями в торговом зале, система видеонаблюдения позволяет отображать на мониторе службы охраны все операции, производимые на принтере интеллектуального кассового терминала в реальном режиме времени, ведь именно в точке расчета за товар сталкиваются интересы разных лиц и возможен сговор покупателей с персоналом магазина.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 255);\"><strong>Торговые центры, супермаркеты и иные объекты торговли являются одними из наиболее сложных и ответственных в плане организации системы охраны. Только на таких объектах охрана одновременно</strong> <strong>использует полный набор мер безопасности</strong><strong>, таких как:</strong></span></div>\r\n<div><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- физическая охрана торговых площадей, складов и зоны разгрузки товара,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- обеспечение сохранности товарно-материальных ценностей,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- использование технических средств безопасности: охранной и пожарной сигнализации, видеонаблюдения и контроля доступа,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- пресечение попыток хищения товара и порчи имущества со стороны посетителей и персонала,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- обеспечение пропускного режима и фейс-контроля,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- организация охранных мероприятий на прилегающей территории и автостоянке,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- поддержание общественного порядка, защита жизни, здоровья, чести и достоинства покупателей и персонала, от неправомерных посягательств и преступных элементов,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- организация мероприятий психологической защиты ТМЦ и персонала,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- осмотр личных вещей покупателей и персонала на выходе (проверка при необходимости выносимого товара на соответствие кассовому чеку),<br />\r\n- антитеррористическая деятельность,</span></strong></div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">- организация тесного взаимодействия с правоохранительными органами.</span></strong></div>\r\n<div><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">По мнению специалистов нашего ЧОП, охрана магазина должна носить комплексный характер. Отдельные мероприятия могут не принести желаемого результата.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">В зависимости от режима работы магазина, охрана может осуществляться круглосуточно, в ночное время, и в дневные часы (в ночное время магазин ставится на сигнализацию). Количество охранников, находящихся одновременно на объекте, напрямую зависит от его площади, интерьера, схемы расположения и количества торговых залов,&nbsp;а также от требований и финансовых возможностей клиента. Наш ЧОП всегда пытается найти компромисс с заказчиком, по вопросам организации охраны магазина с максимальной эффективностью и минимальными затратами.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Для правильного выбора тактики охраны магазина представители нашего охранного предприятия тщательным образом изучают и анализируют объект на предмет уязвимости и потенциальных угроз. На основании этого разрабатываются комплексные меры защиты объекта (Концепция безопасности). Охрана каждого магазина осуществляется по индивидуальной схеме, соответствующей уровню безопасности и оперативной обстановке.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Большое внимание наш ЧОП уделяет профильной (объектовой) подготовке сотрудников охраны, внешнему виду, повышению квалификации, дополнительному обучению работе с техническими средствами охраны и новым методикам обеспечения безопасности. Четкое выполнение нашими сотрудниками всех предписаний и обязанностей гарантирует надежность и безопасность охраняемого объекта.</span></div>\r\n</div>\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(204, 0, 0);\"><strong><span style=\"font-size: medium;\">ОКАЗАНИЕ ОХРАННЫХ УСЛУГ - НАША ПРОФЕССИЯ!</span></strong></span></p>',`date` = '2010-11-18 07:38:08',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '33',`parent` = '27',`position` = '1',`autotext` = '',`page` = 'Охрана автостоянок и гаражей',`name` = 'Охрана автостоянок и гаражей',`title` = 'ЧОП ТАГГЕРД (Москва) предлагает качественную охрану автостоянок, гаражей, автосервисов, автозаправок',`description` = 'ЧОП ТАГГЕРД (Москва) предлагает качественную охрану автостоянки, гаражного комплекса, автосервиса, автотранспортного предприятия, автозаправки. Охрана ЧОП - это надежно, профессионально, качественно!',`keywords` = 'охрана гаражей, охрана автостоянки, охрана автотранспортного предприятия, охрана паркинга, охрана автосервиса, охрана автозаправки',`text` = '<div>&nbsp;</div>\r\n<h1><span style=\"color: rgb(0,0,128)\"><span style=\"font-size: xx-small\">БЕЗОПАСНОСТЬ И ОХРАНА ГАРАЖНОГО КОМПЛЕКСА / АВТОСТОЯННКИ / ПАРКИНГА / АВТОТРАНСПОРТНОГО ПРЕДПРИЯТИЯ / АВТОСЕРВИСА / АВТОЗАПРАВКИ</span></span><span style=\"color: rgb(0,0,128)\"><br />\r\n</span></h1>\r\n<h2><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: large\"><b>ОХРАНА АВТОСТОЯНОК</b></span></span></h2>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><img height=\"263\" alt=\"\" hspace=\"10\" width=\"350\" align=\"left\" src=\"/files/storage/images/ohranastoyanki.jpg\" /></span><strong><span style=\"color: rgb(0,0,128)\">Автостоянки, гаражи, паркинги, различные транспортные предприятия, автосервисы,</span></strong><span style=\"color: rgb(0,0,128)\"> относятся к группе повышенного риска, из-за наличия большого количества дорогостоящей техники и собственности. И не важно, где и как расположен гаражный или стояночный комплекс &ndash; в многоэтажном наземном или подземном паркинге, за бетонным забором или за сеткой-рабицей, на открытой площадке или в закрытом боксе - он притягивает особое внимание организованных преступных групп и мелких воришек.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Одной из самых больших опасностей </span></strong><span style=\"color: rgb(0,0,128)\">грозящих такому объекту, после угрозы пожара, считается угон. Ущерб от угона дорогостоящей иностранной строительной техники, легкового или грузового автомобиля, может составлять до нескольких сотен тысяч у.е.&nbsp;Кроме того, на автостоянке могут находиться фургоны с ценным грузом, за которым охотятся преступные группировки. А стоянка (если она не оборудована должным образом и плохо охраняется), как раз и является наиболее вероятным местом для совершения преступлений &ndash; угонов техники или кражи ценного груза. Поэтому обеспечение безопасности территории и сохранности транспортных средств &ndash; основная задача каждого автостояночного или транспортного комплекса.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Старые методы</span></strong><span style=\"color: rgb(0,0,128)\"> <strong>охраны автостоянок</strong> уже давно неэффективны или вовсе бессильны в решении проблем безопасности, поэтому сегодня на помощь пришли электронные системы охраны, контроля въезда/выезда, идентификации транспорта и водителей. Кроме этого, новые технологии позволили автоматически фиксировать время пересечения шлагбаума, продолжительность стоянки, контролировать оплату. Но, наличие автоматики и современных систем безопасности на объекте не избавляет от необходимости присутствия там физической охраны, для обеспечения правильного использования водителями этого оборудования, а также на случай, если возникнет необходимость перевода в ручной режим шлагбаумов и ворот.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Охрана автостоянок</span></strong><span style=\"color: rgb(0,0,128)\"> организуется в соответствии с регламентирующей и правовой документацией, а так же с учетом проверенных практикой правил. Перед приемом объекта под охрану разрабатывается Концепция безопасности, которая учитывает все опасности, явные и скрытые угрозы, выявленные после всестороннего обследования объекта на защищенность. Меры и методы обеспечения безопасности и охраны для каждого объекта могут быть индивидуальны, и зависеть от целей и задач службы охраны, уровня укрепленности объекта и множества иных факторов, но основная тактика охраны объектов заключается в обеспечении контрольно-пропускного режима - въезда и выезда автотранспорта, и прохода граждан.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: #0000ff\"><span style=\"font-size: small\"><strong>При охране автостоянок, гаражей, автосервисов и транспортных предприятий выполняются следующие задачи и применяются следующие меры безопасности:</strong></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">1. Обеспечение сохранности</span></strong><span style=\"color: rgb(0,0,128)\"> транспортных средств, является основной задачей службы охраны. Для этих целей используются инженерные и электронные средства защиты, системы видеонаблюдения,&nbsp;а также применяется патрулирование территории нарядами охраны. При малейшем подозрении на то, что автомобиль пытается взломать или угнать, охрана должна предпринять активные меры для защиты собственности и задержать злоумышленников до выяснения обстоятельств. По желанию заказчика объект может охраняться с использованием сторожевых собак и вооружения.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">2. Контрольно-пропускной режим</span></strong><span style=\"color: rgb(0,0,128)\"> является основой организации безопасности на объекте. Это режим распространяется как на проезд автотранспорта (по спискам, по пропускам, по магнитным картам), так и на проход граждан. Право прохода на территорию объекта имеют только владельцы автомобилей, водители и обслуживающий персонал. На объект не допускаются лица в состоянии наркотического и алкогольного опьянения. За выездом автотранспорта осуществляется особый, повышенный контроль. Он необходим для того, чтобы исключить возможность угона или воровства транспортных средств. При выезде автомобиля у его хозяина (водителя) проверяются документы - техпаспорт на автомобиль, водительское удостоверение, доверенность на право управления машиной (для водителя).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">3. Постоянный мониторинг</span></strong><span style=\"color: rgb(0,0,128)\"> оперативной обстановки на объекте производится визуально и с использованием систем видеонаблюдения. Все лица, находящиеся на территории объекта, должны находиться под неусыпным контролем охраны. Охрана должна обращать внимание на все потенциальные угрозы и неправомерные действия граждан, в том числе на прилежащей к парковке территории. Всякий, кто в малейшей степени ведет себя необычно, странно, а тем более подозрительно, берется на особый контроль службой охраны. Если, к примеру, гражданин подошел к машине и не смог открыть замок двери за несколько секунд, то он сразу должен попасть в поле зрения охраны.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">4. Организационные меры. </span></strong><span style=\"color: rgb(0,0,128)\">За каждым транспортным средством администрация объекта должна закрепить конкретное место стоянки. Охранник на посту должен иметь схему распределения парковочных мест с указанием марки, номера автомобиля и контактных данных владельца.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">5. Контроль внешнего вида</span></strong><span style=\"color: rgb(0,0,128)\"> транспортных средств на въезде на парковку. Каждый автомобиль, въезжающий на стоянку, необходимо фиксировать и тщательно осматривать на предмет наличия царапин, механических повреждений, вмятин. Это необходимо для того, чтобы хозяин машины потом не мог предъявить претензий к охране за повреждение, устранение которого может обойтись в сотни, а то и тысячи долларов.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">6. Соблюдение правил пожарной безопасности:</span></strong><span style=\"color: rgb(0,0,128)\"> запрещается использовать открытый огонь, курить в не установленных для этого местах, хранить легковоспламеняемые вещества (бензин, машинное масло, краску и т.д.) и газовые баллоны, для этих целей должны быть специально оборудованные&nbsp;места. Объект оборудуется необходимым количеством огнетушителей, подручных средств пожаротушения, пожарных постов и т.д.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">7. Объект должен иметь</span></strong><span style=\"color: rgb(0,0,128)\"> замкнутую огороженную территорию, хорошо освещенную в темное время суток. Автотранспорт охраняется строго в пределах огороженной зоны парковки, которая является первым и самым простым уровнем защиты, достаточно прочным по всему периметру объекта, чтобы остановить транспортное средство на скорости до 30-40 км/ч.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">8. Оборудование объекта</span></strong><span style=\"color: rgb(0,0,128)\"> инженерными и электронными средствами охраны: системами видеонаблюдения, ограничения и контроля доступа, периметровой сигнализации. Входные двери и калитки на территорию объекта необходимо оборудовать кодовыми замками и домофонами, а ворота или шлакбаумы - системами дистанционного управления. Кроме того, могут применяться специальные противоугонные препятствия на выезде с объекта, которые управляются охраной. Установкой оборудования, его дальнейшим обслуживанием и эксплуатацией должны заниматься только профессионалы, которыми могут стать сотрудники ЧОП.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">9. На особо важных объектах</span></strong><span style=\"color: rgb(0,0,128)\"> и стоянках помимо всего прочего ведется и антитеррористическая деятельность. Для того чтобы не допустить проезд автомобилей на объект с взрывными устройствами, они тщательно досматриваются, в том числе с помощью специального устройства с зеркалом для осмотра днища автомашины.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Еще один немаловажный аспект</span></strong><span style=\"color: rgb(0,0,128)\"> в обеспечении безопасности заключается в особом подборе сотрудников охраны для несения службы на такого рода объектах. Желательно, чтобы охранники не только имели представление о различных марках автомобилей, но и могли ими управлять, были внимательны, могли быстро проверить внешний вид и укомплектованность авто, знали современные технические средства безопасности и контроля. Также желательно, чтобы служба охраны имела постоянный штат охранников, так как частые кадровые перестановки ведут &nbsp;к погрешностям и даже грубым ошибкам, способным принести серьезный ущерб охраняемой собственности.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">Охрана автостоянок и гаражных комплексов &ndash; работа достаточно сложная и ответственная, что обусловлено высоким интересом криминальных элементов к дорогостоящему имуществу. ЧОП &quot;ТАГГЕРД&quot; поможет Вам быстро и профессионально оборудовать объект техническими средствами безопасности, наладить несение дежурств в оптимальном для каждого конкретного объекта режиме. Мы имеем немалый опыт в организации охраны автомобильных автостоянок, гаражных комплексов и транспортных предприятий, поэтому можем гарантировать надежную охрану объектов любой сложности. Доверьте свой объект профессионалам!</span></strong></div>\r\n<div style=\"text-align: center\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: center\"><span style=\"color: rgb(204,0,0)\"><span style=\"font-size: medium\"><strong>ОКАЗАНИЕ ОХРАННЫХ УСЛУГ - НАША ПРОФЕССИЯ!</strong></span></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<p>&nbsp;</p>',`date` = '2010-11-21 15:18:53',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '34',`parent` = '27',`position` = '6',`autotext` = '',`page` = 'Охрана строительных объектов',`name` = 'Охрана строительных объектов',`title` = 'ЧОП ТАГГЕРД (Москва) предлагает профессиональную охрану строительных объектов, стройплощадок',`description` = 'ЧОП ТАГГЕРД (Москва) предлагает профессиональную охрану строительных объектов любого назначения, строительных комплексов любого уровня сложности. Охрана ЧОП - это надежно, профессионально, качественно!',`keywords` = 'охрана стройплощадок, охрана строительных объектов, охрана стройки, безопасность строящихся объектов, охрана строительных площадок',`text` = '<div>&nbsp;</div>\r\n<h1><span style=\"color: #000080\"><span style=\"font-size: xx-small\"><strong>охрана строительного объекта / безопасность стройплощадки / охрана строительных комплексов / охрана&nbsp;тмц на стройке</strong></span></span></h1>\r\n<h2><span style=\"color: rgb(0,0,255)\"><span style=\"font-size: large\"><strong>ОХРАНА СТРОИТЕЛЬНЫХ ОБЪЕКТОВ</strong></span></span></h2>\r\n<div style=\"text-align: justify\"><img height=\"226\" alt=\"\" hspace=\"10\" width=\"340\" align=\"left\" src=\"/files/storage/images/ohranastroyplochadki.jpg\" /><strong><span style=\"color: rgb(0,0,128)\">Охрана строительных объектов</span></strong><span style=\"color: rgb(0,0,128)\"> &ndash; задача специфическая и далеко не простая, как может показаться простому обывателю. Обеспечение безопасности на такого рода объектах в народе считается малопрестижой, &laquo;грязной&raquo; занятием, а значит бесперспективной и дешевой работой. Хотя по сложности и ответственности, организация охраны, стройплощадка может посоревноваться с любым режимным промышленным объектом, особенно если речь идет об охране строительного комплекса (например, нового микрорайона).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Именно наличие на объекте большого количества дорогостоящих строительных и отделочных материалов, оборудования, техники, а также людей (строителей), приведенных в единое, совместное, порой круглосуточное движение, под названием стройка, делает эти объекты столь проблемными для организации охраны.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ccccff\"><strong><span style=\"color: rgb(0,0,128)\">В чем же состоит специфика охраны строительных объектов?</span></strong></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">1. Стройплощадки являются самыми уязвимыми, в плане технической укрепленности, объектами. Из-за жесткой экономии, а также по ряду объективных причин, на таком объекте сложно или технически невозможно установить камеры видеонаблюдения, системы контроля доступа, уж не говоря о сигнализации в помещениях и по периметру стройки. Хорошо, если и сам забор будет установлен в капитальном варианте, но зачастую его заменяет сетка-рабица в половину человеческого роста. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">2. На объекте находится огромный объем дорогостоящих товарно-материальных ценностей, оборудования и техники, рассредоточенных (а иногда&nbsp;просто разбросанных) по всей строительной площадке. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">3. На средних и крупных строительных объектах работают, как правило, несколько подрядных строительных организаций, что неизбежно привносит элемент децентрализации и осложняет создание единой системы охраны. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">4. Недостаточная освещенность строительных объектов в темное время суток, причем как по периметру общего ограждения, так и внутри территории. Это происходит из-за полного отсутствия электроснабжения, либо недостатка мощности имеющихся электросетей. Строительная площадка питается электричеством в основном от дизельных передвижных электростанций, мощностей которых (да и выделяемых лимитов топлива) не всегда хватает для нормального освещения внутренней территории, уж не говоря о внешней.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">5. Строительный объект сложно патрулировать не только в дневное время (из-за проведения интенсивных строительных работ по сооружению подземных коммуникаций, рытья траншей, погрузочно-разгрузочных работ и пр.), но и в ночное время суток (ввиду слабой освещенности, наличия не закопанных ям, торчащей арматуры, кабелей, всевозможных препятствий, способных нанести травму охраннику).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">6. Постоянное пребывание на объекте большого количества рабочих, как работающих в смене, так и &laquo;посторонних&raquo; остающихся после смены, проживающих на объекте, передвигающихся по территории стройки и за ее пределы по производственной и личной необходимости в круглосуточном режиме.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">7. Интенсивный зачастую круглосуточный поток автотранспорта, ввозящего и вывозящего ТМЦ; техники и механизмов, двигающихся &nbsp;по территории строительной площадки (кранов, экскаваторов, бульдозеров и пр.); постоянное перемещение строительного оборудования и ТМЦ от складов к местам проведения работ и обратно.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Как видно, условия работы охранников на строительных объектах достаточно сложные и напряженные, если еще учесть то, что для охраны стройплощадок, как правило, выделяются достаточно скудные средства, ведущие к ограничению численности охранников. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Тем не менее, такие объекты и при таких условиях охранять возможно, и достаточно эффективно. Но, эффективность охраны напрямую зависит от наличия постоянного и тесного взаимодействия руководства ЧОП и Заказчика (строительной генподрядной организации), от хорошо продуманной системы организации охраны, четко налаженной оперативной связи, от профессионализма и опыта сотрудников ЧОП, от желания и активного участия в этом вопросе самого Заказчика. В проблему обеспечения безопасности и сохранности ТМЦ обязательно должны быть вовлечены кроме сотрудников ЧОП, руководители подразделений и ИТР Заказчика &ndash; администрация, бухгалтерия, инженеры, прорабы и т.д. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ccccff\"><strong><span style=\"color: rgb(0,0,128)\">Специфика и перечень услуг ЧОП при охране строящихся объектов зависит от ряда факторов:&nbsp;</span></strong></span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">&nbsp;</span></strong><span style=\"color: rgb(0,0,128)\"> </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- общая площадь строительной площадки; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- целевое назначение строящегося объекта;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- конструктивные особенности и расположение строящегося объекта; </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- укрепленность и защищенность объекта;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- плотность людского и транспортного потоков;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- криминогенность обстановки в данном районе строительства;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- текущий этап строительства;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- расположение мест складирования ТМЦ и техники;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">- возможность визуального контакта между постами охраны и др.</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">Приступая к созданию Концепции безопасности объекта, необходимо отчетливо представлять наиболее характерные виды внутренних и внешних угроз, которым будет противостоять охранное предприятие. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ccccff\"><strong><span style=\"color: rgb(0,0,128)\">Одна из основных угроз - хищения товарно-материальных ценностей заказчика, которые имеют ряд разновидностей: </span></strong></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">1. Мелкое хищение</span></strong><span style=\"color: rgb(0,0,128)\"> - может быть совершено как одним человеком, так и группой лиц. Участниками могут быть как посторонние граждане, так и собственно рабочие объекта (по статистике - до 80% всех краж приходится на персонал). Данные хищения совершаются в основном по мелочи, часто спонтанно (что называется, &laquo;прихватил по случаю&raquo;), либо спланировано. Похищенное имущество выносится (в сумках, под одеждой), либо вывозится в сговоре с водителями за пределы объекта или перебрасывается подельникам через забор и т.п. Это наиболее распространенный вид хищений. Как правило, стоимость похищаемого имущества по сравнению с другими видами хищений не очень высока (в руках много не унесешь). Тем не менее,&nbsp;эти кражи представляют собой весьма серьезную опасность для Заказчика в силу своей массовости, а также доступности (в случае плохой защищенности объекта и слабого контроля персонала со стороны администрации).</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">2. Несанкционированный вывоз имущества </span></strong><span style=\"color: rgb(0,0,128)\">автомобильным транспортом или спецтехникой. Здесь возможны два варианта. Первый, мелкий &ndash; когда водитель вывозит за пределы объекта какие-либо материальные ценности, спрятанные в кузове, в кабине, под капотом или прикрепленные к раме. Второй &ndash; куда более серьезный, когда по поддельным документам или в сговоре с материально-ответственными лицами вывозятся десятки тонн арматуры, бетона, раствора, дерева, кирпича, отделочных материалов и оборудования. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><strong><span style=\"color: rgb(0,0,128)\">3. Хищение, совершаемое путем &laquo;недовоза&raquo; ТМЦ </span></strong><span style=\"color: rgb(0,0,128)\">на объект, когда материально-ответственное лицо, находящееся в сговоре с подельниками, делает отметку в товарно-транспортной накладной о приемке якобы завезенного на объект груза, проводит его по материальному отчету, а недостачу впоследствии списывает на разные обстоятельства, в том числе на кражу по вине &laquo;плохой&raquo; охраны. Это самый опасный для экономики строительной компании вид хищений. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">С учетом вышеперечисленных угроз и рисков выстраивается комплексная система безопасности объекта, которая должна учитывать совместное использование инженерных и электронных средств безопасности и охраны, организационные мероприятия и физическую охрану. Охрана стройплощадок, как правило, осуществляется в непрерывном круглосуточном режиме,&nbsp;путем организации стационарных постов (на складах и въездных воротах) и подвижных постов (патрулирование территории). При определении численности охранников и дислокации постов необходимо предусмотреть подмены охранников (на отдых, прием пищи, обогрев, сушку обмундирования и т.п.), а также усиление постов в случае возникновения чрезвычайных ситуаций. На некоторых объектах целесообразно использование вооружение и сторожевых собак. </span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ccccff\"><strong><span style=\"color: rgb(0,0,128)\">Основные меры безопасности, используемые ЧОП при охране объектов строительства:</span></strong></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">- Обеспечение контрольно-пропускного режима: контроля за перемещением товарно-материальных ценностей, как через въездные ворота, так и внутри объекта; контроль въезда/выезда автотранспорта и спецтехники; контроль прохода на территорию объекта граждан; контроль выезда автотранспорта через пункт мойки и очистки колес. </span></em></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">- Обеспечение сохранности материальных ценностей, расположенных на территории объекта, на открытых площадках и в закрытых в складских помещениях.</span></em></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">- Патрулирование территории объекта по периметру (при наличии хорошей освещенности и соблюдения норм техники безопасности на объекте).</span></em></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">- Обеспечение контроля за соблюдением персоналом Заказчика требований внутриобъектового режима: &nbsp;правопорядка, правил пожарной безопасности, графика строительных работ, пресечение фактов нахождения на территории строительства персонала заказчика после окончания рабочей смены и т.д. </span></em></span></div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">- Обеспечение оперативной связи между сотрудниками службы охраны объекта,&nbsp;с оперативный дежурным ЧОП, с администрацией Заказчика, что позволит контролировать обстановку на объекте в режиме реального времени и оперативно реагировать на все ее изменения.</span></em></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">- Пресечение нарушений и противоправных действий, направленных на нанесение материального ущерба имуществу заказчика,&nbsp;а также комплекс мероприятий по действиям в чрезвычайных ситуациях. Установление тесного взаимодействия с местными правоохранительными органами.</span></em></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">&nbsp;- Усиление технической укрепленности объекта: забора, ворот, калиток, помещений и мест хранения материальных ценностей, решеток на окнах; использование систем видеонаблюдения, контроля доступа, охранной и пожарной сигнализации, связи.</span></em></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">- Ведение реестра (списка) ТМЦ, оборудования и механизмов. Складирование ТМЦ и хранение техники в строго отведенных местах, желательно в опечатанном и заблокированном виде.</span></em></span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"background-color: #ffffcc\"><em><span style=\"color: rgb(0,0,128)\">- Антитеррористическая деятельность.</span></em></span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">&nbsp;</span></div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\"><span style=\"color: rgb(0,0,128)\">Правильно организованная работа охраны является неотъемлемой и обязательной частью комплекса мероприятий по их пресечению. </span>Только отработав все указанные&nbsp;&nbsp;выше направления можно говорить об эффективной охране. &nbsp;</span></div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\">&nbsp;</div>\r\n<div style=\"text-align: justify\"><span style=\"color: rgb(0,0,128)\">И еще один важный аспект,&nbsp;&nbsp;которому мы&nbsp;уделяем&nbsp;повышенное внимание&nbsp;&ndash; <strong>строгий отбор и профильная (объектовая) подготовка сотрудников </strong>для охраны строительных объектов, дополнительное обучение работе с техническими средствами охраны и новым методикам обеспечения безопасности в любой нештатной ситуации. Четкое выполнение сотрудниками ЧОП &laquo;ТАГГЕРД&raquo; всех предписаний и обязанностей гарантирует надежность и безопасность охраняемого объекта, сохранность материальных ценностей!</span></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center\"><span style=\"color: #cc0000\"><span style=\"font-size: medium\"><strong>ОКАЗАНИЕ ОХРАННЫХ УСЛУГ - НАША ПРОФЕССИЯ!</strong></span></span></div>',`date` = '2010-11-25 19:02:51',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '35',`parent` = '27',`position` = '6',`autotext` = '',`page` = 'Охрана коттеджных поселков',`name` = 'Охрана коттеджных поселков',`title` = 'ЧОП ТАГГЕРД (Москва) предлагает профессиональную охрану загородных домов и коттеджных (дачных) поселков,',`description` = 'ЧОП ТАГГЕРД (Москва) предлагает профессиональную охрану загородных домов и коттеджных (дачных) поселков, обеспечение пропускного режима и сохранности имущества, с использованием собак, вооружения, автотранспорта. Охрана ЧОП - это надежно, профессионально,',`keywords` = 'охрана дачи, охрана коттеджей, охрана загородного дома, охрана садового товарищества, охрана дачного поселка, охрана коттеджного поселка',`text` = '<div>&nbsp;</div>\r\n<h1><strong><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: xx-small;\">ОХРАНА И БЕЗОПАСНОСТЬ&nbsp; ЗАГОРОДНОГО ДОМА / САДОВОГО ТОВАРИЩЕСТВА / ДАЧНОГО И КОТТЕДЖНОГО ПОСЕЛКА</span></span></strong></h1>\r\n<h2><span style=\"font-size: large;\"><span style=\"color: rgb(0, 0, 255);\"><strong>ОХРАНА КОТТЕДЖЕЙ И ДАЧНЫХ ПОСЕЛКОВ</strong></span></span></h2>\r\n<div>&nbsp;</div>\r\n<div><img width=\"340\" hspace=\"9\" height=\"251\" align=\"left\" alt=\"\" src=\"/files/storage/images/ohranakottedgey.jpg\" /></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Каждый житель мегаполиса мечтает иметь за городом собственный дом. Вдали от шумного города, в укромном тихом месте, люди стремятся получить долгожданный покой и отдых. Но на практике это удается далеко не всегда. Наличие недоброжелателей,&nbsp;скандальных, вредных соседей, риск пожара, воровства, ограбления, делает проживание в личном доме тревожным, а порой даже опасным. Жить в ожидании неприятностей, согласитесь, крайне неуютно, а удаленность от людей и милицейской защиты, только усугубляет ситуацию. Поэтому именно безопасность становится одним из основных условий проживания за городом. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">К сожалению, шансы стать жертвой ограбления, вооруженного и группового нападения в загородном доме достаточно высоки. Специалисты ФБР (США) подсчитали, что вторжения в частные владения случаются каждые 8 секунд. Да и у нас в стране у каждого есть знакомые, ставшие жертвами противоправных посягательств на собственность или даже на жизнь. Так что лучше позаботиться о своей безопасности заранее. Но как? &nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Для начала, было бы хорошо установить технические средства охраны. Надежная охрана объекта достигается применением технических систем на рубежах защиты трех уровней. Первый уровень &ndash; граница объекта, второй &ndash; подходы к зданиям, третий &ndash; внутренние помещения. Типичная техническая защита загородного дома или поселка состоит из следующих элементов:</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Ограждение. Ограничивает и защищает территорию, не позволяет пройти случайным нарушителям и может нести информацию об ответственности за нарушение границ. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Средства обнаружения, размещенные по периметру объекта. Регистрируют факты незаконного вторжения на охраняемую территорию на его ранних этапах&nbsp;(1-ый рубеж охраны).</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Средства обнаружения, размещенные снаружи строений. Регистрируют факты приближения нарушителя к зданиям (2-ой рубеж охраны).</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Средства обнаружения, размещенные внутри строений. Регистрируют факты вторжения в здания и перемещения по нему (3-ий рубеж охраны).</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Средства контроля доступа (магнитные замки, видео-домофоны и др.) Ограничивают несанкционированный проход и проезд на территорию. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Средства инженерной защиты конструктивных элементов строений. Металлические двери, замки, решетки на окнах, колючая проволока и др.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Средства подачи сигналов. Подают сигналы тревоги (звуковые, световые).</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Средства управления и хранения информации. Локальные и удаленные терминалы, для местного или дистанционного управления системой, а также для накопления и хранения оперативной информации и видео-материалов.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Каналы связи. Необходимы для передачи сигналов тревоги во внешние системы (клиент, пульт охраны ЧОП или милиции и т.д.).</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Можно выполнить все вышеперечисленные требования и установить новейшее охранное оборудование, поставить высокий забор, установить железные двери, решетки на окнах, врезать дорогие замки, завести злую собаку... Но, обезопасит ли это вас полностью? </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Конечно нет, никакие технические средства не смогут на 100% предотвратить угрозу посягательства на участок, они только создадут временные трудности злоумышленникам. Необходимо чтобы кто-то отреагировал на тревожные сигналы и выступил на защиту вашей собственности. Никто не справится с этим лучше, чем профессиональная охрана. Только сотрудники ЧОП-а способны оперативно и надежно защитить объект в любой форс-мажорной ситуации, обеспечить сохранность имущества, задержать нарушителей.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Как правило, зона ответственности охраны ограничивается периметром наружного ограждения поселка и границами индивидуальных земельных участков. С этой целью по контуру поселка размещаются видеокамеры, оборудованные датчиками движения и периметровая сигнализация, с передачей сигнала на пульт охраны. Но, по желанию, каждый собственник может подключить свой дом на пульт сигнализации, установить &laquo;тревожную кнопку&raquo;. В случае получения тревожного сигнала наряд охраны обязан предпринять активные меры для защиты имущества собственника, вызвать милицию, а в случае возникновения пожара вызвать пожарную команду. Охрана обеспечивает также пропускной режим, проезд автотранспорта, вывоз имущества через КПП, отвечает за порядок в местах общего пользования, сохранность наружного ограждения поселка и переданного под охрану имущества. Помимо этого осуществляется регулярное патрулирование территории, для выявления и предотвращения противоправных действий, обнаружения очагов возгорания и аварий, принятия мер по их предупреждению и ликвидации. Обход территории осуществляется парным патрулем, при этом возможно использование вооружения, транспортных средств и служебных собак.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">По дополнительной договоренности жители поселка могут передать охране дубликаты ключей от дома, и по звонку из Москвы попросить охранников включить отопление, проверить температуру в доме, проверить все ли отключено и закрыто после выезда гостей, а также передать домашнему персоналу указания &ndash; скажем, нарубить дров для камина, принести воды из местного родника &nbsp;и т.д.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Режим охраны загородных домов и поселков может быть трех степеней:</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- минимальный: территория поселка огорожена, стационарный КПП на въезде на территорию, в котором постоянно находится один охранник, обеспечивающий соблюдение пропускного режима. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- стандартный: предусматривает кроме ограждения территории и КПП на въезде, одновременное несение службы несколькими охранниками, в пользовании которых находятся различные спецсредства и техника, обеспечивающие соблюдение пропускного режима. Также обязательно патрулирование территории поселка пешим порядком или с использованием различных транспортных средств (автомобиль, мотоцикл, велосипед). </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- усиленный: подразумевает уже несколько нарядов охраны, современные технические средства обеспечения безопасности, и на территории поселка, и в коттеджах, установку системы видеонаблюдения и контроля доступа, различные сигнализации, датчики движения по периметру и т.п.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Следует однако понимать, что даже усиленный режим охраны коттеджного поселка не является страховкой от всех бед. Хоть и редко, но и в охраняемых домах происходят неприятные истории, в основном мелкие кражи, но случаются&nbsp;и пожары, и ограбления. Пропускной режим, запись на камеру, сигнализация, патрулирование территории и иные меры, несомненно, играют свою роль в обеспечении безопасности, но и сам собственник не должен расслабляться &ndash; не держать двери нараспашку, не выбалтывать информацию о наличии &nbsp;в доме дорогих материальных ценностей, о применяемых способах охраны и т.д. И, в качестве дополнительной меры, не помешает застраховать свое имущество.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Комплекс охранных мер ЧОП, по обеспечению безопасности загородных поселков:</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Надежное и эффективное ограждение территории.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Охрана внутренней территории поселка, мест общего пользования. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Патрулирование территории с&nbsp;целью предотвращения противоправных действий. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Выставление поста охраны непосредственно на&nbsp;территории конкретного дома. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Создание мобильной группы быстрого реагирования (ГБР). </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Организация контрольно-пропускных пунктов, обеспечивающих пропускной режим.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Контроль прохода граждан и проезда автотранспорта через КПП. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Поддержание правопорядка на&nbsp;территории поселка. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Использование технических средств: видеонаблюдения, контроля доступа, охранно-пожарной сигнализации, тревожной кнопки, подключенных к&nbsp;центральной станции мониторинга.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- Противопожарные и антитеррористические мероприятия. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Принимая объект под охрану, наши специалисты обследуют его на предмет укрепленности, и защищенности от внутренних и внешних угроз. По результатам обследования даются советы и рекомендации по устранению причин и условий, которые ослабляют охрану объекта и влияют на безопасность собственников. По просьбе заказчиков специалисты ЧОП могут разработать Концепцию безопасности объекта, включающую комплекс мер: физическую охрану, технические средства безопасности и организационные мероприятия. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Стратегия охраны конкретного коттеджа или дачного поселка, помимо укрепленности и защищенности, зависит также от условий и места расположения участка, общей площади территории, количества строений, количества проходов и проездов. Все это влияет на объем и характер применяемых технических средств (систем видеонаблюдения, охранно-пожарной сигнализации, периметровой сигнализации, электропитания, связи, освещения), а также на количество и оснащение сотрудников ЧОП. При расчете количества охранников, учитывается время подмены сотрудников на отдых, прием пищи и т.д., а также на усиление группы реагирования, выдвигающейся на место срабатывания сигнализации или к месту происшествия. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Доверив охрану своего дома, коттеджного (дачного) поселка частному охранному предприятию &laquo;ТАГГЕРД&raquo;, Вы сможете больше не беспокоиться о своей безопасности. Наши специалисты обеспечат качественную и эффективную охрану Вашего имущества, спокойное и комфортное проживание Вашей семьи - 24 часа в сутки! Профессиональные знания и опыт сотрудников ЧОП &quot;ТАГГЕРД&quot; - надежная гарантия безопасности наших клиентов! </span></div>\r\n<p>&nbsp;</p>',`date` = '2010-11-30 17:32:22',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '36',`parent` = '27',`position` = '7',`autotext` = '',`page` = 'Охрана ресторанов и клубов',`name` = 'Охрана ресторанов и клубов',`title` = 'ЧОП ТАГГЕРД (Москва) предлагает профессиональную охрану развлекательных центров, ночных клубов, дискотек, кафе, баров, ресторанов',`description` = 'ЧОП ТАГГЕРД (Москва) предлагает профессиональную охрану развлекательных центров, ночных и игровых клубов, дискотек, кафе, баров, ресторанов. Охрана ЧОП - это надежно, профессионально, качественно!',`keywords` = 'охрана развлекательного центра, охрана ресторана, охрана ночного клуба, охрана дискотеки, охрана кафе, охрана бара',`text` = '<div>&nbsp;</div>\r\n<h1><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: xx-small;\"><strong>охрана развлекательного центра / ресторана / ночного и игрового клуба / дискотеки / кафе / бара</strong></span></span></h1>\r\n<h2><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: large;\"><strong>ОХРАНА КЛУБОВ И РЕСТОРАНОВ</strong></span></span></h2>\r\n<div><img width=\"350\" hspace=\"9\" height=\"306\" align=\"left\" src=\"/files/storage/images/ohrana clubov.jpg\" alt=\"\" /></div>\r\n<div style=\"text-align: justify;\">&nbsp;<strong><span style=\"color: rgb(0, 0, 128);\">Развлекательные центры, ночные и игровые клубы, дискотеки, кафе, бары,&nbsp;рестораны</span></strong><span style=\"color: rgb(0, 0, 128);\"> и другие предприятия носящие развлекательную и досуговую направленность, являются объектами повышенной опасности, несущими в себе постоянную угрозу: &nbsp;хулиганство, причинения вреда здоровью, порчу или хищение имущества и личных вещей, панические настроения в чрезвычайных ситуациях, и т.д. Такие объекты особо нуждаются в обеспечении надежной безопасности и охраны, потому что: </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">1. Это место массового скопления людей</span></strong><span style=\"color: rgb(0, 0, 128);\"> разных социальных слоев, разноплановой молодежи, порой разгоряченных алкоголем, что уже само по себе несет в себе конфликт, и следить за правопорядком и безопасностью там просто необходимо.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">2. Это место, где имеются дорогостоящие товарно-материальные ценности</span></strong><span style=\"color: rgb(0, 0, 128);\">, имущество, денежная выручка, требующие обеспечения сохранности.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">3. Это привлекательный объект для преступников</span></strong><span style=\"color: rgb(0, 0, 128);\"> и злоумышленников: от мелких карманных воришек, до террористических и бандитских формирований.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">4. Это место, где любое происшествие быстро становится публичным</span></strong><span style=\"color: rgb(0, 0, 128);\">, что может навечно испортить репутацию заведения и иметь гарантированный общественный резонанс; это имеет отношение не только к влиятельным, известным лицам, но и к обычным посетителям.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">5. Солидная охрана на входе влияет на посещаемость заведения</span></strong><span style=\"color: rgb(0, 0, 128);\">, так как спокойное, уютное и безопасное место привлекает людей, желающих хорошо отдохнуть без проблем и эксцессов. </span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">6. Люди приходят в клуб отдыхать и веселиться</span></strong><span style=\"color: rgb(0, 0, 128);\">, и ничто не должно омрачать их настроение и неприкосновенность.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">7. Высокий уровень безопасности повышает рейтинг </span></strong><span style=\"color: rgb(0, 0, 128);\">и престиж заведения в глазах окружающих, доверие клиентов и партнеров.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Охрану развлекательных заведений и ресторанов в Москве</span></strong><span style=\"color: rgb(0, 0, 128);\"> можно отнести к разряду особо сложных, ибо обеспечение безопасности требует от сотрудников охраны постоянного нервного напряжения, внимательности, бдительности, готовности в любую минуту защитить объект и имущество, персонал и посетителей от неадекватного поведения, и противоправных действий агрессивных людей, не забывая при этом, что любой посетитель &ndash; это прежде всего КЛИЕНТ заведения и отношение к нему должно быть максимально корректным и вежливым.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Сегодня развлекательным центрам и ресторанам требуются в большей степени не тупоголовые &laquo;вышибалы&raquo;, а уверенные в себе дипломаты, способные урегулировать любой конфликт, умеющие быстро и грамотно действовать в сложных ситуациях. У каждого охранника могут быть свои секреты нейтрализации конфликтных ситуаций. Кто-то приглашает расшумевшегося гостя пройти за другой столик или в другой зал, кто-то подзывает к телефону, кто-то отвлекает нейтральными разговорами и т.д. Но, в случае главная задача охраны - вывести потенциально опасного посетителя из зала, изолировать его от окружающих и передать в руки милиции.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Чтобы ограничить количество конфликтных ситуаций</span></strong><span style=\"color: rgb(0, 0, 128);\"> логично заблаговременно уберечься от нежелательных элементов еще на входе. Поэтому охрана клубов, кроме контроля за соблюдением дресс-кода и фейс-контроля, ставит перед собой задачу отсечь агрессивных и подозрительных личностей на входе в заведение. Настоящий профессионал должен без труда вычислять <strong>&laquo;проблемных&raquo; клиентов</strong>, представляющих опасность и не допускать их в клуб. Это могут быть:</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">1. Агрессивная публика и лица в алкогольном опьянении.</span></strong><span style=\"color: rgb(0, 0, 128);\"> Это может быть подвыпившая, развязно ведущая себя компания, либо отдельные агрессивно настроенные&nbsp;граждане, что называется &laquo;не в духе&raquo;,&nbsp;пристающие и &laquo;задирающие&raquo; других гостей. Как правило, они и создают основную массу неприятных ситуаций. Отсутствие самоконтроля, яркие эмоции могут спровоцировать любой конфликт. Таких людей лучше не допускать внутрь заведения, а если администратор решает все-таки пустить их, следует организовать за ними особый контроль, и при малейшем отклонении от норм поведения, вывести из зала.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">2. Воры и мошенники.</span></strong><span style=\"color: rgb(0, 0, 128);\"> Таких злоумышленников по внешнему виду и манерам не всегда определишь. Выявление таких лиц возможно только при внимательном наблюдении за посетителями, за обстановкой в зале и в зоне гардероба. Особое внимание уделяется гостям, пытающимся завести знакомства, провоцирующим посетителей на спор или азартную игру, пришедшим в дорогих вещах, оставляющим на столе телефоны, сумочки, номерки от гардероба, предметы одежды, ценности. Лучше лишний раз взять на контроль ситуацию, &nbsp;нежели потом объясняться с гостями, по поводу пропажи их имущества. Это &nbsp;быстро становится известно окружающим, негативно отражается на общей атмосфере вечера и на репутации заведения.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">3. Лица, не оплачивающие счета. </span></strong><span style=\"color: rgb(0, 0, 128);\">Таких граждан, отказывающихся платить по счету за хорошо проведенный вечер и обильные возлияния, на самом деле достаточно много. Кто-то случайно не рассчитал свои финансовые возможности, а кто-то посещает ресторан за рестораном, заказывает дорогие блюда и выпивку, а потом пытается незаметно улизнуть, не оплатив заказ. Таким группам посетителей, как правило, дается шанс все-таки оплатить вечеринку, съездить одному из компании за деньгами, оставить ценный залог. Если гости категорически отказываются решать проблему &ndash; вызывается наряд милиции и составляется протокол.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">4. Деклассированные элементы. </span></strong><span style=\"color: rgb(0, 0, 128);\">Бомжи, попрошайки, лица с психическими отклонениями, алкоголики. Таких лиц легко распознать по внешнему виду и поведению и категорически не пускать внутрь заведения.</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">5. Продавцы цветов, сувениров, открыток</span></strong><span style=\"color: rgb(0, 0, 128);\">; лица, предлагающие мелкие услуги. Такая публика крайне отрицательно влияет на обстановку в зале. Навязывая свой товар и услуги, они вызывают общее раздражение гостей, которое немедленно отражается на сумме счета и имидже заведения. </span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">6. Наркоманы и лица распространяющие наркотики. </span></strong><span style=\"color: rgb(0, 0, 128);\">Данные лица опасны своим неадекватным поведением и противозаконными действиями, связанными с торговлей и употреблением наркотических препаратов и притягивающими к себе как магнит весь спектр криминала. Таких лиц необходимо выявлять на входе и в помещениях клуба (курительная комната, туалет, танцпол и др.) и выдворять из заведения.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Профилактика и эффективная локализация происшествий и конфликтных ситуаций &ndash; вот основная задача службы охраны развлекательных заведений и ресторанов. Для этого охрана должна быть <strong>&laquo;всевидящей, профессиональной, ненавязчивой и эффективной&raquo;</strong>. Физически крепкие, профессионально подготовленные, фактурные охранники, способные своевременно выявить надвигающуюся конфликтную ситуацию и быстро изолировать зачинщиков беспорядков, вызывают уважение гостей и посетителей заведения. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Требования к охранникам развлекательных заведений и ресторанов</span></strong><span style=\"color: rgb(0, 0, 128);\"> достаточно высоки, ведь во многом по внешнему виду и манерам охранников, состоятельные и влиятельные посетители судят о престиже заведения, в котором они отдыхают.&nbsp;Опрятный и презентабельный внешний вид, уважительная манера общения, подчеркивают имидж заведения и собственный&nbsp; корпоративный имидж ЧОП. От того, как посетителей встретят на входе, зависит и настроение, с которым они проведет время в клубе, а значит и желание посетить данное место еще раз. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Соответствие внешнего вида сотрудников охраны концепции ресторана</span></strong><span style=\"color: rgb(0, 0, 128);\"> - одно из основных условий, оговаривающихся с охранным предприятием. Для &quot;шумных&quot; заведений предпочтительнее высокие и крупного телосложения охранники, всем своим видом внушающие уважение, бесперспективность агрессии и конфликтов. Тогда как в элитных ресторанах с классическим сервисом, в &nbsp;обстановке покоя, отдыха и расслабленности, охрана должна быть практически незаметна и по внешним признакам не особенно отличаться от сотрудников сервиса. Но существуют и общие базовые требования: охранник должен быть презентабельным и внушительным, строго и официально одетым, уметь убедительно и лаконично говорить, быть вежливым и сдержанным в эмоциях. Для работы в подобных заведениях привлекаются граждане, имеющие следующие физические данные: рост - от 180 см, вес &ndash; от 85 кг. От сотрудников требуются крепкие нервы, адекватность поведения, опыт работы в охране объектов от 2-х лет, знание правовых основ и человеческой психологии и особенностей поведения толпы в различных ситуациях. Кандидаты в охрану проходят особый отбор, многоуровневое тестирование, серию проверок и собеседований. Во время работы с сотрудниками проводятся постоянные профессиональные тренинги и дополнительная профессиональная подготовка.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><em><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: small;\"><strong>Охрана клубов и ресторанов в Москве включает в себя комплекс следующих услуг:</strong></span></span></em></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- ведение внутреннего пропускного режима персонала и посетителей, соблюдая режим работы ресторана.</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- осуществление фейс-контроля на предмет выявления правонарушителей</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- осуществление дресс-кода, установленного в заведении или мероприятии</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- контроль оперативной обстановки, с использованием систем видеонаблюдения </span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- задержание административных и иных правонарушителей и передача их работникам милиции</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- принятие мер в случае неоплаты посетителями счетов</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- пресечение проноса запрещенных предметов, оружия, алкоголя, наркотиков и др.,</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- досмотр персонала (до и после работы)</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- принятие активных мер защиты при возникновении аварийных и чрезвычайных ситуаций, пресечение противоправных действий и задержание нарушителей</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- мониторинг оперативной обстановки в помещениях и на прилегающей территории </span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- обеспечение сохранности имущества и материальных ценностей</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- патрулирование внутренней и внешней территории, а также автомобильного &nbsp;паркинга</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- контроль соблюдения правил пожарной безопасности</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<p><em>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">- защита экономических и информационных интересов наших заказчиков</span></div>\r\n</em></p>\r\n<div style=\"text-align: justify;\"><em><span style=\"color: rgb(0, 0, 128);\">- анттеррористические мероприятия</span></em></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></strong></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Еще несколько лет назад владельцы развлекательных центров ограничивались расстановкой нескольких охранников в зале и двумя-тремя камерами наблюдения. Сейчас они склоняются к использованию комплексных систем безопасности. На выбор методов и средств охраны влияет: &nbsp;месторасположение и конструктивные особенности объекта; класс заведения; график работы; среднестатистическое количество посетителей в будние и в выходные дни; наличие автомобильной парковки; функциональные обязанности охранников; техническая оснащенность объекта.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">При принятии объекта под охрану, наши специалисты предварительно проведут его тщательное обследование на предмет укрепленности и защищенности от внутренних и внешних угроз, определят наиболее эффективные и экономически выгодные способы охраны, тактику обеспечения охраны объекта, создадут <strong>Концепцию безопасности</strong>, состоящей из комплекса мер: физической охраны, технических средств безопасности и организационных мероприятий. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Технические средства безопасности</span></strong><span style=\"color: rgb(0, 0, 128);\"> объекта могут представлять собой комплекс, состоящий из&nbsp;систем охранной и пожарной сигнализации, видеонаблюдения, контроля доступа, &laquo;тревожной кнопки&raquo;, проходных рамок и металлоискателей и др. Несмотря на разноплановость, эти системы интегрируются в единый комплекс управления безопасностью объекта, что позволяет не только облегчить общий контроль объекта, но и создать алгоритм совместной работы всей системы при возникновении тревожной ситуации.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Наш ЧОП, имеющий богатый опыт по охране разноплановых объектов, готов организовать надежную комплексную охрану объектов любой сложности, на высоком профессиональном уровне, в Москве и области.&nbsp;Мы поможем владельцам клубов и ресторанов уберечь отдыхающих от назойливых клиентов, нейтрализовать опасных и подозрительных личностей, обеспечить сохранность товарно-материальных ценностей и коммерческой тайны, оснастить объект техническими средствами безопасности. Профессиональные знания и опыт сотрудников ЧОП &quot;ТАГГЕРД&quot; - надежная гарантия безопасности наших клиентов!</span></strong></div>\r\n<div>&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-size: medium;\">ОКАЗАНИЕ УСЛУГ БЕЗОПАСНОСТИ И ОХРАНЫ - НАША ПРОФЕССИЯ!</span></strong></span></div>\r\n<p>&nbsp;</p>',`date` = '2010-12-06 07:43:44',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '37',`parent` = '27',`position` = '9',`autotext` = '',`page` = 'Охрана промышленного предприятия',`name` = 'Охрана промышленного предприятия',`title` = 'ЧОП ТАГГЕРД (Москва)- охрана промышленного и производственного объекта, фабрики, завода, комбината, цеха',`description` = 'ЧОП ТАГГЕРД (Москва) предлагает профессиональную охрану промышленного и производственного предприятия, фабрики, завода, комбината, цеха. Охрана ЧОП - это надежно, профессионально, качественно!',`keywords` = 'охрана промышленного предприятия, охрана производственного предприятия, охрана фабрики, охрана завода, охрана комбината, охрана цеха, безопасность предприятия',`text` = '<div>&nbsp;</div>\r\n<h1><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: xx-small;\">ОХРАНА ПРОМЫШЛЕННОГО И ПРОИЗВОДСТВЕННОГО ПРЕДПРИЯТИЯ, ФАБРИКИ, ЗАВОДА, КОМБИНАТА, ЦЕХА</span></strong></span></h1>\r\n<h2><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: large;\"><strong>&nbsp;ОХРАНА ПРОМЫШЛЕННЫХ ОБЪЕКТОВ</strong></span></span></h2>\r\n<div style=\"text-align: justify;\"><img width=\"300\" hspace=\"9\" height=\"225\" align=\"left\" alt=\"\" src=\"/files/storage/images/ohrana zavoda-2.jpeg\" /><strong><span style=\"color: rgb(0, 0, 128);\">Охрана каждого промышленного предприятия, производственного комбината, завода, фабрики, цеха, </span></strong><span style=\"color: rgb(0, 0, 128);\">имеет свою специфику, обусловленную особенностями конкретного производства, характеристиками территории (внушительной площадью и протяженностью периметра), наличием большого количества дорогостоящих товарно-материальных ценностей, рассредоточенных по всей территории объекта, а также значительным количеством персонала, техники, оборудования, механизмов, находящихся в постоянном движении.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Матрица рисков и угроз</span></strong><span style=\"color: rgb(0, 0, 128);\"> на заводах, фабриках и иных промышленных объектах самая обширная - это и различные умышленные преступления (хищение ТМЦ и имущества, вредительство, вандализм), &nbsp;и многочисленные угрозы для жизни и здоровья людей, и случайные риски (аварии, пожары, взрывы, затопления), и специфические угрозы (риск техногенных и экологических катастроф, террористических актов, забастовок персонала, промышленного шпионажа, инфекционного и бактериологического заражения). </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">При создании системы безопасности следует учитывать</span></strong><span style=\"color: rgb(0, 0, 128);\"> и то,&nbsp;что промышленные предприятия зачастую имеют большое социально-экономическое значение в своем регионе (или на федеральном уровне), и возникновение какой-либо чрезвычайной ситуации на объекте может иметь большой общественный резонанс и негативные последствия в масштабе района, города и региона в целом.&nbsp;Так что, на плечи охраны, помимо сложности и трудоемкости работ, связанных с обеспечением безопасности на предприятии, ложится еще и большая ответственность.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: small;\">Н<strong>аиболее важными ресурсами и одновременно объектами защиты промышленного предприятия, являются:</strong></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><em><span style=\"color: rgb(0, 0, 128);\"><strong>- Люди </strong>(персонал и посетители предприятия); </span></em></div>\r\n<div style=\"text-align: justify;\"><em><span style=\"color: rgb(0, 0, 128);\"><strong>- Материальная и интеллектуальная собственность: </strong>важное или дефицитное технологическое оборудование; секретная и конфиденциальная документация; материальные и финансовые ценности; готовая продукция; интеллектуальная собственность (ноу-хау); средства вычислительной техники&nbsp;и компьютеры; контрольно-измерительные приборы (КИП) и др.; </span></em></div>\r\n<div style=\"text-align: justify;\"><em><span style=\"color: rgb(0, 0, 128);\"><strong>- Конфиденциальная информация: </strong>на материальных носителях, а также циркулирующая во внутренних телекоммуникационных каналах связи и информации, в кабинетах руководства предприятия, на совещаниях и заседаниях; </span></em></div>\r\n<div style=\"text-align: justify;\"><em><span style=\"color: rgb(0, 0, 128);\"><strong>- Финансово-экономические ресурсы,</strong> обеспечивающие эффективное и устойчивое развитие предприятия (капитал, коммерческие интересы, бизнес-планы, договорные документы и обязательства и т.п.). </span></em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Специфика производства,</span></strong><span style=\"color: rgb(0, 0, 128);\"> его&nbsp;объемы и масштабы, постоянно меняющаяся оперативная обстановка, круглосуточный режим работы, наличие большого транспортного потока,&nbsp;постоянное перемещение ТМЦ как внутри, так и за территорию предприятия, большая протяженность границ,&nbsp;и др., &nbsp;создают объективные трудности с организацией охраны объекта. Обособленность охраны от производственного процесса и участие в криминальных схемах самих работников и отдельных руководителей подразделений предприятия создают дополнительные сложности с обеспечением безопасности, поэтому одними силами охраны без участия руководства объекта &nbsp;невозможно обеспечить эффективную охрану объектов такого масштаба. </span><span style=\"color: rgb(0, 0, 128);\"> Только совместная и согласованная работа службы охраны объекта с руководителями и начальниками различных&nbsp;подразделений (отделом кадров, отделами поставки и сбыта, производства и хранения готовой продукции и др.) может сделать работу по обеспечению сохранности ТМЦ по настоящему эффективной. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Для обеспечения безопасности жизненно-важных трудовых, материальных и информационных ресурсов</span></strong><span style=\"color: rgb(0, 0, 128);\"> предприятия необходимо использовать всесторонний комплексный подход и взаимодействие правовых, охранных, организационных и инженерно-технических методов защиты. Физическая охрана, инженерно-техническое оснащение, административные и организационные меры должны быть объединены в единую систему для решения общей задачи &ndash; обеспечение стабильной и безопасной работы предприятия. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Инженерно-техническое оснащение промышленных предприятий</span></strong><span style=\"color: rgb(0, 0, 128);\"> включает в себя: освещение; охранно-пожарную и периметровую сигнализацию; системы видеонаблюдения, средства ограничения и контроля доступа, системы оповещения; досмотровые средства; системы учета ТМЦ; электромеханические препятствия, установленные на всех въездах/выездах (а/м и ж/д), для предотвращения проезда через КПП без остановки; специальные оборудование для распознания номеров а/м и документирование их в текстовом формате. </span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\"><em><strong>Системы охранной сигнализации</strong> помещений и периметра фиксируют приближение и проникновение нарушителей и передают тревожный сигнал на центральный пульт охраны. При этом могут применяться различные датчики: движения, радиолучевые, инфракрасные, ёмкостные (объемные), вибрационные, тепловые. Помимо периметра датчиками охранной сигнализации оснащаются все стратегически важные объекты: административные и складские помещения, бухгалтерия и касса, энергетические узлы, подстанции, топливные системы, системы газо- и водоснабжения, узлы связи и хранения информации, а также в любые помещения, где хранится оборудование, которое может быть похищено, или испорчено злоумышленниками.</em></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\"><em>&nbsp;</em></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\"><em><strong>Система видеонаблюдения </strong>позволяет не только контролировать действия персонала предприятия, но и фиксировать проникновение и перемещение нарушителей через периметр объекта, а также иметь надежные доказательства в форме видеозаписи всех противоправных действий. К тому же сегодня существует возможность передавать видеосигнал и информацию с блока охранной сигнализации по сети Интернет. Так что руководители объекта могут в любое время осуществлять мониторинг безопасности и охраны предприятия на своем компьютере в режиме он-лайн.</em></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\"><em>&nbsp;</em></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\"><em><strong>Устройства контроля доступа</strong> фиксируют время прихода/ухода персонала, въезда/выезда автотранспорта, все перемещения работников и техники по территории предприятия. В системы контроля и ограничения доступа могут входить: турникеты на контрольно-пропускных пунктах, электронные ключи доступа в помещения с особым режимом охраны, шлагбаумы, механическое оборудование для ворот и дверей, оборудование для учета и регистрации.</em></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Организационные мероприятия</span></strong><span style=\"color: rgb(0, 0, 128);\"> включают в себя: усиленный контроль за устройствами взвешивания, и за работой грузоподъемных механизмов во внерабочее время; применение методов ротации дежурных КПП и рабочих бригад по составу смен; внедрение системы регистрации въезда/выезда транспорта, и лиц, управляющих им; регулярные проверки и переучет ТМЦ с участием независимых представителей различных подразделений предприятия и охраны; разработка соответствующих нормативных и регламентирующих документов и др. Возможны и иные неординарные меры. Например, организация зоны личного досмотра работников предприятия (для ювелирных, электронных, пищевых производств), организация специальных площадок досмотра большегрузного транспорта, использование детекторов лжи при назначении на должность и др.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Физическая охрана промышленных объектов</span></strong><span style=\"color: rgb(0, 0, 128);\"> включает в себя: организацию стационарных и подвижных постов охраны; усиленный контроль погрузочно-разгрузочных зон, организация строгого контрольно-пропускного режима, выделение зон с особым режимом прохода, ограничивающих доступ посторонних лиц; непрерывный мониторинг обстановки, с применением систем видеонаблюдения и связи; создание групп быстрого реагирования (ГБР); использование сотрудниками охраны современного оснащения, транспортных средств, сторожевых собак и вооружения.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><strong>Подвижные посты охраны (патруль) </strong>применяют следующий комплекс основных приемов контроля и осмотра охраняемого объекта: </span></div>\r\n<p><span style=\"color: rgb(128, 0, 128);\">\r\n<div style=\"text-align: justify;\"><em>- полный осмотр объекта охраны, во время которого охранник проходит в определенном направлении до границы вверенной территории и обратно;</em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\">\r\n<div style=\"text-align: justify;\"><em>- осмотр объекта постами, которые движутся от границ объекта к центру (точке встречи) и вновь расходятся в направлении периметра охраняемой территории;</em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\">\r\n<div style=\"text-align: justify;\"><em>- концентрический и эксцентрический способы осмотра территории объекта, суть которых заключается в движении охранника по спирали от центра охраняемого участка к его границам и наоборот;</em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\">\r\n<div style=\"text-align: justify;\"><em>- последовательный осмотр отдельных секторов охраняемого объекта по сложной траектории, продиктованной планировкой и структурой объекта;</em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\">\r\n<div style=\"text-align: justify;\"><em>- выборочный осмотр участков объекта, на которых расположены наиболее ценные товарно-материальные ценности или есть неукрепленные участки границы;</em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(128, 0, 128);\">\r\n<div style=\"text-align: justify;\"><em>- движение по охраняемой территории с регулярно меняющимся маршрутом;</em></div>\r\n</span></div>\r\n</span></div>\r\n</span></div>\r\n</span></div>\r\n</span></div>\r\n</span><span style=\"color: rgb(128, 0, 128);\"><em>- движение по объекту с временными остановками и осмотром неукрепленных мест и других секторов с закрытого поста (то есть, из засады).</em></span><span style=\"color: rgb(0, 0, 128);\"><br />\r\n</span></p>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Физическая охрана конкретного объекта строится по индивидуальным принципам</span></strong><span style=\"color: rgb(0, 0, 128);\">, требующим всестороннего подхода. Как правило, каждый пост охраны (подвижной или&nbsp;стационарный) имеет свои задачи и свой способ дежурства. При этом основной и общей задачей службы охраны является полный контроль оперативной обстановки на охраняемом объекте с целью опережающего выявления и предупреждения чрезвычайных и нештатных ситуаций, путем эффективного взаимодействия постов. Расположение постов охраны завода должно способствовать обеспечению режима безопасности на объекте, оптимальному контролю за оперативной обстановкой, с целью опережающего выявления и предупреждения возможных угроз, а также эффективному взаимодействию постов и оказанию взаимопомощи в нештатных и чрезвычайных ситуациях. Наличие нескольких постов требует наличия продуманной системы оперативной связи.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Одной из первоочередных задач ЧОП</span></strong><span style=\"color: rgb(0, 0, 128);\"> является создание системы охраны периметра и обеспечение контрольно-пропускного режима, при котором вывоз краденного или неучтенного товара за территорию предприятия автомобильным и железнодорожным транспортом был бы очень затруднен или невозможен. Охрана периметра предприятия залог сохранности не только имущества, но и коммерческой и промышленной тайны, не только от внутренних угроз (сотрудников предприятия), но и от злоумышленников извне.</span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Охрана периметра предприятия</span></strong><span style=\"color: rgb(0, 0, 128);\"> может включать в себя: стационарное ограждение (забор), защищенный от перелаза колючей проволокой,&nbsp;освещение (лампы и прожектора), охранную сигнализацию, систему видеонаблюдения, систему контроля доступа и контроля перемещения. </span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Далее, охране подлежат различные отдельно стоящие здания и помещения</span></strong><span style=\"color: rgb(0, 0, 128);\"> (офисы, бухгалтерия, производственные цеха, гаражные и подсобные боксы, склады сырья и готовой продукции, и др.), рассредоточенные по всей территории объекта, а также места складирования ТМЦ, погрузочно-разгрузочные эстакады, весовые, зоны стоянки автотранспорта и техники, места хранения горюче-смазочных, токсичных, ядовитых, взрывоопасных веществ и др.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: small;\"><strong>Комплексная система охраны промышленного объекта сотрудниками ЧОП может включать следующие мероприятия: </strong></span></span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- обеспечение контрольно-пропускного режима:</span></strong><span style=\"color: rgb(0, 0, 128);\"> проезда автотранспорта, прохода граждан, контроля ввоза/вывоза ТМЦ, разграничение доступа персонала и посетителей по зонам внутри объекта;</span></em><em>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">- охрана периметра предприятия;</span></strong></div>\r\n</em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- обеспечение сохранности ТМЦ и имущества</span></strong><span style=\"color: rgb(0, 0, 128);\">, зданий и сооружений; предотвращение хищения продукции, на всех этапах производства и хранения;</span></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- патрулирование территории</span></strong><span style=\"color: rgb(0, 0, 128);\"> и помещений по меняющимся маршрутам;</span></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- контроль оперативной обстановки</span></strong><span style=\"color: rgb(0, 0, 128);\"> на объекте, с использованием технических средств сигнализации, видеонаблюдения, контроля доступа;</span></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- оперативное реагирование</span></strong><span style=\"color: rgb(0, 0, 128);\"> на чрезвычайные и нештатные ситуации, на тревожное срабатывание охранной и пожарной сигнализации;</span></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- контроль соблюдения внутриобъектового режима</span></strong><span style=\"color: rgb(0, 0, 128);\"> и трудового распорядка; </span></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- содействие кадровой безопасности</span></strong><span style=\"color: rgb(0, 0, 128);\"> предприятия </span></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- содействие в обеспечении сохранности коммерческой тайны</span></strong><span style=\"color: rgb(0, 0, 128);\"> и противодействия промышленному шпионажу;</span></em></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">- поддержание противопожарной безопасности; </span></strong></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- организация взаимодействия</span></strong><span style=\"color: rgb(0, 0, 128);\"> с местными правоохранительными органами;</span></em></div>\r\n<div style=\"text-align: justify;\"><em><strong><span style=\"color: rgb(0, 0, 128);\">- антитеррористические мероприятия. </span></strong></em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Прежде чем принять объект под охрану наши специалисты проводят его комплексное обследование </span></strong><span style=\"color: rgb(0, 0, 128);\">на предмет защищенности, оснащенности и уязвимости от внутренних и внешних угроз. На основе полученных данных мы разрабатываем комплексную Концепцию безопасности предприятия, определим наиболее выгодные способы охраны, дадим рекомендации по организации внутриобъектового и пропускного режима, по выбору и установке технических средств, предложим&nbsp;критерии отбора охранников, экипировку, вооружение и оснащение и др.&nbsp; </span><span style=\"color: rgb(0, 0, 128);\">От результатов комплексного обследования зависит выбор наиболее эффективной стратегии и тактики охраны объекта, оптимальной структуры и состава используемых средств, по критерию <em><strong>&quot;эффективность - стоимость&quot;</strong></em> (максимум эффективности при минимуме затрат).</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Автоматизированная комплексная система защиты объекта,</span></strong><span style=\"color: rgb(0, 0, 128);\"> является на сегодняшний день самой эффективной превентивной мерой по обеспечению безопасности промышленного предприятия. Применение такой системы может в корне изменить стратегию и тактику охраны объекта, ведь при этом отпадает необходимость в организации постовой службы по охране периметра и отдельных элементов объекта. Вместо этого создаются дежурные тревожные группы (ГБР), которые при получении сигнала тревоги с центрального пульта управления,&nbsp;немедленно выдвигаются на место происшествия, для локализации негативного воздействия и нейтрализации нарушителей. В автоматизированной системе защиты сведено до минимума влияние человеческого фактора и достигается высокая эффективность защиты объекта при минимальном количестве личного состава охраны.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Данная комплексная система безопасности может быть успешно адаптирована специалистами нашего ЧОП к индивидуальным условиям конкретного промышленного предприятия.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Для охраны серьезного объекта требуется и серьезная профессиональна охранна,</span></strong><span style=\"color: rgb(0, 0, 128);\"> которой является группа компаний &laquo;ТАГГЕРД&raquo;. Строгий кадровый отбор, профильная (объектовая) подготовка, исполнительность, дисциплина и ответственность сотрудников &ndash; вот основные аспекты, которым наш ЧОП уделяет особое внимание при организации службы. Мы постоянно работаем над повышением квалификации и уровня профессиональной подготовки охранников, обучаем их методам работы с использованием современных технических средств, проводим тренинги по действиям в нештатных ситуациях. </span><span style=\"color: rgb(0, 0, 128);\"><br />\r\n</span></div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div><strong><span style=\"color: rgb(0, 0, 128);\">Сотрудники ЧОП &laquo;ТАГГЕРД&raquo; обладают необходимым опытом и знаниями в области обеспечения безопасности промышленных предприятий различного масштаба и специализации, и способны обеспечить надежную охрану любого объекта на высоком профессиональном уровне. </span></strong></div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><br />\r\n</span></span></div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><strong>ОКАЗАНИЕ УСЛУГ БЕЗОПАСНОСТИ И ОХРАНЫ - НАША ПРОФЕССИЯ!</strong></span></span></div>\r\n<div>&nbsp;</div>\r\n<p>&nbsp;</p>',`date` = '2010-12-22 08:19:57',`display` = 'Нет';
insert into `taggerd__pages` set `id` = '38',`parent` = '5',`position` = '2',`autotext` = '',`page` = 'Консьерж -служба',`name` = 'Консьерж -служба',`title` = 'ЧОП ТАГГЕРД (Москва) предлагает охранников- консьержей для охраны жилых домов, ТСЖ, гостиниц, общежитий',`description` = 'ЧОП \"ТАГГЕРД\" (Москва) предлагает организацию службы консьержа, для охраны жилых элитных и многоквартирных домов и кварталов, гостиниц, отелей, общежитий, с использованием профессиональных охранников ЧОП. ',`keywords` = 'консьерж в подъезд, консьерж в тсж, консьерж в дом, консьерж в общежитие, консьерж в гостиницу, консьерж в отель',`text` = '<div>&nbsp;</div>\r\n<h1><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-size: xx-small;\">&nbsp;КОНСЬЕРЖ: В ЖИЛОЙ ДОМ / В ПОДЪЕЗД / В ГОСТИНИЦУ / В ОБЩЕЖИТИЕ / В ОТЕЛЬ / В ОФИС / В ТСЖ </span></strong></span></h1>\r\n<h2><span style=\"font-family: Verdana;\"><span style=\"font-size: smaller;\"><strong><span style=\"color: rgb(0, 0, 128);\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: large;\"><strong>КОНСЬЕРЖ -СЛУЖБА</strong></span></span></span></strong></span></span></h2>\r\n<div><span style=\"font-size: larger;\"><span style=\"color: rgb(0, 0, 128);\"><strong><img width=\"250\" hspace=\"10\" height=\"250\" border=\"2\" align=\"left\" src=\"/files/storage/images/consierge-3.jpg\" alt=\"\" /></strong></span></span></div>\r\n<div><span style=\"font-size: smaller;\"><br />\r\n</span></div>\r\n<div style=\"text-align: justify;\"><strong><em><span style=\"color: rgb(0, 0, 128);\">КОНСЬЕРЖ </span></em></strong><em><span style=\"color: rgb(0, 0, 128);\">(франц. concierge) &ndash; швейцар, старший дворник.</span></em></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Консьерж </span></strong><span style=\"color: rgb(0, 0, 128);\">является &laquo;визитной карточкой&raquo;&nbsp;каждого жилого дома, гостиницы, общежития, офиса, подъезда. Ведь от того, как консьерж выполняет свои обязанности зависит и личная безопасность, и сохранность имущества, и правопорядок&hellip; А от того, как Вас встречают и как провожают порой зависит хорошее настроение на целый день!</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Охранник- консьерж</span></strong><span style=\"color: rgb(0, 0, 128);\"> должен знать всех&nbsp;жильцов и сотрудников &laquo;в лицо&raquo; и вести&nbsp;список постоянных и временных жильцов/сотрудников дома, отеля, офиса, вести книгу учета посещений для гостей, а при появлении подозрительных личностей сообщать в милицию. Консьержа можно уполномочить в отказе доступа посетителей, если хозяева сообщили ему о своем отсутствии или нежелании видеть незваных гостей. Хороший консьерж первым узнает обо всех бытовых проблемах жильцов и сотрудников, он знает, кто и во сколько уходит и приходит. Родители вправе спросить консьержа, когда их дети куда-то уходили, как себя вели, кто их навещал. Консьерж также может принимать и передавать информацию для отсутствующих жильцов (сотрудников), а также сообщать обо всех ЧП в соответствующие службы города. Одним словом, консьерж обязан видеть и слышать все, и держать язык за зубами!</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">В функции консьержа также может входить вызов такси и служб экстренной помощи,&nbsp;помощь и консультации жильцам и сотрудникам по различным вопросам обустройства и жизнеобеспечения, сопровождение инвалидов и больных-колясочников (к выходу из дома или при посадке в лифт), помощь с переносом тяжелых и габаритных личных вещей и сумок.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><strong>Основные функциональные обязанности консьержа:</strong></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">1.&nbsp;Контроль прохода (входа/выхода) владельцев помещений, арендаторов, посетителей.<br />\r\n2.&nbsp;Контроль соблюдения правил общежития, пожарной безопасности и чистоты на внутренней и прилегающей территории.</span></strong></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">3. Контроль вноса/выноса имущества.<br />\r\n4.&nbsp;Контроль соблюдения норм парковки около подъезда.</span></strong></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">5. Контроль соблюдения правопорядка на подконтрольной территории.</span></strong></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">6. Реагирование на чрезвычайные и аварийные ситуации, своевременный вызов служб экстренной помощи и аварийных бригад.</span></strong></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">7. Ведение необходимой документации, списка постоянных жильцов (сотрудников), книги учета посещений, журнала дежурства др.</span></strong></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Помимо этого консьерж должен быть добросовестным, вежливым, внимательным, ответственным, доброжелательным, коммуникабельным, дисциплинированным, умеющим хранить секреты.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><strong><span style=\"color: rgb(0, 0, 128);\">Профессия консьержа</span></strong><span style=\"color: rgb(0, 0, 128);\"> имеет свои специфические особенности, в зависимости от того, где он выполняет свои функции -&nbsp;в жилом доме, в офисе, в гостинице (отеле), в ночном клубе, в ресторане&hellip;, могут отличаться критерии отбора и уровень заработной платы, но основные функции одинаковы у всех.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Постоянно повышая свой качественный уровень, мы ориентируемся на европейский опыт работы консьержей. Стоимость службы консьержей не столь велика, а польза &ndash; огромная!</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\">Рекомендуем также ознакомиться со статьей&nbsp;&nbsp;</span><a href=\"../../../../../page/5/23/\"><span style=\"color: rgb(0, 0, 128);\">Правовая основа деятельности&nbsp;ЧОП</span></a></div>\r\n<h1>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: medium;\"><strong><span style=\"color: rgb(255, 0, 0);\">ОКАЗАНИЕ УСЛУГ  БЕЗОПАСНОСТИ - НАША ПРОФЕССИЯ!</span></strong></span></span></div>\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n<div><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-family: Comic Sans MS;\"><span><span style=\"font-size: medium;\"><strong><font size=\"3\"> </font></strong></span></span></span></span></div>\r\n<div style=\"text-align: center;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-family: Comic Sans MS;\"><span><span style=\"font-size: medium;\"><strong><font size=\"3\">\r\n<table width=\"400\" cellspacing=\"1\" cellpadding=\"9\" border=\"1\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#6600ff\" style=\"text-align: center;\"><span style=\"color: rgb(255, 255, 255);\"><span style=\"font-size: medium;\"><strong><a href=\"http://taggerd.su/page/0/19/\"><span style=\"color: rgb(255, 255, 255);\">ЗАКАЗАТЬ&nbsp; УСЛУГУ&nbsp; ОН-ЛАЙН</span></a></strong></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div>&nbsp;</div>\r\n</font></strong></span></span></span></span></div>\r\n</div>\r\n<div>&nbsp;</div>\r\n</h1>\r\n<div>&nbsp;</div>\r\n<p>&nbsp;</p>',`date` = '2010-12-22 15:18:22',`display` = 'Нет';
create table if not exists `taggerd__photos`(
id int(11) NOT NULL PRIMARY KEY  auto_increment,
parent int(11) NOT NULL   ,
name varchar(255) NULL   ,
pic varchar(255) NULL   DEFAULT '/files/images/noimage.gif' ,
icon varchar(255) NULL   DEFAULT '/files/images/noimage.gif' ,
xsmall tinytext NOT NULL  );

create table if not exists `taggerd__users`(
id int(11) NOT NULL PRIMARY KEY  auto_increment,
name varchar(50) NOT NULL UNIQUE  ,
pass varchar(50) NOT NULL   ,
rights enum('user','root') NULL   DEFAULT 'user');

